function lf_setAfSale()
{
	//변경후
	//판매일시
	sf_setDateByDataSet("div_afSaleInfo.cal_saleDtm","div_afSaleInfo.edt_saleDtmHh", "div_afSaleInfo.edt_saleDtmMm", ds_generalSale.GetColumn(0, "sale_dtm"));
	//판매처
    //div_afSaleInfo.edt_salePlcNm.Text        = ds_generalSale.GetColumn(0,"sale_plc_nm");
	div_afSaleInfo.edt_salePlcUkeyCd.Text    = parent.ds_channelChgIfInfo.GetColumn(0,"ukey_agency_cd");//ds_generalSale.GetColumn(0,"sale_agency_cd");
	div_afSaleInfo.edt_salePlcSubCd.Text     = parent.ds_channelChgIfInfo.GetColumn(0,"ukey_sub_cd");//ds_generalSale.GetColumn(0,"sale_sub_cd");
	div_afSaleInfo.edt_salePlcChannelCd.Text = parent.ds_channelChgIfInfo.GetColumn(0,"af_ukey_channel_cd");
	div_afSaleInfo.edt_salePlc.Text          = parent.ds_channelChgIfInfo.GetColumn(0,"af_sale_plc");

	//20090921 
	if ( (parent.ds_channelChgIfInfo.getColumn(0,"sl_cl") == "02") && !uf_IsNull(parent.ds_channelChgIfInfo.getColumn(0,"sl_net_cd")))
	{
	    if ( uf_IsNull(parent.ds_channelChgIfInfo.GetColumn(0,"af_sale_plc")) )
	    {
		    div_afSaleInfo.btn_salePlc.Click();
	    }
	    else
	    {
	        sf_clearControls("div_afSaleInfo.edt_salePlcUkeyCd,div_afSaleInfo.edt_salePlcSubCd,div_afSaleInfo.edt_salePlcChannelCd,div_afSaleInfo.edt_salePlc");
	    }

	    div_afSaleInfo.edt_salePlcNm.Enable = true;
	    div_afSaleInfo.btn_salePlc.Enable = true;

		div_afSaleInfo.cmb_slNetCd.Value = parent.ds_channelChgIfInfo.getColumn(0,"sl_net_cd");	    
		div_afSaleInfo.cmb_slNetCd.Enable = false;
	}
	else
	{
		div_afSaleInfo.btn_salePlc.Click();

		http.sync = true;
		f_getSalePlcChgChgrgId();   //판매담당자
		http.sync = false;    

		if ( uf_IsNull(parent.ds_channelChgIfInfo.getcolumn(0,"sale_chgrg_id")) )
		{
			sFindRow = ds_cmbSaleChgrgId.FindRow("user_id",div_bfSaleInfo.cmb_saleChgrgId.Value);

			if (sFindRow == "-1")
			{
				div_afSaleInfo.cmb_saleChgrgId.value = "";
			}
			else
			{
				div_afSaleInfo.cmb_saleChgrgId.Value = div_bfSaleInfo.cmb_saleChgrgId.Value;
			}
		}
		else
		{
			div_afSaleInfo.cmb_saleChgrgId.Value = parent.ds_channelChgIfInfo.getcolumn(0,"sale_chgrg_id");
		}

		var sSaleChgDtm = "";

		if ( FV_IF_PROC_CL == "1")
		{
			FV_PROC_DTM = ds_channelChgIfInfo.GetColumn(0,"proc_dt") + ds_channelChgIfInfo.GetColumn(0,"proc_tm") + "00"; 
			sSaleChgDtm = ds_channelChgIfInfo.GetColumn(0,"proc_dt") + ds_channelChgIfInfo.GetColumn(0,"proc_tm"); 
		}
		else
		{
			FV_PROC_DTM = ds_generalSale.GetColumn(0, "sale_chg_dtm"); 
			sSaleChgDtm = FV_PROC_DTM;
		}

		lf_getStlPlc();    

		if ( !uf_IsNull(parent.ds_channelChgIfInfo.getColumn(0,"sl_net_cd")) )
		{
			div_afSaleInfo.cmb_slNetCd.Value = parent.ds_channelChgIfInfo.getColumn(0,"sl_net_cd");
			//div_afSaleInfo.cmb_slNetCd.Enable = false;
		}
		else
		{
			div_afSaleInfo.cmb_slNetCd.Value = div_bfSaleInfo.cmb_slNetCd.Value;
			//div_afSaleInfo.cmb_slNetCd.Enable = true;        
		}

		lf_getPol();

	}


    return;
}

/*******************************************************************************
 * @description    : 일반판매 저장시 상품구분-단말기,USIM validation check
*******************************************************************************/
function lf_saveValidationCheck()
{        
    var bFlag = true;

    var sSaleDt = div_afSaleInfo.cal_saleDtm.Value;
    var sSaleTm = div_afSaleInfo.edt_saleDtmHh.Text + div_afSaleInfo.edt_saleDtmMm.Text + "00";

	// 마감월 체크
	//cf_CheckCloseMnth("03", sSaleDt, "판매일자"); // 03 정산
	cf_CheckCloseDay("02", sSaleDt, "판매일자"); // 02 영업	

	if(FV_BOOLEAN_CLOSE) return false; // 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.  

    if ( uf_IsNull(div_afSaleInfo.cal_saleDtm.Value) )
    {
        alert("변경후 판매일자를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_afSaleInfo.edt_saleDtmHh.Text ) )
    {
        alert("변경후 판매시간를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_afSaleInfo.edt_saleDtmMm.Text ) )
    {
        alert("변경후 판매시간 및 분을 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_afSaleInfo.edt_salePlc.Text ) )
    {
        alert("변경후 판매처를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_afSaleInfo.cmb_saleChgrgId.Value ) )
    {
		if ( parent.ds_channelChgIfInfo.GetColumn(0,"sl_cl") == "01" )
		{
		    alert("변경후 판매담당자를 입력해 주십시요.");
		    return false;
		}
    }

    if ( uf_IsNull(div_afSaleInfo.cmb_slNetCd.Value) )
    {
        alert("변경후 영업망을 선택해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_afSaleInfo.cmb_saleDtlTyp.Value) )
    {
        alert("변경후 판매유형을 선택해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_afSaleInfo.edt_dsNetNm.Text ) )
    {
        alert("변경후 유통망을 선택해 주십시요.");
    //    return false;
    }

    return bFlag;
}
