}



/*******************************************************************************
 * @description : 정산처  팝업 
 ******************************************************************************/
function div_search_btn_dealCo_OnClick(obj)
{
	sf_clearControls("div_search.edt_dealCd,div_search.edt_dealUkey");

	var sPosAgency = div_search.edt_agencyCd.Text;

	if( uf_isNull(sPosAgency) == true)
	{
		alert(cf_getMessage(MSG_00083, "대리점"));
		return;
	}

	var sArg  = "deal_co_cl1="+quote(sf_getDealCoCl1());
		sArg += " pos_agency="+quote(sPosAgency); // 옵션	    
		sArg += " only_stl_plc='Y'";

	cf_callDealPop(sArg, div_search, "edt_dealNm,edt_dealCd,edt_dealUkey");
}	


/*******************************************************************************
 * @description    : 정산처 key down - 이벤트
*******************************************************************************/
function div_search_edt_dealNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13")
	{
		var sPosAgency = div_search.edt_agencyCd.Text;

		if( uf_isNull(sPosAgency) == true)
		{
			alert(cf_getMessage(MSG_00083, "대리점"));
			return;
		}

		var sArg  = "deal_co_cl1="+quote(sf_getDealCoCl1());
			sArg += " pos_agency="+quote(sPosAgency);
			sArg += " only_stl_plc='Y'";

		cf_callDealPop(sArg, div_search, "edt_dealNm,edt_dealCd,edt_dealUkey");
	}
}

function div_search_edt_dealNm_OnCharChanged(obj,strPreText,strPostText)
{
	sf_clearControls("div_search.edt_dealCd,div_search.edt_dealUkey");
}

/*******************************************************************************
 * @description    : 그리드 툴팁
*******************************************************************************/
function grd_list_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex)
{
	if(nCell == -1 || nRow == -1) return;

	grd_list.ToolTipText = grd_list.GetCellText("body", nRow, nCell);
}

/*******************************************************************************
 * @description    :  그리드 더블 클릭 이벤트 
*******************************************************************************/
function grd_GeneralSaleList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var nRow = ds_list.currow;

	var sSaleNo  = ds_list.GetColumn(ds_list.currow, "GNRL_SALE_NO");  	// 일반판매번호
	var sSaleSeq = ds_list.GetColumn(ds_list.currow, "GNRL_SALE_CHG_SEQ");// 일반판매변경순번
	var sTloginEqpMdlCd = ds_list.GetColumn(ds_list.currow, "tlogin_mdl_cd");  		// TLOGIN여부

	var sArg     = "p_gnrl_sale_no="      +quote(sSaleNo);
	    sArg    += " p_gnrl_sale_chg_seq="+quote(sSaleSeq);
	    sArg    += " p_if_proc_cl="       +quote("2");      // 전문처리구분  1: UkeyIF ,2:PS 일반판매관리
        sArg    += " excuteMethod="       +quote("lf_dialogClose");

	Dialog("SAL::SALSMG03110.xml", sArg,410,446);
}
