function SALSMG9999_OnLoadCompleted(obj)
{
	f_init();
}

function f_init()
{
	div_search.cal_saleDtmFrom.value = sf_getFirstDay();
	div_search.cal_saleDtmTo.value   = Today();

	f_setCombo();
}

/*******************************************************************************
 * @description    :  콤보 셋팅
*******************************************************************************/
function f_setCombo()
{
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_search.cmb_slCl" , "ZSAL_C_00260", "전체");	// 영업구분					
	cf_setCommonCode("ds_slcl"             , "ZSAL_C_00260");	// 영업구분					
	cf_setCommonCode("ds_salechghstcl"     , "ZSAL_C_00110");	// 영업구분					
	cf_checkCommonCode(); 

	cf_setInitDataForCmb(div_search.cmb_slNetCd, "전체");
	div_search.cmb_table.Index = 0;
}



function f_isValidSearch()
{
	var sControls  = "div_search.cal_saleDtmFrom,div_search.cal_saleDtmTo";
	var sControlNm = "시작일자,종료일자";

	var arrControl   = Split(sControls , ",");
	var arrControlNm = Split(sControlNm, ",");

	var nCntCntrl = arrControl.length();

	for(var i=0; i<nCntCntrl; i++)
	{
		//var oCntrl = Object(arrControl[i]);
		var oCntrl = eval(arrControl[i]);
		if(oCntrl.Text == "")
		{	
			alert(cf_getMessage(MSG_00083, arrControlNm[i]));
			oCntrl.SetFocus();
			return false;
		}
	}

	var nCompared = sf_compare(div_search.cal_saleDtmFrom.Text,div_search.cal_saleDtmTo.Text);
	if(nCompared == -1)
	{
		alert(cf_getMessage(MSG_00095, "종료일자;시작일자"));
		div_search.cal_saleDtmFrom.SetFocus();
		return false;
	}

	return true;
}

function div_search_btn_search_OnClick(obj)
{
	if(f_isValidSearch() == false) return;

	ds_input.Clear();

	// 넘길 파라메터 설정
	cf_setParam(ds_input, "sale_dtm_from", div_search.cal_saleDtmFrom.Value); 
	cf_setParam(ds_input, "sale_dtm_to"  , div_search.cal_saleDtmTo.Value); 
	cf_setParam(ds_input, "agency_cd"    , div_search.edt_agencyCd.Text); 
	cf_setParam(ds_input, "sale_plc"     , div_search.edt_salePlcCd.Text); 

	cf_setParam(ds_input, "sl_cl"        , div_search.cmb_slCl.Value); 
	cf_setParam(ds_input, "sl_net_cd"    , div_search.cmb_slNetCd.Value);
	cf_setParam(ds_input, "gnrl_sale_no"    , div_search.edt_gnrl_sale_no.Value);

	cf_setParam(ds_input, "svc_mgmt_num"  , div_search.edt_svcMgmtNum.Text); 
	cf_setParam(ds_input, "svc_num"       , div_search.edt_svcNum.Text);
	cf_setParam(ds_input, "prod_cd"       , div_search.edt_prodCd.Text);
	cf_setParam(ds_input, "ser_num"       , div_search.edt_serNum.Text);

	// 트랜잭션 설정
	var sSvcID = "svcList";
	var sInDs  = "nc_input_fieldDs=ds_input";
	var sOutDs = "ds_list=list";
	var sArg   = "sktst.sal.SMG#getGrnlList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}




/*******************************************************************************
 * @description    :  대리점 버튼 클릭 이벤트
*******************************************************************************/
function div_search_btn_agency_OnClick(obj)
{	
	sf_clearControls("div_search.edt_agencyCd,div_search.edt_agencyUkeyCd");
	sf_clearControls("div_search.edt_salePlcNm,div_search.edt_salePlcCd,div_search.edt_salePlcUkeyCd,div_search.edt_salePlcSubCd,div_search.edt_salePlcChannelCd");

	var sArg  = "org_id="+quote("P00000"); 
		sArg += " org_cl="+quote("0"); 

	cf_callAgencyPop(sArg, div_search, "edt_agencyCdNm,edt_agencyCd,edt_agencyUkeyCd");
}



/*******************************************************************************
 * @description    :  대리점 키 이벤트
*******************************************************************************/
function div_search_edt_agencyCdNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{	
	if(nChar == 13)
	{
		var sArg  = "org_id="+quote("P00000"); 
			sArg += " org_cl="+quote("0"); 

		cf_callAgencyPop(sArg, div_search, "edt_agencyCdNm,edt_agencyCd,edt_agencyUkeyCd");
	}
}


/*******************************************************************************
 * @description    :  대리점 문자값 변경 이벤트
*******************************************************************************/
function div_search_edt_agencyCdNm_OnCharChanged(obj,strPreText,strPostText)
{
	sf_clearControls("div_search.edt_agencyCd,div_search.edt_agencyUkeyCd");
	sf_clearControls("div_search.edt_salePlcNm,div_search.edt_salePlcCd,div_search.edt_salePlcUkeyCd,div_search.edt_salePlcSubCd,div_search.edt_salePlcChannelCd");	
}


/*******************************************************************************
 * @description    :  판매처 - 키 이벤트 
*******************************************************************************/
function div_search_edt_salePlcNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	var sPosAgency = div_search.edt_agencyCd.Text;

	if( uf_isNull(sPosAgency) == true)
	{
		alert(cf_getMessage(MSG_00083, "대리점"));
		return;
	}
	if(nChar == 13)
	{
		sf_clearControls("div_search.edt_salePlcCd,div_search.edt_salePlcCdNm");

		var sArg  = "deal_co_cl1="+quote(sf_getDealCoCl1());
			sArg += " pos_agency="+quote(sPosAgency); // 옵션	    
		cf_callDealPop(sArg, div_search, "edt_salePlcNm,edt_salePlcCd,edt_salePlcUkeyCd,edt_salePlcSubCd,edt_salePlcChannelCd");
	}

}

/*******************************************************************************
 * @description    :  판매처 찾기 - 이벤트
