	}
}


function div_search_cmb_table_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var bApply = false;
	var nValue = div_search.cmb_table.Value;
	var nCols  = ds_list.GetColCount();
	var bAll   = false;
	grd_list.Redraw = false;
	for(i=0; i< nCols; i++)
	{
		switch(nValue)
		{
		case 'A':
			if(i >= 0 && i <= 41) bApply = true;
			else bApply = false;
			break;
		case 'B':
			if(i >= 42 && i <= 48) bApply = true;
			else bApply = false;
			break;
		case 'C':
			if(i >= 49 && i <= 56) bApply = true;
			else bApply = false;			
			break;
		case 'D':
			if(i >= 57 && i <= 66) bApply = true;
			else bApply = false;			
			break;
		case 'E':
			if(i >= 67 && i <= 72) bApply = true;
			else bApply = false;			
			break;
		case 'F':
			if(i >= 73 && i <= 78) bApply = true;
			else bApply = false;			
			break;
		case 'G':
			if(i >= 79 && i <= 85) bApply = true;
			else bApply = false;			
			break;
		case 'H':
			if(i >= 86 && i <= 98) bApply = true;
			else bApply = false;			
			break;
		case 'I':
			if(i >= 99 && i <= 112) bApply = true;
			else bApply = false;			
			break;
		case 'J':
			if(i >= 113 && i <= 133) bApply = true;
			else bApply = false;			
			break;
		case 'K':
			if(i >= 134 && i <= 145) bApply = true;
			else bApply = false;			
			break;
		case 'L':
			if(i >= 146 && i <= 158) bApply = true;
			else bApply = false;			
			break;
		case 'M':
			if(i >= 159 && i <= 179) bApply = true;
			else bApply = false;			
			break;
		case 'N':
			if(i >= 180 && i <= 184) bApply = true;
			else bApply = false;			
			break;
		default:
			bAll = true;
			bApply = true;
			break;
		}
		if(bAll == true)
		{
			grd_list.AutoSizeCell("Both",0,i,"head");
		}
		else
		{
			if(bApply == false)
			{
				//trace("width - i : " + i);
				grd_list.ChangeColWidth(0,i,0,"head");
			}	
			else
			{
				//trace("Auto i : " + i);
				grd_list.ChangeColWidth(0,i,100,"head");
				//grd_list.AutoSizeCell("Both",0,i,"head");
			}
		}
	}	
	grd_list.Redraw = true;

}

