/*******************************************************************************
* 함수명 : ACCACO00900_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function ACCACO00900_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);

	http.Sync = true;

	f_initSearchSet();

	grd_cal.Format = "Default";

	f_calInfo();

	f_getOnAgentCmmsAccDay();

	f_calInit();

	http.Sync = false;
}

/*******************************************************************************
* 함수명 : f_callBack
* 기능명 : 트랜잭션 콜백
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{

	if(sSvcID == "getAccMtchDtlList")
	{	
		return;
	}

	if(sSvcID == "getCalInfo")
	{
		if(ds_calInfo.GetColumn(0,"TOTAL_WEEK") == 4){

			grd_cal.Height = "275";
			stc_objCnt.Top = "370";
			edt_objCnt.Top = "370";
			stc_fixCnt.Top = "370";
			edt_fixCnt.Top = "370";
			stc_unFixCnt.Top = "370";
			edt_unFixCnt.Top = "370";
			btn_close.Top = "370";
			this.window.height = "440";

			grd_cal.Format = "FourWeek";

		}else if(ds_calInfo.GetColumn(0,"TOTAL_WEEK") == 5){

			grd_cal.Height = "338";
			stc_objCnt.Top = "435";
			edt_objCnt.Top = "435";
			stc_fixCnt.Top = "435";
			edt_fixCnt.Top = "435";
			stc_unFixCnt.Top = "435";
			edt_unFixCnt.Top = "435";
			btn_close.Top = "435";
			this.window.height = "500";

			grd_cal.Format = "FiveWeek";
		}else{
			grd_cal.Format = "Default";
		}

		stc_objCnt.Visible = true;
		edt_objCnt.Visible = true;
		stc_fixCnt.Visible = true;
		edt_fixCnt.Visible = true;
		stc_unFixCnt.Visible = true;
		edt_unFixCnt.Visible = true;
		btn_close.Visible = true;

		return;
	}

	if(sSvcID == "getOnAgentCmmsAccDay")
	{
		if(ds_onAgentCmmsAccDay.rowcount > 0 ){
			edt_objCnt.Text = ds_onAgentCmmsAccDay.sum("OBJ_CNT");
			edt_fixCnt.Text = ds_onAgentCmmsAccDay.sum("FIX_CNT");
			edt_unFixCnt.Text = toNumber(ds_onAgentCmmsAccDay.sum("FIX_CNT")) -
			                    toNumber(ds_onAgentCmmsAccDay.sum("FIX_CNT"));
		}

		return;
	}

}

/*******************************************************************************
* 함수명 : f_calInfo
* 기능명 : 달력 세팅 조회
*******************************************************************************/
function f_calInfo()
{ 
	ds_calInfo.ClearData();

	var sSvcID = "getCalInfo";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_calInfo=ds_calInfo";
	var sArg   = "sktst.acc.ACO#getCalInfo";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

}

/*******************************************************************************
* 함수명 : f_getOnAgentCmmsAccDay
* 기능명 : 추정일마감 일일 현황조회
*******************************************************************************/
function f_getOnAgentCmmsAccDay()
{ 
	var sSvcID = "getOnAgentCmmsAccDay";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_onAgentCmmsAccDay=ds_onAgentCmmsAccDay";
	var sArg   = "sktst.acc.ACO#getOnAgentCmmsAccDay";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

}

/*******************************************************************************
* 함수명 : f_initSearchSet
* 기능명 : 추정일마감 현황 검색조건 세팅
*******************************************************************************/
function f_initSearchSet()
{

	//정산처 dataset 세팅 
	var objNm = object("parent."+PARENT_GRDDS);

	for( var nRow=0; nRow<objNm.rowcount; nRow++ ){
		ds_stlPlc.AddRow();
		ds_stlPlc.SetColumn(nRow,"ACC_PLC",objNm.GetColumn(nRow,"ACC_PLC"));
		ds_stlPlc.SetColumn(nRow,"DEAL_CO_NM",objNm.GetColumn(nRow,"DEAL_CO_NM"));
	}

	div_search.cmb_dealNm.Value = ACC_PLC;

	cf_setParam(ds_condition,"ACC_MTH"   	   ,ACC_MTH);	//정산월
	cf_setParam(ds_condition,"DEAL_NM"   	   ,DEAL_CO_NM);//정산처명
	cf_setParam(ds_condition,"ACC_PLC"   	   ,ACC_PLC);	//정산처코드
	cf_setParam(ds_condition,"SL_CL"   	   	   ,SL_CL);		//영업구분
	cf_setParam(ds_condition,"POS_AGENCY"      ,POS_AGENCY);
	div_search.edt_agencyCdNm.Text = POS_AGENCY_NM;			//대리점명
	div_search.cal_accMth.Text = ACC_MTH;

	div_search.edt_agencyCdNm.Enable = false;
	div_search.cal_accMth.Enable = false;

}

/*******************************************************************************
* 함수명 : f_calInit
* 기능명 : 추정일마감 달력 
*******************************************************************************/
function f_calInit()
{
	var TOTAL_DAY = toNumber(ds_calInfo.GetColumn(0,"TOTAL_DAY"));	
	var FIRST_WEEK = toNumber(ds_calInfo.GetColumn(0,"FIRST_WEEK"));

	grd_cal.Redraw = false;

	for(var j=0; j<43; j++){

		ds_tCalender.SetColumn(0,"DAY"+j, "");

	}

	for(var i = 0; i < TOTAL_DAY; i++){

		ds_tCalender.SetColumn(0,"DAY"+(FIRST_WEEK-1+i), i+1);

		ds_tCalender.SetColumn(0,"OBJ_CNT"+(FIRST_WEEK-1+i), "대상:"+ds_onAgentCmmsAccDay.GetColumn(i,"OBJ_CNT")); 
		ds_tCalender.SetColumn(0,"FIX_CNT"+(FIRST_WEEK-1+i), "확정:"+ds_onAgentCmmsAccDay.GetColumn(i,"FIX_CNT")); 
		ds_tCalender.SetColumn(0,"CFM_YN"+(FIRST_WEEK-1+i), ds_onAgentCmmsAccDay.GetColumn(i,"CFM_YN")); 

	}

	grd_cal.Redraw = true;

	grd_cal.SetCellPos(0);

}

/*******************************************************************************
* 함수명 : div_search_cmb_dealNm_OnChanged
* 기능명 :  정산처 코드 변경시
*******************************************************************************/
function div_search_cmb_dealNm_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	http.Sync = true;

	div_search.cmb_dealNm.Enable = false;

	ds_condition.SetColumn(0,"ACC_PLC",strCode);

	f_getOnAgentCmmsAccDay();

	f_calInit();

	div_search.cmb_dealNm.Enable = true;

	http.Sync = false;
}

/*******************************************************************************
 * @description    : 닫기
*******************************************************************************/
function btn_close_OnClick(obj)
{
	this.Close();
}
