/*******************************************************************************
 * @description    : 대리점 OnChar Changed 이벤트
*******************************************************************************/
function div_search_edt_ordPlcNm_OnCharChanged(obj,strPreText,strPostText)
{
	if(strPreText != strPostText){
		div_search.edt_ordPlcId.value = "";
		div_search.edt_ordPlcUkeyId.Text = "";
	}
}


/*******************************************************************************
 * @description    : 채권상계 상세내역 및 저장 팝업
*******************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var oldPlcNm = div_search.edt_agencyNm.Text;
	var oldPlcId = ds_dpst_list.GetColumn(ds_dpst_list.currow, "ORG_CD");
	var accMth = div_search.cal_acc_mth.Value;

	var fnshYn = ds_dpst_list.GetColumn(ds_dpst_list.currow, "FNSH_YN");
	var dealCl = ds_dpst_list.GetColumn(ds_dpst_list.currow, "DEAL_CL");
	var dpstPlc = ds_dpst_list.GetColumn(ds_dpst_list.currow, "DPST_PLC");
	var dpstPlcNm = ds_dpst_list.GetColumn(ds_dpst_list.currow, "DPST_PLC_NM");
	var chagAccNo = ds_dpst_list.GetColumn(ds_dpst_list.currow, "CHAG_ACC");
	var casaAccNo = ds_dpst_list.GetColumn(ds_dpst_list.currow, "ETC_ACC");
	var modDtm = ds_dpst_list.GetColumn(ds_dpst_list.currow, "MOD_DTM");
	var erpAccMth = ds_dpst_list.GetColumn(ds_dpst_list.currow, "ACC_MTH");

	ds_childPop.ClearData();

	cf_setParam(ds_childPop, "chag_acc", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CHAG_ACC"));
	cf_setParam(ds_childPop, "pre_chag_acc_dpst_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CASH_DPST_ACC_BAMT"));
	cf_setParam(ds_childPop, "pre_etc_acc_dpst_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "ETC_DPST_ACC_BAMT"));
	cf_setParam(ds_childPop, "chag_acc_dpst_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CHAG_ACC_DPST_AMT"));
	cf_setParam(ds_childPop, "etc_acc", ds_dpst_list.GetColumn(ds_dpst_list.currow, "ETC_ACC"));
	cf_setParam(ds_childPop, "to_chag_acc_dpst_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CASH_DPST_AMT"));
	cf_setParam(ds_childPop, "to_etc_acc_dpst_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "ETC_DPST_AMT"));
	cf_setParam(ds_childPop, "etc_acc_dpst_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "ETC_ACC_DPST_AMT"));
	cf_setParam(ds_childPop, "chag_dpst_acc_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CHAG_DPST_ACC_AMT"));
	cf_setParam(ds_childPop, "prp_dpst_acc_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "PRP_DPST_ACC_AMT"));
	cf_setParam(ds_childPop, "casa_dpst_acc_amt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CASA_DPST_ACC_AMT"));
	cf_setParam(ds_childPop, "chag_acc_dpac_bamt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CHAG_ACC_DPAC_BAMT"));
	cf_setParam(ds_childPop, "etc_acc_dpac_bamt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "ETC_ACC_DPAC_BAMT"));
	cf_setParam(ds_childPop, "chag_bond_bamt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CHAG_BOND_BAMT"));
	cf_setParam(ds_childPop, "prp_bond_bamt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "PRP_BOND_BAMT"));
	cf_setParam(ds_childPop, "casa_bond_bamt", ds_dpst_list.GetColumn(ds_dpst_list.currow, "CASA_BOND_BAMT"));
	cf_setParam(ds_childPop, "rmks", ds_dpst_list.GetColumn(ds_dpst_list.currow, "RMKS"));


	var dsParent = "ds_childPop";

	var sArg  = "dsParent='"+dsParent+"'";
		sArg += " sOldPlcId=" + quote(oldPlcId);
		sArg += " sOldPlcNm=" + quote(oldPlcNm);
		sArg += " sAccMth=" + quote(accMth);
		sArg += " sFnshYn=" + quote(fnshYn);
		sArg += " sDealCl=" + quote(dealCl);
		sArg += " sDpstPlc=" + quote(dpstPlc);
		sArg += " sDpstPlcNm=" + quote(dpstPlcNm);
		sArg += " sChagAccNo=" + quote(chagAccNo);
		sArg += " sCasaAccNo=" + quote(casaAccNo);
		sArg += " sModDtm=" + quote(modDtm);
		sArg += " sErpAccMth=" + quote(erpAccMth);

	dialog("ACC::ACCPAC00200.xml", sArg, 700, 600 );

}


