/*******************************************************************************
* 함수명 : f_gridCheckAll
* 기능명 :  그리드의 확정 CHECK 버튼 클릭시
*******************************************************************************/
function f_gridCheckAll(grid,ds)
{
	ds.fireEvent = false;

	// Check Box 전체 선택
	var nCount = ds.rowcount;
	if( grid.GetCellProp("head", 1, "Text") == "1" ) {

		for ( var irow=0; irow < nCount; irow++ ) {

			ds.SetColumn(irow,"CHECKED","0");

			grid.SetCellProp("head",1,"text","0");

		}


	} else {

		for ( var irow=0; irow < nCount; irow++ ){

			ds.SetColumn(irow,"CHECKED","1");

			grid.SetCellProp("head",1,"text","1");

		}

	}

	ds.fireEvent = true;
}



function grdList_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{

	if( nCell==1 )
	{
		f_gridCheckAll(obj,ds_dpst_list);  //전부 체크

	}else{
		cf_markGridSort(obj, nCell); //sort
	}
}
