/*******************************************************************************
* 함수명 : btn_export_OnClick
* 기능명 :  엑셀 export
*******************************************************************************/
function btn_export_OnClick(obj)
{
	//cf_exportExcel(grd_dayClsDetail);
	var sSvcID = "getSaleDetailExcel";
	var sInDs  = "ds_condition=ds_condition";
	var sOutDs = "ds_Pol=rs_Pol ds_Pol2=rs_Pol2 ds_polSplstTmp=ds_polSplstTmp";
	var sArg   = "sktst.acc.DCL#getSaleDetailExcel";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

