/*******************************************************************************
* 함수명 : sample_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
--------------------------------------------------------------------------------
* 작성자      : 전현주
* 작성일      : 2009.02.04
-------------------------------------------------------------------------------
* 1. 변경이력
수정자    :
수정일    :
수정 내역 :
*******************************************************************************/

function ACCPDS00300_OnLoadCompleted(obj)
{

	f_init();

	cf_setGrdChecknSortEvent(grd_prchs_list);

	cf_setAuth(FV_MENU_NO);	

	uf_enableAgencyByAuth("div_search", "edt_ordPlcNm,edt_ordPlcId,btn_ord", "edt_ordPlcUkeyId");

}


/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
--------------------------------------------------------------------------------
* 작성자      : 전현주
* 작성일      : 2009.02.04
--------------------------------------------------------------------------------
* 1. 변경이력
수정자    :
수정일    :
수정 내역 :
*******************************************************************************/
function f_callBack(sSvcID)
{   
    if(sSvcID == "svcSearch")
    {   

    } else if(sSvcID == "svcDetail")
    {   
        Div1.cal_acc_mth.Enable = false;
		Div1.edt_prchsPlcDetailId.Enable = false;
		Div1.edt_prchsPlcDetailNm.Enable = false;
		Div1.cal_aply_sta_dtm.Enable = false;
		Div1.cal_aply_end_dtm.Enable = false;

		f_countPrchsProd();

    } else if(sSvcID == "svcPopup"){

		cf_copyDataSetPop(ds_prchs_detail, F_row, ds_prchs_detail_0, 0);

    } else if(sSvcID == "svcAccDt"){

		var sAccDt = ds_acc_dt.GetColumn(0,"ACC_DT");
		Div1.cal_acc_mth.Text = sAccDt;
		var sPrchsPlc = div_search.edt_prchsPlcId.Text;
		Div1.edt_prchsPlcDetailId.Text= sPrchsPlc;
		Div1.edt_prchsPlcDetailNm.Text= div_search.edt_prchsPlcNm.Text;
		ds_prchs_condition.SetColumn(0,"PRCHS_PLC",sPrchsPlc);
		ds_prchs_condition.SetColumn(0,"PRCHS_PRD_FROM","");
		ds_prchs_condition.SetColumn(0,"PRCHS_PRD_TO","");

		Div1.cal_acc_mth.Enable = true;
		Div1.edt_prchsPlcDetailId.Enable = true;
		Div1.cal_aply_sta_dtm.Enable = true;
		Div1.cal_aply_end_dtm.Enable = true;

    } else if(sSvcID == "svcPrchsQty"){
		f_countPrchsProd();
    } else if(sSvcID == "svcProdCnt"){
		PROD_CNT = ds_prod_cnt.GetColumn(0,"CNT");
    } else if(sSvcID == "svcUpdatePrchsDc"){

		alert(cf_getMessage(MSG_00049, "매입할인"));
    }
}



