		sArg += " sUkeyId='' ";
		sArg += " sOrdPlcNm='' ";
		sArg += " sAccMth='' ";
		sArg += " sPrchsPlc='' ";
		sArg += " sPrchsPlcNm='' ";
		sArg += " sPrchsPrdFrom='' ";
		sArg += " sPrchsPrdTo='' ";
		sArg += " sAccDt='' ";
		sArg += " sInFixYn='' ";
		sArg += " sPrchsDebtFlag=" + quote("0"); 
		sArg += " cudFlag=" + quote("I"); 

    gf_openWindow("100000165", sArg); 
}

/*******************************************************************************
* 함수명 : df_update
* 기능명 : 공통 수정 버튼 클릭
*******************************************************************************/
function df_update(sButtonID)
{
	if(confirm("수정하시겠습니까?") != 1) 
		return;

	var sSvcID 	= "svcUpdate";
	var sInDs 	= "ds_prchs_condition=ds_prchs_detail:u";
	var sOutDs 	= "ds_prchs_detail=prchsDcDetail";
	var sArg	= "sktst.acc.PDS#updatePrchsDc";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

function prchs_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_prodList.rowcount < 1) {
		return false;
	} else {

		var oGrdDs = object(obj.BindDataset);

		var sArg  = "sOrgCd="+quote(ds_condition.getColumn(0,"ORG_CD"));
			sArg += " sUkeyId=" + quote(div_search.edt_ordPlcUkeyId.Text); 
			sArg += " sOrdPlcNm=" + quote(div_search.edt_ordPlcNm.Text); 
			sArg += " sAccMth=" + quote(ds_condition.getColumn(0,"ACC_MTH")); 
			sArg += " sPrchsPlc=" + quote(oGrdDs.getColumn(nRow,"PRCHS_PLC")); 
			sArg += " sPrchsPlcNm=" + quote(oGrdDs.getColumn(nRow,"PRCHS_NM")); 
			sArg += " sAccDt=" + quote(oGrdDs.getColumn(nRow,"ACC_DT"));
			sArg += " sInFixYn=" + quote(oGrdDs.getColumn(nRow,"FIX_YN"));
			sArg += " sPrchsDebtFlag=" + quote(oGrdDs.getColumn(nRow,"DEBT_FIX_YN"));
			sArg += " cudFlag=" + quote("U"); 

		gf_openWindow("100000165", sArg);
	}	 
}




/*******************************************************************************
 * @description    : 정산월 초기화면 셋팅
*******************************************************************************/
function f_init()
{
	div_search.cal_acc_mth.Value = cf_today();
}


/*******************************************************************************
 * @description    : 매입할인 목록 조회 전 검색절 체크
*******************************************************************************/
function f_pre_check(){

	if(uf_isNull(trim(div_search.edt_ordPlcId.Text))){
		alert("대리점을 선택하십시오.");
		div_search.edt_ordPlcNm.SetFocus();
		return false;
	}else if(uf_isNull(ds_condition.GetColumn(0,"ACC_MTH")) ==true){
		alert("정산월을 선택하십시오.");
		div_search.cal_acc_mth.SetFocus();
		return false;
	}

	return true;
}

/*******************************************************************************
 * @description    : 매입할인 내역 조회
*******************************************************************************/
function btn_search_onClick(obj)
{	
	f_search();

}

function f_search(orgCd, orgNm, uKeyId, accDt)
{
	if(orgCd == null)
	{
		orgCd = "";
		uKeyId = "";
		orgNm = "";
		accDt = "";
	}else{
		ds_condition.SetColumn(0,"ORG_CD",orgCd);
		div_search.edt_ordPlcNm.Text = orgNm;
		div_search.edt_ordPlcUkeyId.Text = uKeyId;
		ds_condition.SetColumn(0,"ACC_MTH",accDt);
	}


	if(!f_pre_check()){
		return;
	}

	ds_prchs_detail.ClearData();

	ds_condition.SetColumn(0,"ACC_MTH",substr(div_search.cal_acc_mth.Value,0,6));

	ds_prodList.GroupKey = "PRCHS_PLC";

	var sSvcID = "svcSearch";
	var sInDs = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_prodList=prchsDcList";
	var sArg = "sktst.acc.PDS#getPrchsDcList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	
}


/*******************************************************************************
 * @description    : 매입할인 내역 저장
*******************************************************************************/
function btn_save_onClick(obj)
{
	var nRow = ds_prchs_list.rowcount;

	if(nRow == 0){
		alert("등록한 상세내역이 없습니다.");
		return false;
	}

	if(Confirm("저장하시겠습니까?") == true) {

		var sSvcID 	= "svcUpdatePrchsDc";
		var sInDs 	= "nc_input_fieldDs=ds_condition ds_prchs_list=ds_prchs_list";
		var sOutDs 	= "ds_prchs_list=prchsDcList";
		var sArg	= "sktst.acc.PDS#updatePrchsDcFixYn";

		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

	} else {
		return false;
	}
}

