*******************************************************************************/
var FV_POS_AGENCY_ID;		// 대리점 코드
var FV_POS_AGENCY_UKEY_ID;	// 대리점 UKey 코드
var FV_POS_AGENCY_NM;		// 대리점명
var cmmsAddSubD = "N";
var accMtchDtlD = "N";
var FV_CLOSE_DT;

/*******************************************************************************
* 함수명 : ACCSAC00300_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function ACCSAC00300_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);

	http.Sync = true;

	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("ds_addSubCl", "ZACC_C_00160");  		   //가감구분
	cf_checkCommonCode();

	cf_setParam(ds_condition,"SL_CL", "03");

	f_getLastClsDtm();

	//마감일 세팅
	if(ds_lastClsDtm.rowcount == 0){
		div_search.cal_accYm.Value = substr(TODAY(),0,6);
	}else{
		cf_setParam(ds_condition,"ACC_MTH"   ,ds_lastClsDtm.GetColumn(0,"LAST_CLS_DTM"));
	}

	FV_POS_AGENCY_ID      = gds_session.GetColumn(0,"posAgencyId");
    FV_POS_AGENCY_NM      = gds_session.GetColumn(0,"posAgencyNm");
    FV_POS_AGENCY_UKEY_ID = gds_session.GetColumn(0,"posAgencyUkeyId");

	// 소속 대리점이 존재하면 대리점 셋팅 / 대리점찾기버튼 비활성화
	if( !uf_IsNull(FV_POS_AGENCY_ID) ){
		div_search.edt_agencyCd.value = FV_POS_AGENCY_ID;
		div_search.edt_agencyUkeyCd.value = FV_POS_AGENCY_UKEY_ID;
		div_search.edt_agencyCdNm.value = FV_POS_AGENCY_NM;

		div_search.edt_agencyCdNm.Enable = false;
		div_search.btn_agency.Enable = false;
	}
	else
	{
		div_search.edt_agencyCdNm.Enable = true;
		div_search.btn_agency.Enable = true;

	}

	cf_setParam(ds_condition,"MNP_CMMS_CL", "1");

	http.Sync = false;

}

/*******************************************************************************
* 함수명 : f_callBack
* 기능명 : 트랜잭션 콜백
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	if(sSvcID == "svcSearch")
	{
		ds_monthClsList.Sort("DEAL_CO_NM");
		cf_copyDataset(ds_copyMonthClsList, ds_monthClsList, null, true);
		cf_setOrgDs(ds_monthClsList); // 창 닫기 이벤트시 변경여부 채크

		if(ds_monthClsList.rowcount > 0){
			if(btn_save.Visible == true){
				img_polChg.Visible = true;
				btn_addSubExcel.Visible = true;
				btn_accMtchExcel.Visible = true;
			}
		}else{
			btn_addSubExcel.Visible = false;
			btn_accMtchExcel.Visible = false;
		}

		return;
	}

	if(sSvcID == "svcSearchGetLastClsDtm")
	{
		return;
	}

	if(sSvcID == "svcSearchMnp")
	{
		return;
	}

	if(sSvcID == "svcSearchFeeMonthCls")
	{
		var FEE_MONTH_CLS = toNumber(ds_feeMonthCls.GetColumn(0,"FEE_MONTH_CLS"));

		if(FEE_MONTH_CLS == 0){
			btn_save.Enable = false;
			alert("수수료 월마감이 완료되지 않아 저장할 수 없습니다.");

		}else{
			btn_save.Enable = true;
		}

		return;
	}


	if(sSvcID == "saveOnSaleCmmsAccMonthList")
	{
		df_search();

		return;
	}

	if(sSvcID == "svcDetailSearch")
	{
		ds_monthClsDetail.Sort("SALE_PLC_NM,SVC_DTM");
		cf_exportExcel(grd_monthClsDetail);
	}

}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search()
{
	ds_cmmsAddSubD.ClearData();
	ds_accMtchDtlD.ClearData();
	ds_delCmmsAddSubD.ClearData();
	ds_delAccMtchDtlD.ClearData();

	if(uf_isNull(ds_condition.GetColumn(0,"POS_AGENCY")) == true){
		alert(cf_getMessage(MSG_00135, "대리점"));
		div_search_btn_agency_OnClick();
		return false;
	}

	http.Sync = false;

	FV_CLOSE_DT = div_search.cal_accYm.Value;

	var sSvcID = "svcSearch";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_monthClsList=ds_monthClsList";
	var sArg   = "sktst.acc.SAC#getOnSaleCmmsAccMonthList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
}

/*******************************************************************************
* 함수명 : f_pre_save
* 기능명 : 저장버튼 전 체크함수
*******************************************************************************/
function f_pre_save()
{

	if(ds_monthClsList.rowcount < 1){
		alert(cf_getMessage(MSG_00134, "정산처"));
		return false;
	}

	if(!ds_monthClsList.GetUpdate()){
		alert(cf_getMessage(MSG_00133));
		return false;
	}

    return true;
}

/*******************************************************************************
 * @description    : 저장
*******************************************************************************/
function df_save()
{

	cf_CheckCloseMnth('06',FV_CLOSE_DT,"정산월");  	
	if(FV_BOOLEAN_CLOSE) return;

	cf_CheckCloseDay('04',substr(TODAY(),0,8),"정산월");  	
	if(FV_BOOLEAN_CLOSE) return;

	if (!(f_pre_save())) return;
	//alert(ds_cmmsAddSubD.SaveXML());
	if(confirm(cf_getMessage(MSG_00136, "저장")) == 1){

		var sSvcID = "saveOnSaleCmmsAccMonthList";
		var sInDs  = "ds_monthClsList=ds_monthClsList:U ds_cmmsAddSubD=ds_cmmsAddSubD ds_accMtchDtlD=ds_accMtchDtlD ds_delCmmsAddSubD=ds_delCmmsAddSubD ds_delAccMtchDtlD=ds_delAccMtchDtlD ds_condition=ds_condition";
		var sOutDs = "";
		var sArg   = "sktst.acc.SAC#saveOnSaleCmmsAccMonthList";

		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
	}
}

/*******************************************************************************
* 함수명 : grd_monthClsList_OnExpandUp
* 기능명 :  수수료 가감 팝업 호출
*******************************************************************************/
function grd_monthClsList_OnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	var oGrdDs = object(obj.BindDataset);

	var sArg  = "ACC_MTH=" + quote(oGrdDs.getColumn(nRow,"ACC_MTH"));
		sArg += " DEAL_CO_NM=" + quote(oGrdDs.getColumn(nRow,"DEAL_CO_NM"));
		sArg += " SL_CL=" + quote(oGrdDs.getColumn(nRow,"SL_CL"));
		sArg += " PAY_CL=" + quote("030");
		sArg += " ACC_PLC=" + quote(oGrdDs.getColumn(nRow,"ACC_PLC"));
		sArg += " POS_AGENCY=" + quote(oGrdDs.getColumn(nRow,"POS_AGENCY"));
		sArg += " POS_AGENCY_NM=" + quote(oGrdDs.getColumn(nRow,"POS_AGENCY_NM"));
		sArg += " ERP_FIX_YN=" + quote(oGrdDs.getColumn(nRow,"ERP_FIX_YN"));
		sArg += " FIX_YN=" + quote(oGrdDs.getColumn(nRow,"FIX_YN"));
		sArg += " SALE_CMMS_FIX_YN=" + quote(oGrdDs.getColumn(nRow,"SALE_CMMS_FIX_YN"));
		sArg += " ADD_SUB_DTL_YN=" + quote(oGrdDs.getColumn(nRow,"ADD_SUB_DTL_YN"));
		sArg += " MTCH_DTL_YN=" + quote(oGrdDs.getColumn(nRow,"MTCH_DTL_YN"));
		sArg += " PARENT_CURROW=" + quote(nRow);
		sArg += " PARENT_GRDDS=" + quote("ds_monthClsList");

	if(nCell == 7){ //수수료가감 popup 호출

		var rtn = dialog("ACC::ACCACO00600.xml", sArg, 1024, 600);

		if(rtn == 1){
			ds_monthClsList.setColumn(nRow,"ADD_SUB_DTL_YN","Y");
			ds_monthClsList.setColumn(nRow,"CHK","1");
		}

	}else if(nCell == 12){ //정산상계 popup 호출

		var rtn = dialog("ACC::ACCACO00700.xml", sArg, 1024, 600);

		if(rtn == 1){
			ds_monthClsList.setColumn(nRow,"MTCH_DTL_YN","Y");
			ds_monthClsList.setColumn(nRow,"CHK","1");
		}

	}
}

