/*******************************************************************************
 * Desc   : 온라인소매 수수료 정산 관리 리스트  확정 버튼 클릭시
 * Auth   : 전승호 (2009.01.15)
*******************************************************************************/
function ds_monthClsList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "CHK"){

		if(varOldValue == "1"){

			//확정된 정산처가 ERP 전송이 완료된 건은 해제불가
			if(ds_monthClsList.GetColumn(nRow,"ERP_FIX_YN") == "Y"){
				ds_monthClsList.SetColumn(nRow,"CHK",varOldValue);
				alert(cf_getMessage(MSG_00023, "ERP 전송완료된 정산처"));
				return false;
			}else{
				if(ds_monthClsList.GetColumn(nRow,"ORG_CHK") == "1"){
					if(confirm(cf_getMessage(MSG_00136, "정산처를 초기화")) == 1){

						var ACC_PLC = ds_monthClsList.GetColumn(nRow,"ACC_PLC");
						var ACC_PLC_COPY = ds_copyMonthClsList.FindRow("ACC_PLC",ACC_PLC);

						//수수료가감내역 삭제
						for(var iRow = ds_cmmsAddSubD.rowCount; iRow>=0; iRow--) {

							if(ds_cmmsAddSubD.GetColumn(iRow,"ACC_PLC") == ACC_PLC){
								ds_cmmsAddSubD.deleteRow(iRow);
							}
						}
						ds_monthClsList.setColumn(nRow,"ADD_SUB_DTL_YN","N");
						ds_monthClsList.setColumn(nRow,"CMMS_ADD_SUB_AMT",toNumber(ds_copyMonthClsList.GetColumn(ACC_PLC_COPY,"CMMS_ADD_SUB_AMT")));


						//정산상계내역 삭제
						for(var jRow = ds_accMtchDtlD.rowCount; jRow>=0; jRow--) {

							if(ds_accMtchDtlD.GetColumn(jRow,"ACC_PLC") == ACC_PLC){
								ds_accMtchDtlD.deleteRow(iRow);
							}
						}
						ds_monthClsList.setColumn(nRow,"MTCH_DTL_YN","N");
						ds_monthClsList.setColumn(nRow,"ACC_MTCH_AMT",toNumber(ds_copyMonthClsList.GetColumn(ACC_PLC_COPY,"ACC_MTCH_AMT")));

					}else{
						ds_monthClsList.SetColumn(nRow,"CHK",varOldValue);
					}

				}
			}

		}else{

			//판매수수료 확정여부가 Y 가 아닌 일반판매단말기의 정산처가 존재하면 확정 불가
			if(ds_monthClsList.GetColumn(nRow,"SALE_CMMS_FIX_YN") != "Y"){
				ds_monthClsList.SetColumn(nRow,"CHK",varOldValue);
				alert(cf_getMessage(MSG_00023, "추정일마감 안된 정산처"));
				return false;
			}

		}
	}

	if(strColumnID == "CMMS_ADD_SUB_AMT"){

		obj.setColumn(nRow,"FIX_CNSG_CMMS",toNumber(obj.getColumn(nRow,"FIX_CNSG_CMMS")) +
										   toNumber(varNewValue) -
										   toNumber(varOldValue));

		obj.setColumn(nRow,"FIX_SUPRT_CNSG_CMMS",round(toNumber(obj.getColumn(nRow,"FIX_CNSG_CMMS")/1.1)));

		obj.setColumn(nRow,"FIX_FEE_CNSG_CMMS",toNumber(obj.getColumn(nRow,"FIX_CNSG_CMMS")) -
											   round(toNumber(obj.getColumn(nRow,"FIX_CNSG_CMMS")/1.1)));


		obj.SetColumn(nRow,"ACC_AMT",toNumber(obj.getColumn(nRow,"FIX_CNSG_CMMS")) +
									 toNumber(obj.getColumn(nRow,"ACC_MTCH_AMT")));
	}

	if(strColumnID == "ACC_MTCH_AMT"){
		obj.SetColumn(nRow,"ACC_AMT",toNumber(obj.getColumn(nRow,"FIX_CNSG_CMMS")) +
									 toNumber(obj.getColumn(nRow,"ACC_MTCH_AMT")));
	}

}

