var FV_ORG_CL;         	//조직구분
var FV_SUP_ORG_ID;      //상위조직코드
var FV_USER_GRP;        //사용자그룹
var FV_ORG_AREA_ID; 	// 근무지 cd.
var FV_ORG_AREA_NM; 	// 근무지 nm.
var FV_POS_AGENCY_ID;   // 소속 대리점ID
