var FV_DEAL_CO_CD;
var FV_SAVE_CD;
var FV_SEQ;

function ACCSBC00350_OnLoadCompleted(obj)
{
	div_search.edt_dealCoCl.Text = sDealCoClNm;	
	div_search.edt_stlPlcNm.Text = sDealCoNm;
	div_search.edt_grtInsuNm.Text = sGrtInsuCoNm;
	div_search.edt_RmnCrdt.Text = NumFormat(sRmnCrdt);

	FV_DEAL_CO_CD = sDealCoCd;

	cf_setParam(ds_condition, "ORG_CD", sOrgCd);
	cf_setParam(ds_condition, "DEAL_CO_CD", sDealCoCd);

	f_getSaleCrdtDetail();

}


/*******************************************************************************
 * @description    : msie call
*******************************************************************************/
function msie_callscript(func_name)
{
	var objDocument = div_search.MSIE0.Document;

	var objHtmlElement = objDocument.body;

	var createText = '<input type="hidden" name = "miscript" onclick = "javascript:'+func_name+'">';
	var item0 = objDocument.createElement(createText);

	objHtmlElement.appendChild(item0);

	if(item0 !=null) item0.Click();

}


/*******************************************************************************
 * @description    : 여신이력 조회
*******************************************************************************/
function f_getSaleCrdtDetail()
{
    var sSvcID 	= "getSalesUser";
	var sInDs  	= "nc_input_fieldDs=ds_condition";
	var sOutDs 	= "ds_list=rs_list";
	var sArg	= "sktst.acc.SBC#getSalesCrdtDetail";
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}


