function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 4){

		FV_SEQ = "";

		if(ds_list.GetColumn(nRow,"APPR_DT") ==null){

			var sInSeq = ds_list.GetColumn(nRow,"SEQ");

			FV_SEQ = sInSeq;

			//var PageUrl = "http://localhost:8088/ps/ApprovalReceiveData.jsp?code="+FV_DEAL_CO_CD+"&seq="+sInSeq;

			var PageUrl = gv_url+"sso/ApprovalReceiveData.jsp?code="+FV_DEAL_CO_CD+"&seq="+sInSeq;

			MSIE0.Navigate(PageUrl);

		}
	}
}

function MSIE0_TitleChange(obj,Text)
{
	if(Text.indexOf("|") >=0){


		var sApprCheck = substr(Text, 0, Text.indexOf("|"));

		sApprCheck = trim(sApprCheck);
		var sApprChkStr = substr(Text, Text.indexOf("|")+1);
		var sApprUserId = "";
		var sApprDtm = "";


		var sPos = IndexOf(sApprChkStr, "|");

		if (sPos == -1) {
			sApprUserId = "";
			sApprDtm = "";
		} else {
			sApprUserId = substr(sApprChkStr, 0, sPos);
			sApprDtm = substr(sApprChkStr, sPos + 1, sApprChkStr.length());
			sApprDtm = substr(sApprDtm,0,4)+substr(sApprDtm,5,2)+substr(sApprDtm,8,2)+substr(sApprDtm,11,2)+substr(sApprDtm,14,2)+substr(sApprDtm,17,2);

		}


		if(!(sApprCheck.length >0 && sApprUserId.length >0 && sApprDtm.length >0)){
			alert("승인 요청중입니다.");
			return false;
		}else{


			if(sApprCheck == "Y"){
				alert("승인이 완료되었습니다.");

				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "APPR_CHECK",trim(sApprCheck));
				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "APPR_USER_ID",trim(sApprUserId));
				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "APPR_DT",trim(sApprDtm));
				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "FLAG","Y");

			}else{
				alert("반려되었습니다.");

				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "APPR_CHECK",trim(sApprCheck));
				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "APPR_USER_ID",trim(sApprUserId));
				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "APPR_DT",trim(sApprDtm));
				ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "FLAG","Y");
				//ds_list.SetColumn(ds_list.FindRow("seq",FV_SEQ), "FLAG","N");
			}

		}

	}
}



function btn_save_OnClick(obj)
{
	var chkCnt = ds_list.CaseCount("APPR_CHECK==" + quote("Y"));

	if(chkCnt < 1) {
		alert("승인완료된 이력이 없습니다.");
		return false;
	}


	ds_listPop.ClearData();

	cf_copyDataset(ds_listPop, ds_list, null, false, true);
	ds_listPop.Filter("APPR_CHECK==" + quote("Y"));

	cf_setParam(ds_condition, "APPR_CHECK", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_CHECK"));
	cf_setParam(ds_condition, "APPR_USER_ID", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_USER_ID"));
	cf_setParam(ds_condition, "APPR_DT", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_DT"));
	cf_setParam(ds_condition, "CRDT_LMT_AMT", ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"));

	var sSvcID 	= "svcSave";
	var sInDs  	= "nc_input_fieldDs=ds_condition ds_listPop=ds_listPop";
	//var sOutDs 	= "ds_result=ds_list";
	var sOutDs 	= "";
	var sArg	= "sktst.acc.SBC#saveApprovalReceiveList";
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	
}



/*******************************************************************************
 * @description    :  콜백
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcSave") {

	//Parent.div_search_btn_search_OnClick();

	var sInCrdtLmtAmt = toNumber(parent.ds_list.GetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "CRDT_LMT_AMT"));
	var sInRmnCrdt = toNumber(parent.ds_list.GetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "RMN_CRDT"));

	if(ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_CHECK") == "Y"){
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "APPR_CHECK", "승인완료");
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "APPR_USER_ID", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_USER_ID"));
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "APPR_DT", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_DT"));
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "CRDT_LMT_AMT", ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"));
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "PCT", round((toNumber(ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"))*100)/(parent.ds_list.GetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "INSP_GUA_AMT")),0));
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "RMN_CRDT", (sInRmnCrdt-sInCrdtLmtAmt+toNumber(ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"))));
	}else{
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "APPR_CHECK", "승인반려");
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "APPR_USER_ID", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_USER_ID"));
		parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "APPR_DT", ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_DT"));
		//parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "CRDT_LMT_AMT", ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"));
		//parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "PCT", round((toNumber(ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"))*100)/(parent.ds_list.GetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "INSP_GUA_AMT")),0));
		//parent.ds_list.SetColumn(parent.ds_list.FindRow("deal_co_cd",FV_SAVE_CD), "RMN_CRDT", (sInRmnCrdt-sInCrdtLmtAmt+toNumber(ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT"))));

	}

  	//this.Close();
	}

}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "FLAG")
	{
		//alert("varNewValue=-====="+varNewValue);

		if( varNewValue == "Y" ){

		FV_SAVE_CD ="";

		var sInDealCoCd = ds_list.GetColumn(nRow,"DEAL_CO_CD");
		FV_SAVE_CD = sInDealCoCd;
		var sInSeq = ds_list.GetColumn(nRow,"SEQ");

		ds_listPop.ClearData();
		cf_copyDataset(ds_listPop, ds_list, null, false, true);

		ds_listPop.Filter("deal_co_cd='"+sInDealCoCd+"'&&seq='"+sInSeq+"'");



		cf_setParam(ds_condition, "APPR_CHECK", trim(ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_CHECK")));
		cf_setParam(ds_condition, "APPR_USER_ID", trim(ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_USER_ID")));
		cf_setParam(ds_condition, "APPR_DT", trim(ds_listPop.GetColumn(ds_listPop.rowcount-1,"APPR_DT")));
		cf_setParam(ds_condition, "CRDT_LMT_AMT", trim(ds_listPop.GetColumn(ds_listPop.rowcount-1,"CRDT_LMT_AMT")));

		var sSvcID 	= "svcSave";
		var sInDs  	= "nc_input_fieldDs=ds_condition ds_listPop=ds_listPop";
		//var sOutDs 	= "ds_result=ds_list";
		var sOutDs 	= "";
		var sArg	= "sktst.acc.SBC#saveApprovalReceiveList";
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	

		}
	}
}
