//==============================================================================
// 공통 스크립트 로딩
//==============================================================================
#include "lib::lib_commF.js"
#include "lib::lib_commMsg.js"
#include "lib::lib_commPopup.js"
#include "lib::lib_util.js"

//==============================================================================
// 폼 변수 정의
//==============================================================================
var pPortalId;						// 통합 Login ID
var pInUserId;						// 사용자 ID
var pInStatus;						// 상태값(M:상세조회 및 비밀번호 수정, N:신규)
var pInModFlag;						// 본인 여부(true: 본인은 비밀번호 수정가능, false: 타인은 조회만 가능)
var pPTL_OrgID;
var pPTL_RelOrgID;
var pUserGrp;

var pEff_Sta_dt;
var pEff_End_dt;
var pChannelCD;

var FV_USER_INFO_WINDOW_YN; 		//  판매점관리에 사용자정보 화면 여부.
var FV_USERGRP;						// 사용자그룹
var FV_DEAL_CL2;					// 사용자의 원 근무지의 2/3차점 구분
var FV_MBL_PHONE1;					// 기준/정책/재고 SMS1
var FV_MBL_PHONE2;					// 기준/정책/재고 SMS2
var FV_MBL_PHONE3;					// 기준/정책/재고 SMS3
var FV_UKEY_ID;

/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function form_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);
	// Global Session Info.
	lf_getSession();

	if(IsExistVar("sInStatus")){  
		pPortalId     = sPortalId;
		pInUserId     = sInUserId;
		pInStatus     = sInStatus;
		pInModFlag    = sInModFlag;	

		pPTL_OrgID    = sOrgID;
		pPTL_RelOrgID = sRelOrgID;
		pChannelCD    = sChannelCD;

		FV_USER_INFO_WINDOW_YN = "N";

	}else{
		// 판매점관리에 있는 사용자 조회로 화면을 조회 시
		pInUserId = gds_session.GetColumn(0,"loginId");
		pInStatus = "M";
		FV_USER_INFO_WINDOW_YN = "Y";
	}

	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();	

	//  소속구분 : ZBAS_C_00380
	cf_setCommonCode("ds_attc", "ZBAS_C_00380");

	// 설정된 코드를 기반으로 공통 코드를 추가 및 데이타 바인딩
	cf_checkCommonCode();		

	// 패스워드 초기화버튼을 저장버튼과 동일 권한 적용.
	// KIMYS 2010.08.24 판매점포탈SSO 관련 비밀번호변경 없음
//KIMYS	btn_pwdReset.Visible = btn_save.Visible;

	// 상세보기
	if(pInStatus == "M" && pInUserId != "")
	{
		//Div0.edt_Pwd.SetFocus();
		f_getUserInfo();
		btn_pwdReset.Enable = true;
	}
	if(pInStatus == null) pInStatus = "N";

	// 사용자 신규 생성
	if(pInStatus == "N")
	{
		ds_user.ClearData();
		ds_user.AddRow();

		// 통합 Login ID Setting
		Div4.edt_PortalUserID.Text = pPortalId;

		//Div0.edt_UserCd.SetFocus();
		Div0.chk_EffUserYn.Value = "Y";
		Div0.Static4.Visible = false;
		Div0.edt_pwdExpDt.Visible = false;
		f_editAllowed();
		// 사용자그룹 가져오기
		f_getUserGrpList();
		// 사용자직책 가져오기
		f_getUserDutyList();

		Div0.edt_Pwd.Text = "0000";
		Div0.edt_PwdCon.Text = "0000";		

		// 원본 데이터셋 생성
		cf_setOrgDs(ds_user);

	}

	// 판매점포탈의 경우 SSO로 부터 사용자명을 I/F
	// 기본 편집가능 .. Portal I/F인 경우 편집불가
	// f_editAllowed 내에 있던 것을 이쪽으로 가지고 옮...
	Div0.edt_UserNm.Enable = true;
	if(pInStatus == "P")
	{
		ds_user.ClearData();
		ds_user.AddRow();

		//Div0.edt_UserCd.SetFocus();
		Div0.chk_EffUserYn.Value = "Y";
		Div0.Static4.Visible = false;
		Div0.edt_pwdExpDt.Visible = false;

		f_editAllowed();
		// 사용자그룹 가져오기
		f_getUserGrpList();
		// 사용자직책 가져오기
		f_getUserDutyList();

		// 원본 데이터셋 생성
		cf_setOrgDs(ds_user);

        //alert(Length(sPortalId));
        var sUserCd = "";
        if (Length(sPortalId) > 10) {
           sUserCd = SubStr(sPortalId,0,10);
        } else {
           sUserCd = sPortalId;
        }

        //alert(sPortalId);

		pEff_Sta_dt                  = sEffSta;
		pEff_End_dt                  = sEffEnd;
		Div4.edt_PortalUserID.Text   = sPortalId;
		Div0.edt_UserCd.Text         = sUserCd;
		Div4.edt_UserId.Text         = sPortalId;
		Div0.edt_Pwd.Text            = sPWD;
		Div0.edt_PwdCon.Text         = sPWD;
		Div0.edt_UserNm.Text         = sHanNM;
		Div0.edt_Email1.Text         = sEMail1;
		Div0.edt_Email2.Text         = sEMail2;
		Div3.edt_MstMblPhone.Text    = sMblPhon;

		pUserGrp = sUserGrp;
		if (sUserGrp == "D14" || sUserGrp == "D36") {
			Div1.cmb_UserGrp.Value   = sUserGrp;
			Div1.cmb_UserGrp.Enable  = false;
			Div0.edt_UkeyId.Text     = "";
		}

		Div4.edt_PortalUserID.Enable = false;
		Div0.edt_Pwd.Enable          = false;
		Div0.edt_PwdCon.Enable       = false;
		Div0.edt_UserNm.Enable       = false;
		Div0.edt_Email1.Enable       = false;
		Div0.edt_Email2.Enable       = false;
		Div3.edt_MstMblPhone.Enable  = false;

		if (sUserGrp == "D14" || sUserGrp == "D36" || sUserGrp == "999") {

			ds_input.Clear();
			cf_setParam(ds_input, "USER_GRP",   sUserGrp);
			cf_setParam(ds_input, "ORG_ID",     sOrgID);
			cf_setParam(ds_input, "REL_ORG_ID", sRelOrgID);

			var sSvcID 	= "getDealInfo";
			var sInDs 	= "nc_input_fieldDs=ds_input";
			var sOutDs 	= "ds_DealInfo=nc_output_fieldDs";
			var sArg 	= "sktst.bas.USM#getDealInfo";

			// 공통 트랜잭션 호출
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
		}
	}


}

/*******************************************************************************
 * @description    : 글로벌 섹션 정보
*******************************************************************************/
function lf_getSession()
{
	FV_USERGRP   = gds_session.GetColumn(0, "usergrp");
	FV_UKEY_ID   = gds_session.GetColumn(0, "uKeyId");
}

/*******************************************************************************
 * @description    : 사용자 상세조회 - 이벤트
*******************************************************************************/
function f_getUserInfo()
{

    cf_setParam(ds_input, "user_id", pInUserId);

	var sSvcID 	= "getUserInfo";
	var sInDs 	= "nc_input_fieldDs=ds_input";
	var sOutDs 	= "ds_user=nc_output_fieldDs";
	var sArg 	= "sktst.bas.USM#getUserInfo";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * @description    : Vaild Chack
*******************************************************************************/
function lf_ReqField(sField)
{
	var arr_Data = split2(sField, "#", "|");
	var nCnt = arr_Data.length();

	for(var i=0; i < nCnt; i++)
	{
		var arr_Control = arr_Data[i];
		var sControl = arr_Control[0];
		var sMsg = arr_Control[1];
		var oControl = object(sControl);
		var sDataSet_ID = oControl.BindDataset;

		if(sDataSet_ID == "")
		{	// 데이터셋 바인딩이 되어 있지 않을 경우
			if(oControl.Enable == 0) continue;
		}
		else
		{
			var oDataSet = object(sDataSet_ID);
			if(oDataSet == null)
			{
				alert("데이터셋을 찾을수 없습니다. : " + sDataSet_ID);
				return false;
			}

			var nRows = oDataSet.getRowCount();
			if(nRows == 0) continue;
		}

		if(oControl.Text == "")
		{
			alert(sMsg );
			oControl.setFocus();
			return false;
		}
	}
	return true;
}


/*******************************************************************************
 * @description    : Vaild Chack
*******************************************************************************/
function lf_ValidSave()
{
	var sPwd = ds_user.GetColumn(0, "pwd");
	var sValidField = ""
					//+ "Div0.edt_UserCd|사번을 입력하세요."
					+ "Div0.edt_UserId|사용자ID를 입력하십시오"
					//+ "#Div0.edt_UkeyId|UKeyID를 입력하세요."
					+ "#Div0.edt_UserNm|이름을 입력하십시오."
					+ "#Div0.edt_Pwd|비밀번호를 입력하십시오."
					+ "#Div0.edt_PwdCon|비밀번호확인을 입력하십시오."
					+ "#Div1.cmb_UserGrp|사용자그룹을 선택하십시오."
					+ "#Div1.cmb_userDuty|직책을 선택하십시오.";
					//+ "#Div1.edt_orgNm|소속을 입력하십시오.";

	if(lf_ReqField(sValidField) == false) return false;

	// div validation 체크.
	if(!cf_checkObjValues(Div1)) return false;

	if(Div0.edt_Pwd.Text != Div0.edt_PwdCon.Text)
	{
		alert(MSG_01014); //비밀번호와 비밀번호확인이 일치하지않습니다.";
		return false;
	}

	if(Div0.edt_UserCd.Text == "" )
	{
		alert(MSG_01015);//사번을 입력하십시오
		Div0.edt_UserCd.SetFocus();
		return false;
	}

	if(sPwd != Div0.edt_Pwd.Text)
	{
		ds_user.SetColumn(0, "pwd", Div0.edt_Pwd.Text);
	}
	if(ds_user.GetUpdate() == false)
	{
		alert(MSG_00133); // "변경사항이 없습니다.";
		return false;
	}
	if(Div1.edt_orgId.Text == "")
	{
		alert(MSG_01016); //소속정보를 입력해주십시오
		Div1.edt_orgId.Text = "";
		Div1.edt_orgId.SetFocus();
		return false;
	}
	if(Div1.edt_orgNm.Text == "")
	{
		alert(MSG_01017); //소속정보를 완전히 입력해주십시오.
		Div1.edt_orgId.Text = "";
		Div1.edt_orgId.SetFocus();
		return false;
	}
	if(Div0.edt_Email1.Text == "")
	{
		alert(MSG_01018); //E-mail을 입력해주십시오.
		Div0.edt_Email1.SetFocus();
		return false;
	}
	if(Div0.edt_Email2.Text == "")
	{
		alert(MSG_01018); //E-mail을 입력해주십시오.
		Div0.edt_Email2.SetFocus();
		return false;
	}
	if(Div0.edt_Email1.Text != "" && Div0.edt_Email2.Text != "")
	{
		if(!uf_checkEmailAddr(Div0.edt_Email1.Text+"@"+Div0.edt_Email2.Text))
		{
			return false;
		}
	}
	if(Div1.edt_OrgAreaId.Text != "" && Div1.edt_OrgAreaNm.Text == "")
	{
		alert(MSG_01019); //근무지정보를 완전히 입력해주십시오.";
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaId.SetFocus();
		return false;
	}

	if(Div1.cmb_userDuty.Value == "1" ){

		if(Div1.edt_deal_co_cl1.value == "A3"){
			alert(MSG_01020); //근무지가 판매점인 경우 직책을 관리로 선택할 수 없습니다.
			return false;
		}
	}

	if( uf_IsNull(Div1.edt_posAgencyId.value)){

		if(!Confirm("소속대리점이 입력 되지 않았습니다. 저장 하시겠습니까?")){
			div1.edt_posAgencyNm.SetFocus();
			return false;
		}
	}

	if( uf_IsNull(Div1.edt_OrgAreaNm.value)){

		if(!Confirm("근무지가 입력 되지 않았습니다. 저장 하시겠습니까?")){
			div1.edt_OrgAreaId.SetFocus();
			return false;
		}
	}	


	return true;
}



