/*******************************************************************************
 * @description    : 공통 조회 함수
*******************************************************************************/
function df_search()
{
	cf_setParam(ds_input, "user_id", Div0.edt_UserId.Text);
	// 트랜잭션 설정
	// 파라메터 성 데이타의 경우 약속된 nc_input_fieldDs 를 통해서만 전달
	// 받는 데이타 셋 역시 약속된 nc_output_fieldDs 를 통해서만 받음
	var sSvcID = "svcUserInfo";
	var sInDs = "nc_input_fieldDs=ds_input";
	var sOutDs = "ds_user=nc_output_fieldDs";
	var sArg = "sktst.bas.USM#getUserInfo";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * @description    : 공통 수정 함수
*******************************************************************************/
function df_update()
{
	if(lf_ValidSave() == false) return;

	// 트랜잭션 설정
	var sSvcID 	= "updateUser";
	var sInDs 	= "nc_input_fieldDs=ds_user";
	var sOutDs 	= "";
	var sArg 	= "sktst.bas.USM#updateUser";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * @description    : 공통 저장 함수
*******************************************************************************/
function df_save()
{
	if(lf_ValidSave() == false) return;

	var sSvcID 	= "addUser";
	var sInDs 	= "nc_input_fieldDs=ds_user";
	var sOutDs 	= "";
	var sArg 	= "sktst.bas.USM#addUser";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * @description    : 저장
*******************************************************************************/
function btn_Save_OnClick(obj)
{

	// 전화번호 Merge
	f_Merge_PhoneNum();

	// 근무지가 2차점에서 3차점으로 변경될 경우 기준/정책/재고 SMS 초기화 KIMYS
	if ( Div1.edt_DealCoCl2.value == "3")
	{
		if(confirm("근무지가 3차점이므로 공통/정책/재고 SMS 수신번호가 초기화 됩니다.\n\n 처리를 진행하시겠습니까?") == false) return;
		ds_user.SetColumn(0, "mbl_phon", "");
		ds_user.SetColumn(0, "mbl_phon2", "");
		ds_user.SetColumn(0, "mbl_phon3", "");
	}

	// 판매점 포탈용 항목 Setting
	ds_user.SetColumn(0, "PTL_ORG_ID",        pPTL_OrgID);
	ds_user.SetColumn(0, "PTL_REL_ORG_ID",    pPTL_RelOrgID);
	ds_user.SetColumn(0, "PTL_AUDIT_USER_ID", FV_UKEY_ID);
	ds_user.SetColumn(0, "PTL_AUDIT_DTM",     getDate());
	ds_user.SetColumn(0, "PTL_TRANS_DTM",     getDate());

    if (Div0.chk_EffUserYn.Value == "Y") {
		if (uf_IsNull(pEff_Sta_dt)) {
			ds_user.SetColumn(0, "PTL_EFF_STA_DT",    ToDay());
			ds_user.SetColumn(0, "PTL_EFF_END_DT",   "20301231");
		} else {
			ds_user.SetColumn(0, "PTL_EFF_STA_DT",    pEff_Sta_dt);
			ds_user.SetColumn(0, "PTL_EFF_END_DT",    pEff_End_dt);
		}
    } else {
		if (uf_IsNull(pEff_Sta_dt)) {
			ds_user.SetColumn(0, "PTL_EFF_STA_DT",    ToDay());
			ds_user.SetColumn(0, "PTL_EFF_END_DT",    ToDay());
		} else {
			ds_user.SetColumn(0, "PTL_EFF_STA_DT",    pEff_Sta_dt);
			ds_user.SetColumn(0, "PTL_EFF_END_DT",    ToDay());
		}
    }

	// 상세보기 및 비밀번호 수정
	if(pInStatus == "M" && pInUserId != "")
	{
		df_update();
	}
	// 신규 사용자 추가
	if(pInStatus == "N" || pInStatus == "P")
	{
		df_save();
	}
}

/*******************************************************************************
 * @description    : 3개로 나누어져 있는 전화번호를 하나로 Merge
*******************************************************************************/
function f_Merge_PhoneNum()
{
	// 대표 유선전화번호
	var sPhone = trim(Div3.edt_WPhon1.text)
	           + trim(Div3.edt_WPhon2.text)
	           + trim(Div3.edt_WPhon3.text);
	ds_user.SetColumn(0, "WPHON", sPhone);

	// 공통/정책/재고 SMS 전화
	    sPhone = trim(Div3.edt_MPhon11.text)
	           + trim(Div3.edt_MPhon12.text)
	           + trim(Div3.edt_MPhon13.text);
	ds_user.SetColumn(0, "MBL_PHON", sPhone);
	    sPhone = trim(Div3.edt_MPhon21.text)
	           + trim(Div3.edt_MPhon22.text)
	           + trim(Div3.edt_MPhon23.text);
	ds_user.SetColumn(0, "MBL_PHON2", sPhone);
	    sPhone = trim(Div3.edt_MPhon31.text)
	           + trim(Div3.edt_MPhon32.text)
	           + trim(Div3.edt_MPhon33.text);
	ds_user.SetColumn(0, "MBL_PHON3", sPhone);

	// 영업/Happy SMS 전화
	    sPhone = trim(Div3.edt_HPhone11.text)
	           + trim(Div3.edt_HPhone12.text)
	           + trim(Div3.edt_HPhone13.text);
	ds_user.SetColumn(0, "HAPPY_SMS1", sPhone);
	    sPhone = trim(Div3.edt_HPhone21.text)
	           + trim(Div3.edt_HPhone22.text)
	           + trim(Div3.edt_HPhone23.text);
	ds_user.SetColumn(0, "HAPPY_SMS2", sPhone);
	    sPhone = trim(Div3.edt_HPhone31.text)
	           + trim(Div3.edt_HPhone32.text)
	           + trim(Div3.edt_HPhone33.text);
	ds_user.SetColumn(0, "HAPPY_SMS3", sPhone);

	// 요금/정산 SMS 전화
	    sPhone = trim(Div3.edt_APhon11.text)
	           + trim(Div3.edt_APhon12.text)
	           + trim(Div3.edt_APhon13.text);
	ds_user.SetColumn(0, "ACC_SMS1", sPhone);
	    sPhone = trim(Div3.edt_APhon21.text)
	           + trim(Div3.edt_APhon22.text)
	           + trim(Div3.edt_APhon23.text);
	ds_user.SetColumn(0, "ACC_SMS2", sPhone);
}


/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	// 사용자 상세정보 가져온 후
	if(sSvcID == "getUserInfo")
	{
		Div0.edt_pwdCon.Value = "";
		pInStatus = "M";
		Div0.Static4.Visible = true;
		Div0.edt_pwdExpDt.Visible = true;
		Div0.edt_pwdExpDt.Enable = false;

		// 화면 제어
		f_editAllowed();

		// 사용자그룹 목록 가져오기.
		f_getUserGrpList();

		// 사용자직책 가져오기
		f_getUserDutyList();

		//Div1_cmb_UserGrp_OnChanged();
		//Div0.edt_pwdCon.SetFocus();
		Div0.edt_pwdCon.text = ds_user.getColumn(0, "PWD");

		if (Div1.cmb_UserDuty.Value == "9")
		{
			Div0.edt_UserCd.Enable = false;
		}

		f_setSaleStore();	

		f_setMoblie();

		// 판매점관리에 있는 사용자 조회로 화면을 조회 시
		if(FV_USER_INFO_WINDOW_YN == "Y"){

// KIMYS			btn_pwdReset.Visible = false;
			btn_save.Visible = false;
			btn_close.Visible = false;
			Div1.cmb_UserGrp.Enable = false;
			Div1.cmb_userDuty.Enable = false;
			Div1.edt_orgId.Enable = false;
			Div1.btn_searchOrg.Enable = false;
			Div1.edt_orgNm.Enable = false;
			Div1.edt_posAgencyNm.Enable = false;
			Div1.btn_searchPosAgency.Enable = false;
			Div1.edt_posAgencyId.Enable = false;
			Div1.cmb_attcCat.Enable = false;
			Div1.edt_OrgAreaId.Enable = false;
			Div1.btn_searchOrgArea.Enable = false;
			Div1.edt_OrgAreaNm.Enable = false;
			Div1.chk_posAgencyEqual.Enable = false;
			Div0.edt_UkeyId.Enable   = false;
			Div0.btn_ukeyPop.Enable  = false;
			Div0.edt_Email1.Enable   = false;
			Div0.edt_Email2.Enable   = false;
			Div0.edt_UserNm.Enable   = false;
			Div3.edt_Wphon1.Enable   = false;
			Div3.edt_Wphon2.Enable   = false;
			Div3.edt_Wphon3.Enable   = false;
			Div3.edt_MPhon11.Enable  = false;
			Div3.edt_MPhon12.Enable  = false;
			Div3.edt_MPhon13.Enable  = false;
			Div3.edt_MPhon21.Enable  = false;
			Div3.edt_MPhon22.Enable  = false;
			Div3.edt_MPhon23.Enable  = false;
			Div3.edt_MPhon31.Enable  = false;
			Div3.edt_MPhon32.Enable  = false;
			Div3.edt_MPhon33.Enable  = false;
			Div3.edt_HPhone11.Enable = false;
			Div3.edt_HPhone12.Enable = false;
			Div3.edt_HPhone13.Enable = false;
			Div3.edt_HPhone21.Enable = false;
			Div3.edt_HPhone22.Enable = false;
			Div3.edt_HPhone23.Enable = false;
			Div3.edt_HPhone31.Enable = false;
			Div3.edt_HPhone32.Enable = false;
			Div3.edt_HPhone33.Enable = false;
			Div3.edt_APhon11.Enable  = false;
			Div3.edt_APhon12.Enable  = false;
			Div3.edt_APhon13.Enable  = false;
			Div3.edt_APhon21.Enable  = false;
			Div3.edt_APhon22.Enable  = false;
			Div3.edt_APhon23.Enable  = false;
			Div2.edt_Rmks.Enable     = false;		
		}

		// 원본 데이터셋 생성
		cf_setOrgDs(ds_user);

		// 전화번호를 3-4-4 / 3-3-4로 나누어 보여주기
		f_Split_PhoneNum();


		//(대리점)Admin 사용자그룹은 (본사)Admin 권한자만 수정/변경 가능	KIMYS - 2010.03.23
		if (Div1.cmb_UserGrp.Value == "D22" && !(FV_USERGRP == "P12" || FV_USERGRP == "P17"))
		{
			Div1.cmb_UserGrp.Enable     = false;
		}   else {
			Div1.cmb_UserGrp.Enable     = true;
		}

		// 근부지가 2/3차점 변경될 때를 대비해 전화번호 및 거래처구분 2 저장
		FV_DEAL_CL2   = ds_user.GetColumn(0, "deal_co_cl2");				// 사용자의 원 근무지의 2/3차점 구분
		FV_MBL_PHONE1 = ds_user.GetColumn(0, "mbl_phon");					// 기준/정책/재고 SMS1
		FV_MBL_PHONE2 = ds_user.GetColumn(0, "mbl_phon2");					// 기준/정책/재고 SMS2
		FV_MBL_PHONE3 = ds_user.GetColumn(0, "mbl_phon3");					// 기준/정책/재고 SMS3

		// 근부지가 3차점인 경우 공통/정책/재고 비활성화, 전화번호 Initialize...
		if (FV_DEAL_CL2 == "3")
		{
			Div3.edt_MPhon11.Enable = false;
			Div3.edt_MPhon12.Enable = false;
			Div3.edt_MPhon13.Enable = false;
			Div3.edt_MPhon21.Enable = false;
			Div3.edt_MPhon22.Enable = false;
			Div3.edt_MPhon23.Enable = false;
			Div3.edt_MPhon31.Enable = false;
			Div3.edt_MPhon32.Enable = false;
			Div3.edt_MPhon33.Enable = false;
		} else {
			Div3.edt_MPhon11.Enable = true;
			Div3.edt_MPhon12.Enable = true;
			Div3.edt_MPhon13.Enable = true;
			Div3.edt_MPhon21.Enable = true;
			Div3.edt_MPhon22.Enable = true;
			Div3.edt_MPhon23.Enable = true;
			Div3.edt_MPhon31.Enable = true;
			Div3.edt_MPhon32.Enable = true;
			Div3.edt_MPhon33.Enable = true;
		}
	}
	// 사용자그룹 검색후
	if(sSvcID == "getUserGrpList")
	{

		//  유저권한이 대리점 인 경우 대리점으로 유저그룹 제한.
		var sUserGrp = gds_session.GetColumn(0,"userGrp");

		if(SubStr(sUserGrp,0,1) == "D"){

			ds_user_grp.Filter("substr(comm_cd_val,0,1)=='D'");
		}

		// 사용자 신규 생성
		if(pInStatus == "N")
		{
			//Div0.edt_UserCd.SetFocus();
		}
	}
	//  신규 사용자 추가후
	if(sSvcID == "addUser")
	{
		if(IndexOf(msg_message,"사번")>-1)
		{
			alert(msg_message);
			Div0.edt_UserCd.Text = "";
			//Div0.edt_UserCd.SetFocus();
		}
		else if(IndexOf(msg_message,"사용자ID")>-1)
		{
			alert(msg_message);
			Div0.edt_UserId.Text = "";
			Div0.edt_UserId.SetFocus();
		}
		else if(IndexOf(msg_message,"U.Key ID")>-1)
		{
			alert(msg_message);
			Div0.edt_UkeyId.Text = "";
			Div0.edt_UkeyId.SetFocus();
		}
		else
		{
			cf_setOrgDs(ds_user);
			//alert(cf_getMessage(MSG_00006, "사용자 정보 저장이"));

			parent.ds_condition.SetColumn(0,"USER_ID",Div0.edt_UserId.Value);
			parent.btn_search_OnClick();

			this.Close();
		}
	}
	// 사용자 수정후
	if(sSvcID == "updateUser")
	{
		cf_setOrgDs(ds_user);
		//alert(cf_getMessage(MSG_00006, "사용자 정보 수정이"));
		//var sArg  = "user_id="+Div0.edt_UserId.Text;
		var sArg = "";
		gf_openWindow("100000004", sArg);
		this.Close();

	}

	if (sSvcID == "getDealInfo") {

		// 조직
		Div1.edt_orgNm.Text       = ds_DealInfo.GetColumn(0, "ORG_NM");
		Div1.edt_orgId.Text       = ds_DealInfo.GetColumn(0, "ORG_ID");
		Div1.edt_orgCl.Text       = ds_DealInfo.GetColumn(0, "ORG_CL");		

		// 소속대리점
		Div1.edt_posAgencyNm.Text = ds_DealInfo.GetColumn(0, "AGENCY_NM");
		Div1.edt_posAgencyId.Text = ds_DealInfo.GetColumn(0, "AGENCY_CD");

		// 소속근무지
		Div1.edt_OrgAreaNm.Text   = ds_DealInfo.GetColumn(0, "AREA_NM");
		Div1.edt_OrgAreaId.Text   = ds_DealInfo.GetColumn(0, "AREA_CD");

		if (uf_IsNull(Div1.edt_orgId.Text)) {

			Div1.edt_orgNm.Enable     = true;
			Div1.edt_orgId.Enable     = true;
			Div1.btn_searchOrg.Enable = true;

		} else {

			Div1.edt_orgNm.Enable     = false;
			Div1.edt_orgId.Enable     = false;
			Div1.btn_searchOrg.Enable = false;
		}

		if ((pUserGrp == "D14" || pUserGrp == "D36") && !uf_IsNull(Div1.edt_OrgAreaNm.Text)) {
			Div1.btn_searchPosAgency.Enable = false;
			Div1.btn_searchOrgArea.Enable   = false;

			Div1.edt_posAgencyNm.Enable     = false;
			Div1.edt_posAgencyId.Enable     = false;
			Div1.edt_OrgAreaNm.Enable       = false;
			Div1.edt_OrgAreaId.Enable       = false;
		}
	}
}

