/*******************************************************************************
 * @description    : 전화번호를 3-4-4 / 3-3-4로 나누어 보여주기
*******************************************************************************/
function f_Split_PhoneNum()
{
	// 대표유선번호
	var sPhone = ds_user.GetColumn(0, "WPHON");
	f_Split_phone(sPhone, Div3.edt_WPhon1, Div3.edt_WPhon2, Div3.edt_WPhon3);

	// 공통/정책/재고 SMS 전화번호 1
	    sPhone = ds_user.GetColumn(0, "MBL_PHON");
	f_Split_phone(sPhone, Div3.edt_MPhon11, Div3.edt_MPhon12, Div3.edt_MPhon13);

	// 공통/정책/재고 SMS 전화번호 2
	    sPhone = ds_user.GetColumn(0, "MBL_PHON2");
	f_Split_phone(sPhone, Div3.edt_MPhon21, Div3.edt_MPhon22, Div3.edt_MPhon23);

	// 공통/정책/재고 SMS 전화번호 3
	    sPhone = ds_user.GetColumn(0, "MBL_PHON3");
	f_Split_phone(sPhone, Div3.edt_MPhon31, Div3.edt_MPhon32, Div3.edt_MPhon33);

	// 영업/Happy SMS 전화번호 1
	    sPhone = ds_user.GetColumn(0, "HAPPY_SMS1");
	f_Split_phone(sPhone, Div3.edt_HPhone11, Div3.edt_HPhone12, Div3.edt_HPhone13);

	// 영업/Happy SMS 전화번호 2
	    sPhone = ds_user.GetColumn(0, "HAPPY_SMS2");
	f_Split_phone(sPhone, Div3.edt_HPhone21, Div3.edt_HPhone22, Div3.edt_HPhone23);

	// 영업/Happy SMS 전화번호 3
	    sPhone = ds_user.GetColumn(0, "HAPPY_SMS3");
	f_Split_phone(sPhone, Div3.edt_HPhone31, Div3.edt_HPhone32, Div3.edt_HPhone33);

	// 요금/정산 SMS 전화번호 1
	    sPhone = ds_user.GetColumn(0, "ACC_SMS1");
	f_Split_phone(sPhone, Div3.edt_APhon11, Div3.edt_APhon12, Div3.edt_APhon13);

	// 요금/정산 SMS 전화번호 2
	    sPhone = ds_user.GetColumn(0, "ACC_SMS2");
	f_Split_phone(sPhone, Div3.edt_APhon21, Div3.edt_APhon22, Div3.edt_APhon23);
}

function f_Split_Phone(sPhoneNum, Obj_Phon1, Obj_Phon2, Obj_Phon3)
{
	// 전달된 전화번호가 Null인 경우
	if (length(sPhoneNum) == 0) {
		Obj_Phon1.text = "";
		Obj_Phon2.text = "";
		Obj_Phon3.text = "";
		return;
	}


	// 전달된 전화번호가 Null인 경우
	if (length(sPhoneNum) == 11) {
		Obj_Phon1.text = mid(sPhoneNum, 0, 3);
		Obj_Phon2.text = mid(sPhoneNum, 3, 4);
		Obj_Phon3.text = mid(sPhoneNum, 7, 4);

		return;
	} else {
		Obj_Phon1.text = mid(sPhoneNum, 0, 3);
		Obj_Phon2.text = mid(sPhoneNum, 3, 3);
		Obj_Phon3.text = mid(sPhoneNum, 6, 4);

		return;
	}
}

/*******************************************************************************
 * @description    : 초기화 - 이벤트
*******************************************************************************/
function btn_Clear_OnClick(obj)
{

	if(pInStatus == "M")
	{
		f_getUserInfo();
	}
	if(pInStatus == "N" || pInStatus == "P")
	{
		form_OnLoadCompleted(obj);
	}
}

/*******************************************************************************
 * @description    :  공통 폼 재호출 함수
 * @param sArg     : 파라메터
*******************************************************************************/
function df_reSet(sArg)
{
	//alert(sArg);
	// 넘어온 파라메터를 데이타셋으로 세팅
	cf_setArg("ds_BASUSM00200", sArg);
	pPortalId = ds_BASUSM00200.getColumn(0, "sPortalId");
	pInUserId = ds_BASUSM00200.getColumn(0, "sInUserId");
	pInStatus = ds_BASUSM00200.getColumn(0, "sInStatus");
	pInModFlag = ds_BASUSM00200.getColumn(0, "sInModFlag");

	// 상세보기
	if(pInStatus == "M" && pInUserId != "")
	{
		f_getUserInfo();
	}
	if(pInStatus == null) pInStatus = "N";

	// 사용자 신규 생성
	if(pInStatus == "N")
	{
		ds_user.ClearData();
		ds_user.AddRow();

		Div0.chk_EffUserYn.Value = "Y";
		f_editAllowed();
		Div0.Static4.Visible = false;
		Div0.edt_pwdExpDt.Visible = false;
		// 사용자그룹 가져오기
		f_getUserGrpList();
		// 사용자직책 가져오기
		f_getUserDutyList();
	}
	if(pInStatus == "P")
	{
		ds_user.ClearData();
		ds_user.AddRow();

		Div0.chk_EffUserYn.Value = "Y";
		f_editAllowed();
		Div0.Static4.Visible = false;
		Div0.edt_pwdExpDt.Visible = false;
		// 사용자그룹 가져오기
		f_getUserGrpList();
		// 사용자직책 가져오기
		f_getUserDutyList();
	}
}

/******************************************************************************
 * 사용자그룹 OnChanced Event
 *****************************************************************************/
function Div1_cmb_UserGrp_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	if(strCode == "D14" || strCode == "D36"){
	    // 판매점/MM 인 경우 
		div1.cmb_userDuty.value = "9"; // 외부
		div1.cmb_attcCat.value  = "9";// 비직원
	}

	f_setSaleStore();
	f_setMoblie();
}

/******************************************************************************
 * 판매점인 경우 셋팅.
 *****************************************************************************/
function f_setSaleStore(){
	if(div1.cmb_UserGrp.Value == "D14" || div1.cmb_UserGrp.Value == "D36"){
	    // 판매점/MM 인 경우 
		div1.cmb_userDuty.Enable = false; // 외부
		div1.cmb_attcCat.Enable = false;// 비직원		
	}else{
		div1.cmb_userDuty.Enable = true; // 외부
		div1.cmb_attcCat.Enable = true;// 비직원			
	}
}

