function Div1_edt_orgId_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13)
	{
/*
		ds_ParentORg.ClearData();
		ds_ParentORg.AddRow();
		cf_commPopOrgCdDs("ds_ParentORg", 0);
		if (ds_ParentORg.rowcount >0) {
			Div1.edt_orgNm.Text = ds_ParentORg.GetColumn(0, "ORG_NM");
