		Div1.edt_posAgencyNm.Text = "";
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
	}
}

/******************************************************************************
 * 소속대리점 팝업
 *****************************************************************************/
function Div1_btn_searchPosAgency_OnClick(obj)
{
	if(Div1.edt_orgId.Text == "" || Div1.edt_orgNm.Text == "")
	{
		alert(MSG_01022); //소속조직을 먼저 입력 또는 선택해 주십시오.
		Div1.edt_posAgencyId.Text = "";
		Div1.edt_posAgencyNm.Text = "";
		Div1.edt_orgId.SetFocus();
		return;
	}

	/*
	if(Div1.edt_orgCl.Text != "3")
	{
		//소속조직이 영업센터인 경우만 대리점과 근무지를 입력할 수 있습니다.
		alert(MSG_01023);
		Div1.edt_posAgencyId.Text = "";
		Div1.edt_posAgencyNm.Text = "";
		return;
	}
	*/

	var sArg  = "org_id="+quote(Div1.edt_orgId.Text); // 필수
	cf_callAgencyPop(sArg, Div1, "edt_posAgencyNm,edt_posAgencyId");	

	Div1.edt_OrgAreaId.Text = "";
	Div1.edt_OrgAreaNm.Text = "";
	Div1.chk_posAgencyEqual.Value = "N";
}

/******************************************************************************
 * 소속대리점 OnKeyDown Event
 *****************************************************************************/
function Div1_edt_posAgencyNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	// Enter 키 이벤트
	if (nChar==13)
	{
		Div1_btn_searchPosAgency_OnClick(obj);
	}
	// Delete 키, Backspace 키 이벤트
	if (nChar==46 || nChar==8)
	{
		Div1.edt_posAgencyId.Text = "";
		Div1.edt_posAgencyNm.Text = "";
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
	}
}

