 * 근무지 팝업
 *****************************************************************************/
function Div1_edt_orgAreaId_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	// Enter 키 이벤트
	if (nChar==13)
	{
		btn_searchOrgArea_OnClick(obj);
	}
	// Delete 키, Backspace 키 이벤트
	if (nChar==46 || nChar==8)
	{
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
	}
}

/*******************************************************************************
 * @description    : 근무조직팝업
*******************************************************************************/
function btn_searchOrgArea_OnClick(obj)
{
	if(Div1.edt_orgId.Text == "" || Div1.edt_orgNm.Text == "")
	{
		alert(MSG_01022); //소속조직을 먼저 입력 또는 선택해 주십시오.
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
		Div1.edt_orgId.SetFocus();
		return;
	}
	/*
	if(Div1.edt_orgCl.Text != "3")
	{
		//소속조직이 영업센터인 경우만 대리점과 근무지를 입력할 수 있습니다.
		alert(MSG_01023);
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
		return;
	}
	*/
	if(Div1.edt_posAgencyId.Text == "" || Div1.edt_posAgencyNm.Text == "")
	{
		alert(MSG_01025); //소속대리점을 먼저 입력해 주십시오.":
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
		Div1.edt_posAgencyId.SetFocus();
		return;
	}

	if(Div1.chk_posAgencyEqual.Value == "Y")
	{
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
		Div1.chk_posAgencyEqual.Value = "N";
	}

	http.Sync = true;

	var sArg  = "deal_co_cl1="+quote("A1,A2,A3,B1,B2,C1"); // 필수 - 대리점
	//sArg += " eff_dtm="+quote(today()); // 옵션
	sArg += " org_id="+quote(Div1.edt_orgId.Text); // 옵션 - 소속조직
	sArg += " pos_agency="+quote(Div1.edt_posAgencyId.Text); // 옵션 - 소속대리점
	cf_callDealPop(sArg, Div1, "edt_OrgAreaNm,edt_OrgAreaId");

	// 팝업에서 거래처구분을 받아 글로벌 변수에 담는다.

	var arrayP = Split(FV_STR_POP_RESULT,"^","webstyle");
	var pPCodeCd = arrayP[4];
	Div1.edt_deal_co_cl1.value   = arrayP[5];
	Div1.edt_DealCoCl2.value     = arrayP[6];

	if (Div1.edt_DealCoCl2.value == "2") {
		Div1.edt_DealCoCl2NM.Text = "2차점";
	}
	if (Div1.edt_DealCoCl2.value == "3") {
		Div1.edt_DealCoCl2NM.Text = "3차점";
	}

	// 근무지가 3차점인 경우 기준/정책/재고 SMS 입력불가
	if (Div1.edt_DealCoCl2.value == "3")
	{
		Div3.edt_MPhon11.Enable = false;
		Div3.edt_MPhon12.Enable = false;
		Div3.edt_MPhon13.Enable = false;
		Div3.edt_MPhon21.Enable = false;
		Div3.edt_MPhon22.Enable = false;
		Div3.edt_MPhon23.Enable = false;
		Div3.edt_MPhon31.Enable = false;
		Div3.edt_MPhon32.Enable = false;
		Div3.edt_MPhon33.Enable = false;
	} else {
		Div3.edt_MPhon11.Enable = true;
		Div3.edt_MPhon12.Enable = true;
		Div3.edt_MPhon13.Enable = true;
		Div3.edt_MPhon21.Enable = true;
		Div3.edt_MPhon22.Enable = true;
		Div3.edt_MPhon23.Enable = true;
		Div3.edt_MPhon31.Enable = true;
		Div3.edt_MPhon32.Enable = true;
		Div3.edt_MPhon33.Enable = true;
	}

	if (!uf_IsNull(pChannelCD) &&  pChannelCD != pPCodeCd) {		
		btn_save.Enable = false;
		alert("근무지의 채널코드[" + pPCodeCd + "]가 포탈 SSO의 조직정보 [" + pChannelCD + "] 와 다릅니다.");
	} else {
		btn_save.Enable = true;
	}

	http.Sync = false;

}

/*******************************************************************************
 * @description    : 소속대리점과 근무지가 같은지 여부
*******************************************************************************/
function Div1_chk_posAgencyEqual_OnClick(obj,strValue)
{
	if(Div1.chk_posAgencyEqual.Value == "Y")
	{
		if(Div1.edt_posAgencyId.Text == "" || Div1.edt_posAgencyNm.Text == "")
		{
			alert(MSG_01025); //소속대리점을 먼저 입력해 주십시오.":
			Div1.chk_posAgencyEqual.Value = "N";
			Div1.edt_OrgAreaId.Text = "";
			Div1.edt_OrgAreaNm.Text = "";
			Div1.edt_posAgencyId.SetFocus();
			return;
		}
		Div1.edt_OrgAreaId.Text = Div1.edt_posAgencyId.Text;
		Div1.edt_OrgAreaNm.Text = Div1.edt_posAgencyNm.Text;
	}
	else
	{
		Div1.edt_OrgAreaId.Text = "";
		Div1.edt_OrgAreaNm.Text = "";
	}
}

