function btn_close_OnClick(obj)
{
	this.Close();
}

/*******************************************************************************
 * @description    : 유키 팝업 - 이벤트
*******************************************************************************/
function Div0_btn_ukeyPop_OnClick(obj)
{
	var sArg   = "posAgency="+quote(ds_user.GetColumn(0, "POS_AGENCY"));
	    sArg  += " userNm="+quote(ds_user.GetColumn(0, "USER_NM"));
	var sResult  = dialog("BAS::BASUSM08100.xml", sArg, 328, 280, "OpenStyle=Max CloseFlag=false");

	if(sResult.length > 0 && sResult != 0)
	{
		var arrResult = sResult.split("^");
		var sId = arrResult[0];
		var sNm = arrResult[1];

		//if(true)login_id
		if(ds_user_org.GetColumn(0, "U_KEY_ID") != sId)
		{
			ds_user.AddColumn("op_dt");
			ds_user.AddColumn("op_tm");
			ds_user.AddColumn("seq");
			var sOpDt = arrResult[2];
			var sOpTm = arrResult[3];
			var sSeq = arrResult[4];
			ds_user.SetColumn(0, "op_dt", sOpDt);
			ds_user.SetColumn(0, "op_tm", sOpTm);
			ds_user.SetColumn(0, "seq", sSeq);
		}
		ds_user.SetColumn(0, "U_KEY_ID", sId);
		ds_user.SetColumn(0, "USER_NM", sNm);
	}
}

/*******************************************************************************
 * @description    : password 초기화 버튼 클릭- 이벤트
*******************************************************************************/
function btn_pwdReset_OnClick(obj)
{
	if(confirm(MSG_00167)){ //비밀번호를 초기화 하시겠습니까?";

		// 트랜잭션 설정
		var sSvcID 	= "pwdReset";
		var sInDs 	= "nc_input_fieldDs=ds_user";
		var sOutDs 	= "";
		var sArg 	= "sktst.bas.USM#updatePwdReset";

		// 공통 트랜잭션 호출
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	
	}

}

/*******************************************************************************
 * @description    : mobile2, moblie3 setting 
*******************************************************************************/
function f_setMoblie(){
	var sUserGrp = Div1.cmb_UserGrp.value;

	// 판매점/MM 인 경우 모든 전화버호 등록 가능
	//              아니면 정책/공통/재고 1만 등록 가능
	if(sUserGrp == "D14" || sUserGrp == "D36"){
		Div3.edt_MPhon21.Enable = true;
		Div3.edt_MPhon22.Enable = true;
		Div3.edt_MPhon23.Enable = true;
		Div3.edt_MPhon31.Enable = true;
		Div3.edt_MPhon32.Enable = true;
		Div3.edt_MPhon33.Enable = true;
		Div3.edt_HPhone11.Enable = true;
		Div3.edt_HPhone12.Enable = true;
		Div3.edt_HPhone13.Enable = true;
		Div3.edt_HPhone21.Enable = true;
		Div3.edt_HPhone22.Enable = true;
		Div3.edt_HPhone23.Enable = true;
		Div3.edt_HPhone31.Enable = true;
		Div3.edt_HPhone32.Enable = true;
		Div3.edt_HPhone33.Enable = true;
		Div3.edt_APhon11.Enable = true;
		Div3.edt_APhon12.Enable = true;
		Div3.edt_APhon13.Enable = true;
		Div3.edt_APhon21.Enable = true;
		Div3.edt_APhon22.Enable = true;
		Div3.edt_APhon23.Enable = true;
	}else{
		Div3.edt_MPhon21.value = "";
		Div3.edt_MPhon22.value = "";
		Div3.edt_MPhon23.value = "";
		Div3.edt_MPhon31.value = "";
		Div3.edt_MPhon32.value = "";
		Div3.edt_MPhon33.value = "";
		Div3.edt_HPhone11.value = "";
		Div3.edt_HPhone12.value = "";
		Div3.edt_HPhone13.value = "";
		Div3.edt_HPhone21.value = "";
		Div3.edt_HPhone22.value = "";
		Div3.edt_HPhone23.value = "";
		Div3.edt_HPhone31.value = "";
		Div3.edt_HPhone32.value = "";
		Div3.edt_HPhone33.value = "";
		Div3.edt_APhon11.value = "";
		Div3.edt_APhon12.value = "";
		Div3.edt_APhon13.value = "";
		Div3.edt_APhon21.value = "";
		Div3.edt_APhon22.value = "";
		Div3.edt_APhon23.value = "";
		Div3.edt_MPhon21.Enable = false;
		Div3.edt_MPhon22.Enable = false;
		Div3.edt_MPhon23.Enable = false;
		Div3.edt_MPhon31.Enable = false;
		Div3.edt_MPhon32.Enable = false;
		Div3.edt_MPhon33.Enable = false;
		Div3.edt_HPhone11.Enable = false;
		Div3.edt_HPhone12.Enable = false;
		Div3.edt_HPhone13.Enable = false;
		Div3.edt_HPhone21.Enable = false;
		Div3.edt_HPhone22.Enable = false;
		Div3.edt_HPhone23.Enable = false;
		Div3.edt_HPhone31.Enable = false;
		Div3.edt_HPhone32.Enable = false;
		Div3.edt_HPhone33.Enable = false;
		Div3.edt_APhon11.Enable = false;
		Div3.edt_APhon12.Enable = false;
		Div3.edt_APhon13.Enable = false;
		Div3.edt_APhon21.Enable = false;
		Div3.edt_APhon22.Enable = false;
		Div3.edt_APhon23.Enable = false;
	}

}
