	// 공통 코드 설정
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_search.cmb_UserGrp", "ZBAS_C_00250", "전체", null, false); // 사용자 그룹

	http.Sync = true;
	cf_checkCommonCode();
	http.Sync = false;
}


/*******************************************************************************
