/*******************************************************************************
 * @description    : 조회조건 변경 - 이벤트
*******************************************************************************/
function cmb_SchCon_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if ( strCode == "D14" ) {

		div_search.cmb_UserGrp.Value = "D14";
		div_search.cmb_UserGrp.Text = "(대리점)판매점/MM";
		div_search.cmb_UserGrp.Enable = false;

	}  else {
		div_search.cmb_UserGrp.Value = null;
		div_search.cmb_UserGrp.Text = "전체";
		div_search.cmb_UserGrp.Enable = true;
	}
}

/*******************************************************************************
 * @description    :  공통 폼 재호출 함수
 * @param sArg     : 파라메터
*******************************************************************************/
function df_reSet(sArg)
{
	//f_reSet();
	cmb_SchCon_OnChanged();
	div_search.edt_UserId.Text = f_getParamValue("user_id", sArg);

	btn_Search_OnClick();
}

/*******************************************************************************
 * @description           : 특정 파라미터 값 취득
 * @param sDsId           : 파라미터 이름
		sArg              : 지정할 파라메터
*******************************************************************************/
function f_getParamValue(paramName, sArg)
{
	var arrParams = sArg.split(" ", "webstyle");
	var returnVal = "";
	for(var i=0, n=arrParams.length; i<n; i++)
	{
		var arrParamInfo= arrParams[i].split("=","webstyle");
		var sParamName = arrParamInfo[0];
		var sparamValue = arrParamInfo[1];
		if(paramName == sParamName)
		{
			returnVal = sparamValue;
		}
	}

	return returnVal;

}

/*******************************************************************************
 * @description    : 조회조건 초기화
*******************************************************************************/
function f_reSet()
{
	div_search.cmb_UserGrp.Index = 0;
	div_search.lbl_UserGrp.Visible = true;
	div_search.cmb_UserGrp.Visible = true;
	div_search.lbl_UserNm.Visible = false;
	div_search.edt_UserNm.Visible = false;
	div_search.edt_UserNm.Text = "";

}

