		cf_exportExcel(grd_list);

		f_ExcelExportHst();
	}
}

/*******************************************************************************
 * @description    : 목록 Excel Export 내역 저장
*******************************************************************************/
function f_ExcelExportHst()
{
	var sCondition  = "조회조건 : "       + div_search.cmb_SchCon.Value    + "(" + div_search.cmb_SchCon.Text      + ")  / ";
	    sCondition += "사용자그룹 : "     + div_search.cmb_UserGrp.Value   + "(" + div_search.cmb_UserGrp.Text     + ")  / ";
	    sCondition += "성명 : "           + div_search.edt_UserNm.Text     + "  / ";
	    sCondition += "조직 : "           + div_search.edt_orgId.Text      + "(" + div_search.edt_orgNm.Text       + ")  / ";
	    sCondition += "근무지 : "         + div_search.edt_orgAreaId.Text  + "(" + div_search.edt_orgAreaNm.Text   + ")  / ";
	    sCondition += "사용자 ID : "      + div_search.edt_UserId.Text     + "  / ";
	    sCondition += "U.Key ID : "       + div_search.edt_UkeyId.Text     + "  / ";
	    sCondition += "유효사용자여부 : " + div_search.cmb_effUserYn.Value + "(" + div_search.cmb_effUserYn.Text   + ")";

	cf_setParam(ds_condition, "EXPORT_DTM",     GetDate());
	cf_setParam(ds_condition, "EXPORT_SCREEN",  pFrmNum);
	cf_setParam(ds_condition, "EXPORT_COND",    sCondition);
	cf_setParam(ds_condition, "EXPORT_USER",    gds_session.GetColumn(0, "loginId"));
	cf_setParam(ds_condition, "USER_LOGIN_IP",  gds_session.GetColumn(0, "ip"));
	cf_setParam(ds_condition, "USER_ORG_CD",    gds_session.GetColumn(0, "orgId"));
	cf_setParam(ds_condition, "USER_AGENCY_CD", gds_session.GetColumn(0, "posAgencyId"));
	cf_setParam(ds_condition, "USER_USER_GRP",  gds_session.GetColumn(0, "userGrp"));
	cf_setParam(ds_condition, "EXPORT_CNT",     ds_output_1.rowcount);

	// 트랜잭션 설정
	var sSvcID 	= "addExcelExportHst";
	var sInDs 	= "nc_input_fieldDs=ds_condition";
	var sOutDs 	= "";
	var sArg 	= "sktst.bas.USM#addExcelExportHst";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);

}

