function btn_New_OnClick(obj)
{

	var jobFlag = false;

	for(var i=0; i < ds_output_1.rowcount; i++)
	{
		if (ds_output_1.GetColumn(i, "CHK") == "1")
		{
			jobFlag = true;
			ds_output_1.SetColumn(i, "AUDIT_USER_ID", FV_UKEY_ID);
		}
	}

	if (jobFlag == false) {
		alert("하나 이상의 사용자ID를 선택해 주시기 바랍니다.");
		return;
	}

    var sSvcID = "svcSaveMapping";
	var sInDs  = "List=ds_output_1";
	var sOutDs = "";
	var sArg   = "sktst.bas.USM#updatePortalUserMapping";	

	//alert(ds_output_1.SaveXML());
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}
