/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function CSTADV00500_OnLoadCompleted(obj)
{

	http.Sync = true;

	lf_getSession();
	lf_init();
	lf_setCombo();
	FV_PARENT_NUM = vFrmNum;

	if( !uf_IsNull(FV_AGENCY_ID)){
		div_conInfo1.edt_agencyNm.value = FV_AGENCY_NM;
	    div_conInfo1.edt_posAgency.value = FV_AGENCY_ID;
		div_conInfo1.edt_agency.value = FV_AGENCY_UKEY_ID;
		div_conInfo1.btn_agency.Enable = false;
		div_conInfo1.edt_agencyNm.Enable = false;
		if (FV_AGENCY_ID != FV_ORG_AREA_ID && !uf_IsNull(FV_ORG_AREA_ID)) {
			div_conInfo1.edt_dealNm.value = FV_ORG_AREA_NM;
			div_conInfo1.edt_dealCd.value = FV_ORG_AREA_ID;
			div_conInfo1.btn_dealCo.Enable = false;
			div_conInfo1.edt_dealNm.Enable = false;
		} else {
			div_conInfo1.btn_dealCo.Enable = true;
			div_conInfo1.edt_dealNm.Enable = true;
		}	
	}else{	
		div_conInfo1.btn_agency.Enable = true;	
		div_conInfo1.edt_agencyNm.Enable = true;
		div_conInfo1.btn_dealCo.Enable = true;
		div_conInfo1.edt_dealNm.Enable = true;
	}

	/* 유저그룹이 대리점일 경우 처리 */

	if (FV_USER_GRP != 'S00') {
		if (Left(FV_USER_GRP,1) == "D") {
			div_conInfo1.sta_agency_nm.Text = "대리점";
			div_conInfo1.img_agency.Left = 10;
			div_conInfo3.chk_chagDedtYn.Value = "1";
			div_conInfo3.chk_chagDedtYn.Enable = false;
			div_conInfo3.sta_bank.Visible = false;
			div_conInfo3.sta_accNo.Visible = false;
			div_conInfo3.cmb_bankCd.Visible = false;
			div_conInfo3.edt_accNo.Visible = false;
		}else{
			div_conInfo1.sta_agency_nm.Text = "팀";
			div_conInfo1.img_agency.Left = 40;
			div_conInfo3.chk_chagDedtYn.Visible = false;
			div_conInfo3.sta_svcNo.Visible = false;
			div_conInfo3.sta_svcMgmtNo.Visible = false;
			div_conInfo3.edt_svcNo.Visible = false;
			div_conInfo3.edt_svcMgmtNo.Visible = false;
		}	
	}	

	lf_formVarInit();
	if (!uf_IsNull(FV_CON_MGMT_NO)){
		f_getConsult_m();
//		div_conInfo2.edt_serNm.Enable = false;
//		div_conInfo2.edt_ProdCd.Enable = false;
	} else {
//		div_conInfo1.edt_userId = FV_USER_ID;
//		div_conInfo1.edt_userNm = FV_USER_NM;
		lf_getRateList();	
	}

	if ( FV_PRC_GB == "N" ) {
		btn_save.Visible = true;
	} else {
		btn_save.Visible = false;
//		btn_print.Visible = false;
	}

//	if(gds_session.GetColumn(0, "usergrp") != 'S00'){
//		btn_print.Visible = false;
//	}

	http.Sync = false;	

}

/*******************************************************************************
* 함수명 : df_reSet
* 기능명 : 화면 초기화
*******************************************************************************/
function df_reSet(sArg)
{
	http.Sync = true;

	lf_getSession();
	lf_init();
	lf_setCombo();
	FV_PARENT_NUM = vFrmNum;

	if( !uf_IsNull(FV_AGENCY_ID)){
		div_conInfo1.edt_agencyNm.value = FV_AGENCY_NM;
	    div_conInfo1.edt_posAgency.value = FV_AGENCY_ID;
		div_conInfo1.edt_agency.value = FV_AGENCY_UKEY_ID;
		div_conInfo1.btn_agency.Enable = false;
		div_conInfo1.edt_agencyNm.Enable = false;
		if (FV_AGENCY_ID != FV_ORG_AREA_ID && !uf_IsNull(FV_ORG_AREA_ID)) {
			div_conInfo1.edt_dealNm.value = FV_ORG_AREA_NM;
			div_conInfo1.edt_dealCd.value = FV_ORG_AREA_ID;
			div_conInfo1.btn_dealCo.Enable = false;
			div_conInfo1.edt_dealNm.Enable = false;
		} else {
			div_conInfo1.btn_dealCo.Enable = true;
			div_conInfo1.edt_dealNm.Enable = true;
		}	
	}else{	
		div_conInfo1.btn_agency.Enable = true;	
		div_conInfo1.edt_agencyNm.Enable = true;
		div_conInfo1.btn_dealCo.Enable = true;
		div_conInfo1.edt_dealNm.Enable = true;
	}

	/* 유저그룹이 대리점일 경우 처리 */
	if (Left(FV_USER_GRP,1) == "D") {
		div_conInfo1.sta_agency_nm.Text = "대리점";
		div_conInfo1.img_agency.Left = 10;
		div_conInfo3.chk_chagDedtYn.Value = "1";
		div_conInfo3.chk_chagDedtYn.Enable = false;
		div_conInfo3.sta_bank.Visible = false;
		div_conInfo3.sta_accNo.Visible = false;
		div_conInfo3.cmb_bankCd.Visible = false;
		div_conInfo3.edt_accNo.Visible = false;
	}else{
		div_conInfo1.sta_agency_nm.Text = "팀";
		div_conInfo1.img_agency.Left = 40;
		div_conInfo3.chk_chagDedtYn.Visible = false;
		div_conInfo3.sta_svcNo.Visible = false;
		div_conInfo3.sta_svcMgmtNo.Visible = false;
		div_conInfo3.edt_svcNo.Visible = false;
		div_conInfo3.edt_svcMgmtNo.Visible = false;
	}	
	FV_CON_MGMT_NO = "";
	FV_PROD_CD = "";
	FV_OUT_YN = "";
	cf_setArg("ds_reset", sArg);
	FV_CON_MGMT_NO = ds_reset.GetColumn(0,"p_con_mgmt_no");
	FV_PROD_CD = ds_reset.GetColumn(0,"p_prod_cd");
	FV_OUT_YN = ds_reset.GetColumn(0,"p_out_yn");

	if (!uf_IsNull(FV_CON_MGMT_NO)){
		f_getConsult_m();
//		div_conInfo2.edt_serNm.Enable = false;
//		div_conInfo2.edt_ProdCd.Enable = false;
	} else {
//		div_conInfo1.edt_userId = FV_USER_ID;
//		div_conInfo1.edt_userNm = FV_USER_NM;
		lf_getRateList();	
	}

//	if(gds_session.GetColumn(0, "usergrp") != 'S00'){
//		btn_print.Visible = false;
//	}

	http.Sync = false;	
}

function lf_init()
{
}
function lf_setCombo()
{
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_conInfo3.cmb_bankCd"       , "ZBAS_C_00060", "선택");	
	cf_setCommonCode("div_conInfo3.cmb_policy"       , "ZBAS_C_00480", "선택");	
	cf_checkCommonCode(); 
}
/*******************************************************************************
 * @description         : 로컬 변수 초기화 초기화
*******************************************************************************/
function lf_formVarInit()
{    
    //상담이력조회 선택
    if(IsExistVar("p_con_mgmt_no",true)){ FV_CON_MGMT_NO = p_con_mgmt_no; } 
    if(IsExistVar("p_prod_cd",true)){ FV_PROD_CD = p_prod_cd; }   
    if(IsExistVar("p_con_dt",true)){ FV_CON_DT = p_con_dt; }   
    if(IsExistVar("p_out_yn",true)){ FV_OUT_YN = p_out_yn; }
    if(IsExistVar("p_prc_gb",true)){ FV_PRC_GB = p_prc_gb; }
}

/*******************************************************************************
 * @description    : 글로벌 섹션 정보
*******************************************************************************/
function lf_getSession()
{
	FV_USER_ID        = gds_session.GetColumn(0, "loginid");						// 사용자ID
	FV_USER_NM        = gds_session.GetColumn(0, "usernm");							// 사용자명

	FV_ORG_ID         = gds_session.GetColumn(0, "orgid");							// 소속 
	FV_ORG_NM         = gds_session.GetColumn(0, "orgnm");							// 소속명
	FV_ORG_CL         = gds_session.GetColumn(0, "orgcl");							// 소속구분
	FV_AGENCY_ID      = gds_session.GetColumn(0, "posAgencyId");        			// 소속 팀ID
	FV_AGENCY_NM      = gds_session.GetColumn(0, "posAgencyNm");        			// 소속 팀명
	FV_AGENCY_UKEY_ID = gds_session.GetColumn(0, "posAgencyUkeyId");    			// 소속 팀UKEY ID	

	FV_ORG_AREA_ID            = gds_session.GetColumn(0, "orgareaid");				// 근무지 ID
	FV_ORG_AREA_NM            = gds_session.GetColumn(0, "orgareanm");				// 근무지명
	FV_ORG_AREA_POS_AGENCY_CD = gds_session.GetColumn(0, "orgareaposagencycd");		// 근무지 
	FV_ORG_AREA_UKEY_ID       = gds_session.GetColumn(0, "orgareaukeyid");      	// 근무지ukeyid
	FV_ORG_AREA_SUB_CD        = gds_session.GetColumn(0, "orgareaukeysubcd");   	// 근무지subcd
	FV_ORG_AREA_CHANNEL_CD    = gds_session.GetColumn(0, "orgareaukeychannelcd"); 	// 근무지 channelcd	
	FV_USER_GRP               = gds_session.GetColumn(0, "usergrp");				// 유저그룹

	FV_ORG_AREA_CL1           = gds_session.GetColumn(0, "orgareacl1");				// 근무지 구분
	FV_DUTY                   = gds_session.GetColumn(0, "duty");	   		    	// 직책		
}

