/*******************************************************************************
 * @description : 금액 초기화
 ******************************************************************************/
function f_clear()
{
	lf_init();
	div_conInfo2.cal_runDtmFrom = "";
	div_conInfo2.cmb_color.Index = 0;
	div_conInfo2.cmb_rate.Index = 0;

	div_conInfo4.edt_day = 0;
	div_conInfo4.msk_dayAmt = 0;
	div_conInfo4.msk_perferAmt = 0;
	div_conInfo4.edt_eqpSt = "";
	div_conInfo4.edt_eqpStRate = 0;
	div_conInfo4.msk_eqpStAmt = 0;

	div_conInfo5.msk_conAmt.value = 0;
	div_conInfo2.sta_rmk.Text = "";
	div_conInfo2.sta_comp_cl.Text = "";

	FV_DAY = "";
	FV_DAY_AMT = "";
	FV_STANDARD_AMT = "";
	FV_PREFER_AMT = "";
	FV_GRADE = "";
	FV_GRADE_AMT = "";
}

/*******************************************************************************
 * @description    :  단말기등급조회
*******************************************************************************/
function lf_getRateList()
{
	ds_rate.ClearData();

	var sSvcID 	= "getRateList";
	var sInDs  	= "nc_input_fieldDs=ds_prod_m";
	var sOutDs 	= "ds_rate=ds_rate";
	var sArg	= "sktst.cst.ADV#getRateMgmt";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);


}

