}

/*******************************************************************************
 * @description    :  상담등록 저장시 validation check
*******************************************************************************/
function lf_saveValidationCheck()
{
    var bFlag = true;

    if ( uf_IsNull(div_conInfo1.edt_dealNm.Text) || uf_IsNull(div_conInfo1.edt_dealCd.Text) ) {
		alert("상담처를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.edt_serNm.Text) ) {
		alert("일련번호를 입력해 주십시요.");
		return false;
	}

	if ( length(div_conInfo2.edt_serNm.Text) != 7 && 
		 length(div_conInfo2.edt_serNm.Text) != 11 &&
		 length(div_conInfo2.edt_serNm.Text) != 12) {
		alert("일련번호는 7자리 또는 11자리 또는 12자리만 입력가능합니다.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.cal_runDtmFrom.Value) ) {
		alert("개통일자를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.edt_ProdCd.Text) || uf_IsNull(div_conInfo2.edt_ProdNm.Text) ) {
		alert("상품을 선택해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.cmb_color.Value) ) {
		alert("색상을 선택해 주십시요.");
		return false;
	}

	for(var i = 0 ; i < ds_consult_d.rowcount ; i++) {
		if( ds_consult_d.GetColumn(i,"DTL_CL") == "1" && ds_consult_d.GetColumn(i,"HLD_QTY") == 0 ) {
			alert("기본배터리는 필수구성품 입니다.");
			return false;
		}	
    }

	if ( uf_IsNull(div_conInfo3.edt_custNm.Text) ) {
		alert("고객명을 입력해 주십시요.");
		return false;
	}
/*
	if ( uf_IsNull(div_conInfo3.edt_resNo.Text) ) {
		alert("주민번호를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo3.edt_telNo.Text) ) {
		alert("연락처를 입력해 주십시요.");
		return false;
	}	

	if ( div_conInfo3.chk_chagDedtYn.value == '0' && 
	    (uf_IsNull(div_conInfo3.cmb_bankCd.Value)||div_conInfo3.cmb_bankCd.Value==" ")) {
		alert("은행을 선택해 주십시요.");
		return false;
	}

	if ( div_conInfo3.chk_chagDedtYn.value == '0' && uf_IsNull(div_conInfo3.edt_accNo.Text) ) {
		alert("계좌번호를 입력해 주십시요.");
		return false;
	}
*/
	if ( div_conInfo3.chk_chagDedtYn.value == '1' && uf_IsNull(div_conInfo3.edt_svcNo.Text) ) {
		alert("서비스번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo3.chk_chagDedtYn.value == '1' && uf_IsNull(div_conInfo3.edt_svcMgmtNo.Text) ) {
		alert("서비스관리번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo3.chk_chagDedtYn.value == '1' && length(div_conInfo3.edt_svcMgmtNo.Text) != 10) {
		alert("서비스관리번호는 10자리만 입력가능합니다.");
		return false;
	}

	if (ds_prod_m.GetColumn(0,"COMP_CL") != '1' && uf_IsNull(div_conInfo2.edt_imei.Text)) {
		alert("IMEI를 입력해 주십시요.");
		return false;
	}
/*	
	if ( uf_IsNull(div_conInfo3.edt_zipCd1.Text) || uf_IsNull(div_conInfo3.edt_zipCd2.Text)) {
		alert("우편번호를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo3.edt_addr.Text) || uf_IsNull(div_conInfo3.edt_dtlAddr.Text)) {
		alert("주소를 입력해 주십시요.");
		return false;
	}					
*/
    return bFlag;

}


