	ds_consult_m.SetColumn(0,"DTL_ADDR","");
	ds_consult_m.SetColumn(0,"ZIP_CD1","");
	ds_consult_m.SetColumn(0,"ZIP_CD2","");
//---------			


	var ds_Cnt = ds_consult_d.GetRowCount();
	for( i=0 ; i <ds_Cnt ; i++){

		tmpStr = ds_consult_d.getColumn(i, "DTL_CL");
		tmpStr_cnt = ds_consult_d.getColumn(i, "HLD_QTY");

		if(tmpStr == '1'||tmpStr == '2'||tmpStr == '3'){	// 배터리 수량
			batt_tot_cnt = batt_tot_cnt + tmpStr_cnt; 
			ds_prodDtl.SetColumn(0,"BATTERY_CNT",batt_tot_cnt);

		}else if(tmpStr == '4' || tmpStr == '5'){	// 충전기 (젠더)
			charg_cnt = charg_cnt + tmpStr_cnt;
			ds_prodDtl.SetColumn(0,"CHARGE",charg_cnt);
//			if( charg_cnt > '0'){
//				ds_prodDtl.SetColumn(0,"CHARGE","Y");
//			}else{
//				ds_prodDtl.SetColumn(0,"CHARGE","N");
//			}
		}else if(tmpStr == '7'){	// 베터리 케이스
			ds_prodDtl.SetColumn(0,"BATTERY_CASE",tmpStr_cnt);
//			if( tmpStr_cnt > '0'){
//				ds_prodDtl.SetColumn(0,"BATTERY_CASE","Y");
//			}else{
//				ds_prodDtl.SetColumn(0,"BATTERY_CASE","N");
//			}
		}else if(tmpStr == '8'){	//  DATA 케이블
			ds_prodDtl.SetColumn(0,"DATA_CABLE",tmpStr_cnt);
//			if( tmpStr_cnt > '0'){
//				ds_prodDtl.SetColumn(0,"DATA_CABLE","Y");
//			}else{
//				ds_prodDtl.SetColumn(0,"DATA_CABLE","N");
//			}
		}
	}

	dialog("CST::CSTADV00510.xml", "" , 300, 500);
    this.close();
}
function div_conInfo3_chk_polYn_OnClick(obj,strValue)
{
	if(div_conInfo3.chk_polYn.Value == "1") {
		div_conInfo3.cmb_policy.Visible = true;
	} else {
		div_conInfo3.cmb_policy.Value = "";
		div_conInfo3.cmb_policy.Visible = false;
	}
}
