/*******************************************************************************
 * @description    : 상담목록 조회 
*******************************************************************************/
function btn_search_onClick(obj)
{
	ds_list.ClearData();

	if (fn_checkSearchVal()) {
		var sSvcID 	= "getConList";
		var sInDs  	= "nc_input_fieldDs=ds_condition";
		var sOutDs 	= "ds_list=ds_list";
		var sArg	= "sktst.cst.ADV#getPrchsCustAdvList";
//		var sArg	= "sktst.cst.ADV#getPrchsBuyList";
		// 공통 트랜잭션 호출
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
	}

}

/*******************************************************************************
 * @description    :  조회조건 체크 
*******************************************************************************/
function fn_checkSearchVal()
{
//	return sf_isValidDateFromTo(div_search.cal_From,div_search.cal_To, 0, true);

	var bFlag = true;

    if(Length(div_search.cal_From.Value) < 1)
    {
        alert(cf_getMessage(MSG_00083, "검수일자"));
        div_search.cal_From.SetFocus();
        return false;
    }

    if(Length(div_search.cal_To.Value) < 1)
    {
        alert(cf_getMessage(MSG_00083, "검수일자"));
        div_search.cal_To.SetFocus();
        return false;
    }

    if(div_search.cal_From.Value > div_search.cal_To.Value)
    {
        alert(cf_getMessage(MSG_00086, "시작일;마지막일"));
        div_search.cal_From.SetFocus();
        return false;
    }
/*    
    if(substr(div_search.cal_From.Value,0,6) != substr(div_search.cal_To.Value,0,6))
	{
		alert("시작일자와 종료일자을 동일한 월로 지정하세요");
		div_search.cal_From.SetFocus();
		return;
	}
*/	
	if( datetime(div_search.cal_To.Value) - datetime(div_search.cal_From.Value) > 31) {
		alert("시작일자와 종료일자을 1개월 이내로 지정하세요");
		div_search.cal_From.SetFocus();
		return;	
	}	

    return bFlag;

}

