/*******************************************************************************
 * @description    :  callBack
*******************************************************************************/
function f_callBack(sSvcID)
{
    if (sSvcID == "RemitNo") {
		alert("송금확인이 취소 되었습니다.");
		btn_search_onClick();
    }
    if (sSvcID == "RemitYes") {
		alert("송금확인이 처리 되었습니다.");
		btn_search_onClick();
    }    
} 


function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "PRC_CHECK") {
		if (ds_list.GetColumn(nRow,"PRC_CHECK") == "1") {
		   if (ds_list.GetColumn(nRow,"OLDEQ_PAY_MTHD_YN") != "Y") {
			   ds_list.SetColumn(nRow,"OLDEQ_PAY_DT",Left(cf_getDate(), 8));
		   }
		} else {
		   if (ds_list.GetColumn(nRow,"OLDEQ_PAY_MTHD_YN") != "Y") {
			   ds_list.SetColumn(nRow,"OLDEQ_PAY_DT","");
		   }
		}	
	}
}

function btn_remitNo_OnClick(obj)
{
	var sSvcID = "RemitNo";
	var sInDs  = "ds_list=ds_list";
	var sOutDs = "";
	var sArg   = "sktst.cst.SKN#updateSknRemitNo";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

	return;
}

function btn_remitYes_OnClick(obj)
{

	if ( f_checkSave() == "Y" ) {
		alert("송금처리일자를 입력해 주세요.");
		return;
	}

	var sSvcID = "RemitYes";
	var sInDs  = "ds_list=ds_list";
	var sOutDs = "";
	var sArg   = "sktst.cst.SKN#updateSknRemitYes";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

	return;
}

/*******************************************************************************
 * @description    : 저장 체크
*******************************************************************************/
function f_checkSave()
{
	var bFlag = "N";
    for(var i=0; i<ds_list.rowcount; i++) {
		if ( ds_list.GetColumn(i,"PRC_CHECK") == "1" && uf_IsNull(ds_list.GetColumn(i,"OLDEQ_PAY_DT"))) {
			bFlag = "Y";
		}
	}
	return bFlag;
}
