Window.Script:
/*******************************************************************************
* 프로그램ID : DISBEQ00500
* 업무명      :  불량기기관리 - Ukey 교품신청결과상세
* 프로그램명  : Ukey 교품신청결과상세
--------------------------------------------------------------------------------
* 작성자      : 이정현
* 작성일      : 2009.03.
--------------------------------------------------------------------------------
* 1. 변경이력
수정자    :
수정일    :
수정 내역 :
*******************************************************************************/

//==============================================================================
// 공통 스크립트 로딩
//==============================================================================
#include "lib::lib_commF.js"
#include "lib::lib_commPopup.js"
#include "lib::lib_commMsg.js"
#include "lib::lib_util.js"

//==============================================================================
// 폼 변수 정의
//==============================================================================
var FV_DS_CURR_ROW_INDEX;		// 부모폼의 현재 row index

var FV_ORG_CL;    		// 조직구분
var FV_ORG_ID;    		// 조직ID
var FV_ORG_NM;    		// 조직명
var FV_SUP_ORG_ID; 		// 상위조직
var FV_SUP_ORG_NM;  	// 상위조직명
var FV_USER_GRP;    	// 사용자그룹
var FV_ORG_AREA_ID;    // 근무지
var FV_ORG_AREA_NM;    // 근무지명
var FV_POS_AGENCY_ID;		// 소속대리점ID
var FV_POS_AGENCY_NM;   	// 소속대리점명
var FV_POS_AGENCY_UKEY_ID; // 소속대리점 Ukey ID

/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function DISBEQ00500_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);

	// 세션 정보 설정
	f_setSessionInfo();

	// 부모창으로부터 값을 지정
	cf_setParam(ds_Condition, "prod_cd", parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "prod_cd"));  	// 상품코드
	cf_setParam(ds_Condition, "color_cd", parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "color_cd"));  	// 색상코드
	cf_setParam(ds_Condition, "ser_num", parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "ser_num"));  	// 일련번호
	cf_setParam(ds_Condition, "rgst_seq", parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "rgst_seq"));  	// 등록순번

	// 모델정보 지정
	div_search_01.edt_Prodcd.Value = parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "prod_cd");
	div_search_01.edt_ProdNm.Value = parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "prod_nm");
	div_search_01.edt_SerNum.Value = parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "ser_num");
	div_search_01.edt_ColorNm.Value = parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "color_nm");

    // 영업담당 및 판매점 지정
	div_search_02.edt_ChrgrUserNm.Value = parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "chrgr_user_nm");
	div_search_02.edt_SalePlcNm.Value = parent.ds_BadEquipOut.GetColumn(FV_DS_CURR_ROW_INDEX, "sale_plc_nm");

	// 불량기기 Ukey 정보 조회
	f_BadEquipUkeyInfo();
}

/*******************************************************************************
 * @description    : 세션 정보 설정
*******************************************************************************/
function f_setSessionInfo()
{
	FV_ORG_CL = gds_session.GetColumn(0,"orgCl");
	FV_ORG_ID = gds_session.GetColumn(0,"orgId");
	FV_ORG_NM = gds_session.GetColumn(0,"orgNm");
	FV_SUP_ORG_ID = gds_session.GetColumn(0,"supOrgId");
	FV_SUP_ORG_NM = gds_session.GetColumn(0,"supOrgNm");
	FV_USER_GRP = gds_session.GetColumn(0,"userGrp");
	FV_ORG_AREA_ID = gds_session.GetColumn(0,"orgAreaId");
	FV_ORG_AREA_NM = gds_session.GetColumn(0,"orgAreaNm");
    FV_POS_AGENCY_ID      = gds_session.GetColumn(0,"posAgencyId");   
    FV_POS_AGENCY_NM      = gds_session.GetColumn(0,"posAgencyNm");     
    FV_POS_AGENCY_UKEY_ID = gds_session.GetColumn(0,"posAgencyUkeyId");	
}

/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{		
	if(sSvcID == "svcBadEquipUkeyInfo")
	{
		return;
	}
}

/*******************************************************************************
 * @description    : 불량기기 Ukey 정보 조회
*******************************************************************************/
function f_BadEquipUkeyInfo()
{
    // 출력 Dataset 초기화
    ds_BadEquipUKeyInfo.ClearData();

	// 트랜잭션 설정
	var sSvcID = "svcBadEquipUkeyInfo";
	var sInDs = "nc_input_fieldDs=ds_Condition";
	var sOutDs = "ds_BadEquipUKeyInfo=ds_BadEquipUKeyInfo";
	var sArg = "sktst.dis.BEQ#getBadEquipUkeyInfo";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
}

/*******************************************************************************
 * @description    : 닫기 click 이벤트
*******************************************************************************/
function btn_close_OnClick(obj)
{
	this.Close();
}
