/*******************************************************************************
 * @description    : 보유처별재고현황 OnCharChanged 이벤트
*******************************************************************************/
function div_search_edt_HldPlcNm_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
	{
		div_search.edt_HldPlcId.value = "";
	}	
}

/*******************************************************************************
 * @description    : 보유처별재고현황 OnKillFocus 이벤트
*******************************************************************************/
function div_search_edt_AgencyNm_OnKillFocus(obj)
{
	if (!uf_IsNull(div_search.edt_AgencyNm.Value))
	{
		http.Sync = true;

		// 영업담당 조회
		f_getChrgrUserIdList();

		http.Sync = false;	

		cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");	
	}
	else
	{
		// 출력 Dataset 초기화
		ds_ChrgrUser.ClearData();

		cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");		
	}
}
