//==============================================================================
#include "lib::lib_commF.js"
#include "lib::lib_commPopup.js"
#include "lib::lib_commMsg.js"
#include "lib::lib_util.js"
#include "lib::lib_DIS.js"

//==============================================================================
// 폼 변수 정의
//==============================================================================

//Session변수
var FV_ORG_CL;    		// 조직구분
var FV_ORG_ID;    		// 조직ID
var FV_ORG_NM;    		// 조직명
var FV_SUP_ORG_ID; 		// 상위조직
var FV_SUP_ORG_NM;  	// 상위조직명
var FV_USER_GRP;    	// 사용자그룹
var FV_ORG_AREA_ID;    // 근무지
var FV_ORG_AREA_NM;    // 근무지명
var FV_POS_AGENCY_ID;		// 소속대리점ID
var FV_POS_AGENCY_NM;   	// 소속대리점명
var FV_POS_AGENCY_UKEY_ID; // 소속대리점 Ukey ID

/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function DISDSM01000_OnLoadCompleted(obj)
{
    // 기준일자 셋팅
	div_search.cal_StrdDt.Value = Mid(cf_getDate(),0,8);
	div_search.cal_StrdDt.SetFocus();

	// 권한 설정
	cf_setAuth(pFrmNum);

	f_setSessionInfo();

	// 로그인 사용자별 조직 제어
	f_setOrgInfo();

	// 로그인 사용자별 거래처 제어
	f_setDealInfo();

	// 공통 코드 조회
	f_initCombo();

    // 출력 Dataset 초기화
    ds_ModelDisMgmtOut.ClearData();
    ds_DisStrdMth.ClearData();

	// 그리드 체크박스 설정 및 정렬
	cf_setGrdChecknSortEvent(grd_ModelDisMgmtList);
}

/*******************************************************************************
 * @description    : 세션 정보 설정
*******************************************************************************/
function f_setSessionInfo()
{
	FV_ORG_CL = gds_session.GetColumn(0,"orgCl");
	FV_ORG_ID = gds_session.GetColumn(0,"orgId");
	FV_ORG_NM = gds_session.GetColumn(0,"orgNm");
	FV_SUP_ORG_ID = gds_session.GetColumn(0,"supOrgId");
	FV_SUP_ORG_NM = gds_session.GetColumn(0,"supOrgNm");
	FV_USER_GRP = gds_session.GetColumn(0,"userGrp");
	FV_ORG_AREA_ID = gds_session.GetColumn(0,"orgAreaId");
	FV_ORG_AREA_NM = gds_session.GetColumn(0,"orgAreaNm");
    FV_POS_AGENCY_ID      = gds_session.GetColumn(0,"posAgencyId");   
    FV_POS_AGENCY_NM      = gds_session.GetColumn(0,"posAgencyNm");     
    FV_POS_AGENCY_UKEY_ID = gds_session.GetColumn(0,"posAgencyUkeyId");	
}

/*******************************************************************************
 * @description    : 조회 조건상의 Combox 항목에 공통코드를 설정
*******************************************************************************/
function f_initCombo()
{
	http.Sync = true;

	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();

	cf_setCommonCode("ds_search_cl", "ZDIS_C_00250");    // 조회구분
	cf_setCommonCode("ds_search_strd", "ZDIS_C_00260");  // 조회기준
	cf_setCommonCode("ds_deal_co_cl", "ZBAS_C_00240");   // 거래처구분
	cf_setCommonCode("ds_prod_cl", "ZBAS_C_00010");      // 상품구분
	cf_setCommonCode("ds_com_mthd", "ZBAS_C_00440");   // 통신방식
	cf_setCommonCode("ds_sale_grp", "ZBAS_C_00130");   // 영업그룹

	// 설정된 코드를 기반으로 공통 코드를 추가 및 데이타 바인딩
	cf_checkCommonCode();

	// 영업담당 조회
	f_getChrgrUserIdList();

	http.Sync = false;

	cf_setInitDataForCmb(div_search.cmb_SearchCl,"선택");
	cf_setInitDataForCmb(div_search.cmb_SearchStrd,"선택");
	cf_setInitDataForCmb(div_search.cmb_DealCoCl,"전체");
	cf_setInitDataForCmb(div_search.cmb_ProdCl,"전체");	
	cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");
	cf_setInitDataForCmb(div_search.cmb_ComMthd,"전체");	
	cf_setInitDataForCmb(div_search.cmb_SaleGrp,"전체");	

	div_search.cmb_ProdCl.Index = 1;
	div_search.cmb_SearchCl_1.Index = 0;
}

/*******************************************************************************
 * @description    : 로그인 사용자별 조직 제어
*******************************************************************************/
function f_setOrgInfo()
{
	// 소속 대리점이 있는 경우 대리점 선택 비활성화
	if(!uf_IsNull(FV_POS_AGENCY_ID))
	{
		div_search.edt_AgencyCd.value = FV_POS_AGENCY_ID;
		div_search.edt_AgencyNm.value = FV_POS_AGENCY_NM;
		div_search.edt_AgencyNm.Enable = false;
		div_search.btn_AgencyId.Enable = false;
	}	
	else
	{
		div_search.edt_AgencyNm.Enable = true;	
		div_search.btn_AgencyId.Enable = true;
	}
}

/*******************************************************************************
 * @description    : 로그인 사용자별 거래처 제어
*******************************************************************************/
function f_setDealInfo()
{
	// 로그인한 사용자의 소속매장이 직영점, 판매점인 경우 
	if(FV_POS_AGENCY_ID != FV_ORG_AREA_ID)
	{
		div_search.edt_HldPlcId.value = FV_ORG_AREA_ID;
		div_search.edt_HldPlcNm.value = FV_ORG_AREA_NM;
		div_search.edt_HldPlcNm.Enable = false;
		div_search.btn_HldPlcId.Enable = false;
	}	
	else
	{
		div_search.edt_HldPlcNm.Enable = true;	
		div_search.btn_HldPlcId.Enable = true;
	}
}

/*******************************************************************************
 * @description    : 모델별재고관리현황 조회
*******************************************************************************/
function f_checkCondition()
{
    if(uf_IsNull(div_search.cal_StrdDt.Value))
    {
        alert(cf_getMessage(MSG_00083, "기준일자"));
        div_search.cal_StrdDt.SetFocus();
        return false;
    }

    if(uf_IsNull(div_search.cmb_SearchCl.Value))
    {
        alert(cf_getMessage(MSG_00083, "조회구분"));
        div_search.cmb_SearchCl.SetFocus();
        return false;
    }

    if(uf_IsNull(div_search.cmb_SearchStrd.Value))
    {
        alert(cf_getMessage(MSG_00083, "조회기준"));
        div_search.cmb_SearchStrd.SetFocus();
        return false;
    }

    if (uf_IsNull(div_search.edt_AgencyCd.Value))
    {
		alert(cf_getMessage(MSG_00083, "대리점"));
        div_search.edt_AgencyCd.SetFocus();
		return false;
    }

    if (uf_IsNull(div_search.cmb_SearchCl_1.Value))
    {
		alert(cf_getMessage(MSG_00083, "현황구분"));
		return false;
    }

    return true;	
}

/*******************************************************************************
 * @description    : 영업담당 조회
*******************************************************************************/
function f_getChrgrUserIdList()
{
	// 입력 파라메터 설정
	cf_setParam(ds_Condition, "pos_agency", div_search.edt_AgencyCd.Value);  // 대리점

    // 출력 Dataset 초기화
    ds_ChrgrUser.ClearData();

	// 트랜잭션 설정
	var sSvcID = "svcChrgrUserIdList";
	var sInDs = "nc_input_fieldDs=ds_Condition";
	var sOutDs = "ds_ChrgrUser=ds_ChrgrUser";
	var sArg = "sktst.dis.BEQ#getChrgrUserId";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * @description    : 모델별재고관리현황 조회
*******************************************************************************/
function f_getModelDisMgmtList()
{
	http.Sync = true;

	// 기준일자에 대한 최근 재고마감년월 지정
	f_getDisStrdMth();

	// 입력 파라메터 설정
	cf_setParam(ds_Condition, "strd_ym", ds_DisStrdMth.GetColumn(0,"strd_ym"));  	// 최근재고마감년월
	cf_setParam(ds_Condition, "from_dt", addMonth(ds_DisStrdMth.GetColumn(0,"strd_ym") + '01',1));  	// 시작일자
	cf_setParam(ds_Condition, "strd_dt", div_search.cal_StrdDt.Value);  		// 기준일자
	cf_setParam(ds_Condition, "deal_co_cl", div_search.cmb_DealCoCl.Value);  	// 거래처구분
	cf_setParam(ds_Condition, "prod_cl", div_search.cmb_ProdCl.Value);  		// 상품구분
	cf_setParam(ds_Condition, "pos_agency", div_search.edt_AgencyCd.Value);		// 대리점
	cf_setParam(ds_Condition, "hld_plc_id", div_search.edt_HldPlcId.Value);		// 보유처
	cf_setParam(ds_Condition, "mfact_id", div_search.edt_MfactId.Value);		// 제조사
	cf_setParam(ds_Condition, "prod_cd", div_search.edt_ProdCd.Value);			// 상품
	cf_setParam(ds_Condition, "strd_dt_cl", div_search.cmb_SearchStrd.Value);   // 조회기준구분
	cf_setParam(ds_Condition, "chrgr_user_id", div_search.cmb_ChrgrUser.Value);		// 영업담당
	cf_setParam(ds_Condition, "com_mthd", div_search.cmb_ComMthd.Value);    // 통신방식
	cf_setParam(ds_Condition, "sale_grp", div_search.cmb_SaleGrp.Value);    // 영업그룹
	cf_setParam(ds_Condition, "prod_nm", ToUpper(div_search.edt_prod.Value));        // 모델명

	http.Sync = false;

    // 출력 Dataset 초기화
    ds_ModelDisMgmtOut.ClearData();

	if (div_search.cmb_SearchCl.Value == "01")  // 조회구분 (수량)
	{
		if (div_search.cmb_SearchCl_1.Index == 0) // 보유처별
		{
			// 트랜잭션 설정
			var sSvcID = "svcHldModelDisMgmtQtyList";
			var sInDs = "nc_input_fieldDs=ds_Condition";
			var sOutDs = "ds_ModelDisMgmtOut=ds_ModelDisMgmtOut";
			var sArg = "sktst.dis.DSM#getHldModelDisMgmtQtyList";

			// 공통 트랜잭션 호출
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
		}
		else if (div_search.cmb_SearchCl_1.Index == 1) // 모델별
		{
			// 트랜잭션 설정
			var sSvcID = "svcModelDisMgmtQtyList";
			var sInDs = "nc_input_fieldDs=ds_Condition";
			var sOutDs = "ds_ModelDisMgmtOut=ds_ModelDisMgmtOut";
			var sArg = "sktst.dis.DSM#getModelDisMgmtQtyList";

			// 공통 트랜잭션 호출
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
		}
	}
	else if (div_search.cmb_SearchCl.Value == "02")  // 조회구분 (금액)
	{
		if (div_search.cmb_SearchCl_1.Index == 0) // 보유처별
		{
			// 트랜잭션 설정
			var sSvcID = "svcHldModelDisMgmtAmtList";
			var sInDs = "nc_input_fieldDs=ds_Condition";
			var sOutDs = "ds_ModelDisMgmtOut=ds_ModelDisMgmtOut";
			var sArg = "sktst.dis.DSM#getHldModelDisMgmtAmtList";

			// 공통 트랜잭션 호출
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
		}
		else if (div_search.cmb_SearchCl_1.Index == 1) // 모델별
		{
			// 트랜잭션 설정
			var sSvcID = "svcModelDisMgmtAmtList";
			var sInDs = "nc_input_fieldDs=ds_Condition";
			var sOutDs = "ds_ModelDisMgmtOut=ds_ModelDisMgmtOut";
			var sArg = "sktst.dis.DSM#getModelDisMgmtAmtList";

			// 공통 트랜잭션 호출
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
		}
	}        
}

/*******************************************************************************
 * @description    : 최근 재고마감년월 조회
*******************************************************************************/
function f_getDisStrdMth()
{
	// 입력 파라메터 설정
	cf_setParam(ds_Condition, "strd_dt", div_search.cal_StrdDt.Value);  	// 기준일자
	cf_setParam(ds_Condition, "pos_agency", div_search.edt_AgencyCd.Value);		// 대리점

    // 출력 Dataset 초기화
    //ds_DisStrdMth.ClearData();

	// 트랜잭션 설정
	var sSvcID = "svcDisStrdMth";
	var sInDs = "nc_input_fieldDs=ds_Condition";
	var sOutDs = "ds_DisStrdMth=ds_DisStrdMth";
	var sArg = "sktst.dis.DSM#getDisStrdMth";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcModelDisMgmtQtyList" || sSvcID == "svcModelDisMgmtAmtList")
	{		
		ds_ModelDisMgmtOut.Sort("prod_cl,prod_nm:A");
	}

	if(sSvcID == "svcHldModelDisMgmtQtyList" || sSvcID == "svcHldModelDisMgmtAmtList")
	{		
		ds_ModelDisMgmtOut.Sort("deal_co_cl,hld_plc_nm,prod_cl,prod_nm:A");
	}

	if(sSvcID == "svcDisStrdMth")
	{
		if (uf_IsNull(ds_DisStrdMth.GetColumn(0,"strd_ym")))
		{
			cf_setParam(ds_DisStrdMth, "strd_ym", substr(addMonth(div_search.cal_StrdDt.Value,-1),0,6)); 
		}
	}

    if(sSvcID == "svcChrgrUserIdList")
	{
		return;
	}
}

/*******************************************************************************
 * @description    : 조회버튼 click 이벤트
*******************************************************************************/
function div_search_btn_search_OnClick(obj)
{
	// 조회조건 유효성 검사
	if (f_checkCondition())
	{
		// 모델별재고관리현황 조회
		f_getModelDisMgmtList();	
	}	
}

/*******************************************************************************
 * @description    : 엑셀 다운로드
*******************************************************************************/
function div_page_btn_excelDown_OnClick(obj)
{
	cf_exportExcel(grd_ModelDisMgmtList, 1);
}

/*******************************************************************************
 * @description    : 보유처 OnClick 이벤트
*******************************************************************************/
function div_search_btn_HldPlcId_OnClick(obj)
{
	if (uf_IsNull(div_search.edt_AgencyCd.Value))
	{
		alert(cf_getMessage(MSG_00083, "대리점"));
		return;
	}

	var sArg  = "deal_co_cl1=" + quote("A1,A2,A3,A4,A5,B1,B2,C1,Z1");
		sArg += " pos_agency=" + div_search.edt_AgencyCd.Value; 
		//sArg += " eff_dtm="+quote(div_search.cal_StrdDt.Value);

	cf_callDealPop(sArg, div_search, "edt_HldPlcNm,edt_HldPlcId");
}

/*******************************************************************************
 * @description    : 보유처 OnKeyDown 이벤트
*******************************************************************************/
function div_search_edt_HldPlcNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13)
	{
		if (uf_IsNull(div_search.edt_AgencyCd.Value))
		{
			alert(cf_getMessage(MSG_00083, "대리점"));
			return;
		}

		var sArg  = "deal_co_cl1=" + quote("A1,A2,A3,A4,A5,B1,B2,C1,Z1");
			sArg += " pos_agency=" + div_search.edt_AgencyCd.Value; 
		    //sArg += " eff_dtm="+quote(div_search.cal_StrdDt.Value);

		cf_callDealPop(sArg, div_search, "edt_HldPlcNm,edt_HldPlcId");
	}	
}

/*******************************************************************************
 * @description    : 대리점 click 이벤트
*******************************************************************************/
function div_search_btn_AgencyId_OnClick(obj)
{
	// org_id 와 org_cl 의 파라메터는 필수 입니다.
	var sArg  = "org_id=" + FV_ORG_ID; // 필수
		//sArg += " eff_dtm="+quote(div_search.cal_StrdDt.Value);

	cf_callAgencyPop(sArg, div_search, "edt_AgencyNm,edt_AgencyCd");

	http.Sync = true;

	// 영업담당 조회
	f_getChrgrUserIdList();

	http.Sync = false;	

	cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");
}

/*******************************************************************************
 * @description    : 대리점 OnKeyDown 이벤트
*******************************************************************************/
function div_search_edt_AgencyNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13"){
		// org_id 와 org_cl 의 파라메터는 필수 입니다.
		var sArg  = "org_id=" + FV_ORG_ID; // 필수
		    //sArg += " eff_dtm="+quote(div_search.cal_StrdDt.Value);

		cf_callAgencyPop(sArg, div_search, "edt_AgencyNm,edt_AgencyCd");
	} 	
}

/*******************************************************************************
 * @description    : 대리점 OnCharChanged 이벤트
*******************************************************************************/
function div_search_edt_AgencyNm_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
	{
		div_search.edt_AgencyCd.value = "";
	}
}

