/*******************************************************************************
 * @description    : 모델별재고관리현황 OnKillFocus 이벤트
*******************************************************************************/
function div_search_edt_AgencyNm_OnKillFocus(obj)
{
	if (!uf_IsNull(div_search.edt_AgencyNm.Value))
	{
		http.Sync = true;

		// 영업담당 조회
		f_getChrgrUserIdList();

		http.Sync = false;	

		cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");	
	}
	else
	{
		// 출력 Dataset 초기화
		ds_ChrgrUser.ClearData();

		cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");		
	}
}

/*******************************************************************************
 * @description    : 모델별재고관리현황 OnChanged 이벤트
*******************************************************************************/
function div_search_cmb_SearchCl_1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
    // 출력 Dataset 초기화
    ds_ModelDisMgmtOut.ClearData();

	if (nNewIndex == 0) // 보유처별
	{
		//칼럼의 사이즈 조절
		grd_ModelDisMgmtList.SetColProp(1,"Width", 90);					
		grd_ModelDisMgmtList.SetColProp(2,"Width", 90);					
		grd_ModelDisMgmtList.SetColProp(3,"Width", 90);					
		grd_ModelDisMgmtList.SetColProp(4,"Width", 90);					
	}
	else if (nNewIndex == 1)  // 모델별
	{
		//칼럼의 사이즈 조절
		grd_ModelDisMgmtList.SetColProp(1,"Width", 0);					
		grd_ModelDisMgmtList.SetColProp(2,"Width", 0);					
		grd_ModelDisMgmtList.SetColProp(3,"Width", 0);					
		grd_ModelDisMgmtList.SetColProp(4,"Width", 0);	
		grd_ModelDisMgmtList.SetColProp(5,"Width", 0);
	}
}
