/*******************************************************************************
 * @description    : 현재고현황 OnCellClick 이벤트
*******************************************************************************/
function grd_CurrentDisList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_CurrentDisOut.GetColumn(nRow,"prod_cl") == "9")
	{
		return;
	}

	if (nCell == 10)   // 현재고
	{
		if (ds_CurrentDisOut.GetColumn(nRow,"cur_dis_qty") == 0) return;

		// 재고현황상세 화면 팝업
		var sArg  = " FV_POS_AGENCY=" + Quote(div_search.edt_AgencyCd.Value);
		    sArg += " FV_STRD_DT=" + Quote(div_search.cal_StrdDt.Value); 
		    sArg += " FV_STRD_YM=" + Quote(ds_DisStrdMth.GetColumn(0,"strd_ym"));
		    sArg += " FV_FROM_DT=" + Quote(addMonth(ds_DisStrdMth.GetColumn(0,"strd_ym") + '01',1));

		if (div_search.cmb_SearchCl.Index == 0) // 보유처별
		{
			sArg += " FV_HLD_PLC_ID=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"hld_plc_id"));			
		}
		else if (div_search.cmb_SearchCl.Index == 1) // 모델별
		{
			sArg += " FV_HLD_PLC_ID=" + Quote(div_search.edt_HldPlcId.Value);
		}

		if (!uf_IsNull(div_search.cmb_DealCoCl.Value))
		{
		    sArg += " FV_DEAL_CO_CL=" + Quote(div_search.cmb_DealCoCl.Value);
		}

		if (!uf_IsNull(div_search.cmb_ProdCl.Value))
		{
		    sArg += " FV_PROD_CL=" + Quote(div_search.cmb_ProdCl.Value);
		}

		if (!uf_IsNull(div_search.edt_MfactId.Value))
		{
		    sArg += " FV_MFACT_ID=" + Quote(div_search.edt_MfactId.Value);
		}

		if (!uf_IsNull(ds_CurrentDisOut.GetColumn(nRow,"prod_cd")))
		{
		    sArg += " FV_PROD_CD=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"prod_cd"));
		}

		if (!uf_IsNull(ds_CurrentDisOut.GetColumn(nRow,"color_cd")))
		{
		    sArg += " FV_COLOR_CD=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"color_cd"));
		}

		if (!uf_IsNull(div_search.cmb_ComMthd.Value))
		{
		    sArg += " FV_COM_MTHD=" + Quote(div_search.cmb_ComMthd.Value);
		}

		if (!uf_IsNull(div_search.cmb_SaleGrp.Value))
		{
		    sArg += " FV_SALE_GRP=" + Quote(div_search.cmb_SaleGrp.Value);
		}

		if (!uf_IsNull(div_search.cmb_ChrgrUser.Value))
		{
		    sArg += " FV_CHRGR_USER_ID=" + Quote(div_search.cmb_ChrgrUser.Value);
		}

		if (!uf_IsNull(div_search.edt_prod.Value))
		{
		    sArg += " FV_PROD_NM=" + Quote(ToUpper(div_search.edt_prod.Value));
		}

		if (!uf_IsNull(div_search.cal_StrdDt.Value))
		{
		    sArg += " FV_WORK_CL=CUR";
		}
		else
		{
		    sArg += " FV_WORK_CL=NOW";
		}

		Dialog("DIS::DISDSM01100.xml", sArg, 1024, 524); 
	}

	if (nCell == 11)   // 정상재고
	{
		if (ds_CurrentDisOut.GetColumn(nRow,"cur_nor_qty") == 0) return;

		// 재고현황상세 화면 팝업
		var sArg  = " FV_POS_AGENCY=" + Quote(div_search.edt_AgencyCd.Value);
		    sArg += " FV_BAD_YN=" + Quote(FV_BAD_CL_NOR);
		    sArg += " FV_STRD_DT=" + Quote(div_search.cal_StrdDt.Value); 
		    sArg += " FV_STRD_YM=" + Quote(ds_DisStrdMth.GetColumn(0,"strd_ym"));
		    sArg += " FV_FROM_DT=" + Quote(addMonth(ds_DisStrdMth.GetColumn(0,"strd_ym") + '01',1));

		if (div_search.cmb_SearchCl.Index == 0) // 보유처별
		{
			sArg += " FV_HLD_PLC_ID=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"hld_plc_id"));			
		}
		else if (div_search.cmb_SearchCl.Index == 1) // 모델별
		{
			sArg += " FV_HLD_PLC_ID=" + Quote(div_search.edt_HldPlcId.Value);
		}

		if (!uf_IsNull(div_search.cmb_DealCoCl.Value))
		{
		    sArg += " FV_DEAL_CO_CL=" + Quote(div_search.cmb_DealCoCl.Value);
		}

		if (!uf_IsNull(div_search.cmb_ProdCl.Value))
		{
		    sArg += " FV_PROD_CL=" + Quote(div_search.cmb_ProdCl.Value);
		}

		if (!uf_IsNull(div_search.edt_MfactId.Value))
		{
		    sArg += " FV_MFACT_ID=" + Quote(div_search.edt_MfactId.Value);
		}

		if (!uf_IsNull(ds_CurrentDisOut.GetColumn(nRow,"prod_cd")))
		{
		    sArg += " FV_PROD_CD=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"prod_cd"));
		}

		if (!uf_IsNull(ds_CurrentDisOut.GetColumn(nRow,"color_cd")))
		{
		    sArg += " FV_COLOR_CD=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"color_cd"));
		}

		if (!uf_IsNull(div_search.cmb_ComMthd.Value))
		{
		    sArg += " FV_COM_MTHD=" + Quote(div_search.cmb_ComMthd.Value);
		}

		if (!uf_IsNull(div_search.cmb_SaleGrp.Value))
		{
		    sArg += " FV_SALE_GRP=" + Quote(div_search.cmb_SaleGrp.Value);
		}

		if (!uf_IsNull(div_search.cmb_ChrgrUser.Value))
		{
		    sArg += " FV_CHRGR_USER_ID=" + Quote(div_search.cmb_ChrgrUser.Value);
		}

		if (!uf_IsNull(div_search.edt_prod.Value))
		{
		    sArg += " FV_PROD_NM=" + Quote(ToUpper(div_search.edt_prod.Value));
		}

		if (!uf_IsNull(div_search.cal_StrdDt.Value))
		{
		    sArg += " FV_WORK_CL=CUR";
		}
		else
		{
		    sArg += " FV_WORK_CL=NOW";
		}

		Dialog("DIS::DISDSM01100.xml", sArg, 1024, 524); 
	}

	if (nCell == 12)   // 불량재고
	{
		if (ds_CurrentDisOut.GetColumn(nRow,"cur_err_qty") == 0) return;

		// 재고현황상세 화면 팝업
		var sArg  = " FV_POS_AGENCY=" + Quote(div_search.edt_AgencyCd.Value);
		    sArg += " FV_BAD_YN=" + Quote(FV_BAD_CL_ERR);
		    sArg += " FV_STRD_DT=" + Quote(div_search.cal_StrdDt.Value); 
		    sArg += " FV_STRD_YM=" + Quote(ds_DisStrdMth.GetColumn(0,"strd_ym"));
		    sArg += " FV_FROM_DT=" + Quote(addMonth(ds_DisStrdMth.GetColumn(0,"strd_ym") + '01',1));

		if (div_search.cmb_SearchCl.Index == 0) // 보유처별
		{
			sArg += " FV_HLD_PLC_ID=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"hld_plc_id"));			
		}
		else if (div_search.cmb_SearchCl.Index == 1) // 모델별
		{
			sArg += " FV_HLD_PLC_ID=" + Quote(div_search.edt_HldPlcId.Value);
		}

		if (!uf_IsNull(div_search.cmb_DealCoCl.Value))
		{
		    sArg += " FV_DEAL_CO_CL=" + Quote(div_search.cmb_DealCoCl.Value);
		}

		if (!uf_IsNull(div_search.cmb_ProdCl.Value))
		{
		    sArg += " FV_PROD_CL=" + Quote(div_search.cmb_ProdCl.Value);
		}

		if (!uf_IsNull(div_search.edt_MfactId.Value))
		{
		    sArg += " FV_MFACT_ID=" + Quote(div_search.edt_MfactId.Value);
		}

		if (!uf_IsNull(ds_CurrentDisOut.GetColumn(nRow,"prod_cd")))
		{
		    sArg += " FV_PROD_CD=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"prod_cd"));
		}

		if (!uf_IsNull(ds_CurrentDisOut.GetColumn(nRow,"color_cd")))
		{
		    sArg += " FV_COLOR_CD=" + Quote(ds_CurrentDisOut.GetColumn(nRow,"color_cd"));
		}

		if (!uf_IsNull(div_search.cmb_ComMthd.Value))
		{
		    sArg += " FV_COM_MTHD=" + Quote(div_search.cmb_ComMthd.Value);
		}

		if (!uf_IsNull(div_search.cmb_SaleGrp.Value))
		{
		    sArg += " FV_SALE_GRP=" + Quote(div_search.cmb_SaleGrp.Value);
		}

		if (!uf_IsNull(div_search.cmb_ChrgrUser.Value))
		{
		    sArg += " FV_CHRGR_USER_ID=" + Quote(div_search.cmb_ChrgrUser.Value);
		}

		if (!uf_IsNull(div_search.edt_prod.Value))
		{
		    sArg += " FV_PROD_NM=" + Quote(ToUpper(div_search.edt_prod.Value));
		}

		if (!uf_IsNull(div_search.cal_StrdDt.Value))
		{
		    sArg += " FV_WORK_CL=CUR";
		}
		else
		{
		    sArg += " FV_WORK_CL=NOW";
		}

		Dialog("DIS::DISDSM01100.xml", sArg, 1024, 524); 
	}	

}

/*******************************************************************************
 * @description    : 현재고현황 OnCharChanged 이벤트
*******************************************************************************/
function div_search_edt_HldPlcNm_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
	{
		div_search.edt_HldPlcId.value = "";
	}	
}

/*******************************************************************************
 * @description    : 현재고현황 OnCharChanged 이벤트
*******************************************************************************/
function div_search_edt_ProdCd_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
	{
		div_search.edt_ProdNm.value = "";
	}		
}

/*******************************************************************************
 * @description    : 현재고현황 OnCharChanged 이벤트
*******************************************************************************/
function div_search_edt_MfactNm_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
	{
		div_search.edt_MfactId.value = "";
	}			
}

/*******************************************************************************
 * @description    : 현재고현황 OnChanged 이벤트
*******************************************************************************/
function div_search_cmb_SearchCl_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
    // 출력 Dataset 초기화
    ds_CurrentDisOut.ClearData();

	if (nNewIndex == 0) // 보유처별
	{
		//칼럼의 사이즈 조절
		grd_CurrentDisList.SetColProp(1,"Width", 150);					
		grd_CurrentDisList.SetColProp(2,"Width", 150);					
		grd_CurrentDisList.SetColProp(3,"Width", 150);					
		grd_CurrentDisList.SetColProp(4,"Width", 150);					

		grd_CurrentDisList.SetColProp(5,"Width", 150);					
		grd_CurrentDisList.SetColProp(6,"Width", 0);					
		grd_CurrentDisList.SetColProp(7,"Width", 0);					
		grd_CurrentDisList.SetColProp(8,"Width", 0);
		grd_CurrentDisList.SetColProp(9,"Width", 0);
	}
	else if (nNewIndex == 1)  // 모델별
	{
		//칼럼의 사이즈 조절
		grd_CurrentDisList.SetColProp(1,"Width", 0);					
		grd_CurrentDisList.SetColProp(2,"Width", 0);					
		grd_CurrentDisList.SetColProp(3,"Width", 0);					
		grd_CurrentDisList.SetColProp(4,"Width", 0);					

		grd_CurrentDisList.SetColProp(5,"Width", 0);					
		grd_CurrentDisList.SetColProp(6,"Width", 200);					
		grd_CurrentDisList.SetColProp(7,"Width", 200);					
		grd_CurrentDisList.SetColProp(8,"Width", 200);
		grd_CurrentDisList.SetColProp(9,"Width", 200);	
	}
}

/*******************************************************************************
 * @description    : 현재고현황 OnKillFocus 이벤트
*******************************************************************************/
function div_search_edt_AgencyNm_OnKillFocus(obj)
{
	if (!uf_IsNull(div_search.edt_AgencyNm.Value))
	{
		http.Sync = true;

		// 영업담당 조회
		f_getChrgrUserIdList();

		http.Sync = false;	

		cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");	
	}
	else
	{
		// 출력 Dataset 초기화
		ds_ChrgrUser.ClearData();

		cf_setInitDataForCmb(div_search.cmb_ChrgrUser,"전체");		
	}
}
