				sArg += " FV_INOUT_MGMT_NO=" + Quote(ds_ProdHstOut.GetColumn(nRow,"inout_mgmt_no"));
				sArg += " FV_INOUT_SEQ=" + Quote(ds_ProdHstOut.GetColumn(nRow,"inout_seq"));
