/*******************************************************************************
 * @description    : 일련번호이력삭제 OnMouseOver 이벤트
*******************************************************************************/
function grd_ProdHstList_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex)
{
	if(nCell == 10 && !uf_IsNull(obj.GetCellValue(nRow, 10)))
	{
		grd_ProdHstList.ToolTipText = "메모 : " + replace(obj.GetCellValue(nRow, 10),"	"," ");	
	}
	else
	{
		grd_ProdHstList.ToolTipText = "";
	}
}

/*******************************************************************************
 * @description    : 일련번호이력삭제 OnColumnChanged 이벤트
*******************************************************************************/
function ds_ProdHstOut_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var iCnt = 0;

	if(strColumnID == "DEL_CH_YN")
	{
		if(varNewValue == 1){

			// 이전 상품 입출고 이력 중 이전 건이 체크 되지 않으면 체크 안됨.
			for(var i=0; i<nRow; i++)
			{
				if(ds_ProdHstOut.getColumn(i, "del_ch_yn") == "0"){
					iCnt++;
				}
			}

			if(iCnt > 0){
				alert("이전 행에 상품입출고 이력 중 체크되지 않은 건이 있습니다.");
				ds_ProdHstOut.SetColumn(nRow, "del_ch_yn", "0");						
			}else{
				ds_ProdHstOut.SetColumn(nRow, "del_ch_yn", "1");						
			}

		}else{	

			// 이후 상품 입출고 이력 중 이전 건이 체크건이 있으면 체크해제 안됨.
			for(var i=nRow+1; i<ds_ProdHstOut.rowcount; i++)
			{
				if(ds_ProdHstOut.getColumn(i, "del_ch_yn") == "1"){
					iCnt++;
				}
			}

			if(iCnt > 0){
				alert("이후 행에 상품입출고 이력 중 체크된 건이 있습니다.");
				ds_ProdHstOut.SetColumn(nRow, "del_ch_yn", "1");						
			}else{
				ds_ProdHstOut.SetColumn(nRow, "del_ch_yn", "0");						
			}

		}
	}
}

/*******************************************************************************
 * @description    : 일련번호이력삭제 OnClick 이벤트
*******************************************************************************/
function btn_save_OnClick(obj)
{
	//저장 전 변경자료여부 확인
	if(ds_ProdHstOut.GetUpdate() == 0){
		alert(cf_getMessage(MSG_00071,"(삭제)")); // 처리할 대상이 없습니다
		return;
	}

	//저장 전 변경자료여부 확인
	if(uf_IsNull(ds_rmks.GetColumn(0,"rmks"))){
		alert("비고 항목을 입력해야 합니다."); 
		return;
	}

	// 마감월 체크
	cf_CheckCloseDay('01',ds_ProdHstOut.GetColumn(0,"inout_dt"),"발생일자"); // 01 : 재고
	if(FV_BOOLEAN_CLOSE) {
		return; // 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.        
	}	

	// 트랜잭션 설정
	var sSvcID = "svcProdHstDelete";
	var sInDs = "ds_ProdHstOut=ds_ProdHstOut:U ds_rmks=ds_rmks:U";
	var sOutDs = "";
	var sArg = "sktst.dis.DSM#deleteProdInoutHst";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);		
}

/********************************************************************************
 *  재고 상태 변경 버튼 클릭시 처리
 ********************************************************************************/
function btn_change_OnClick(obj)
{
 if(ds_ProdHstOut.RowCount() < 1){
	alert("재고 상태 변경 가능한 정보가 없습니다."); 
    return;
 }
         var hldPlcId = ds_ProdHstOut.GetColumn(0,"in_plc_id");
         var hldPlcNm = ds_ProdHstOut.GetColumn(0,"in_plc_nm");
         if(Trim(hldPlcId) == "" || hldPlcId == null){
            hldPlcId = ds_ProdHstOut.GetColumn(0,"out_plc_id");
            hldPlcNm = ds_ProdHstOut.GetColumn(0,"out_plc_nm");
         }

		 var sArg  = " FV_PROD_CD="       + Quote(ds_ProdInfo.GetColumn(0,"prod_cd"));
		     sArg += " FV_PROD_NM="       + Quote(ds_ProdInfo.GetColumn(0,"prod_nm"));
     		 sArg += " FV_COLOR_CD="      + Quote(ds_ProdInfo.GetColumn(0,"color_cd"));
     		 sArg += " FV_COLOR_NM="      + Quote(ds_ProdInfo.GetColumn(0,"color_nm"));
			 sArg += " FV_SER_NUM="       + Quote(ds_ProdInfo.GetColumn(0,"ser_num"));
			 sArg += " FV_MFACT_ID="      + Quote(ds_ProdInfo.GetColumn(0,"mfact_id"));
			 sArg += " FV_MFACT_NM="      + Quote(ds_ProdInfo.GetColumn(0,"mfact_nm"));
			 sArg += " FV_POS_AGENCY="    + Quote(ds_ProdInfo.GetColumn(0,"pos_agency"));
			 sArg += " FV_POS_AGENCY_NM=" + Quote(ds_ProdInfo.GetColumn(0,"pos_agency_nm"));
			 sArg += " FV_BAD_YN="        + Quote(ds_ProdInfo.GetColumn(0,"bad_yn"));
			 sArg += " FV_BAD_NM="        + Quote(ds_ProdInfo.GetColumn(0,"bad_yn_nm"));
			 sArg += " FV_DIS_ST="        + Quote(ds_ProdInfo.GetColumn(0,"dis_st"));
			 sArg += " FV_DIS_ST_NM="     + Quote(ds_ProdInfo.GetColumn(0,"dis_st_nm"));
			 sArg += " FV_HLD_PLC_ID="    + Quote(ds_ProdInfo.GetColumn(0,"hld_plc_id"));
			 sArg += " FV_HLD_PLC_NM="    + Quote(ds_ProdInfo.GetColumn(0,"hld_plc_nm"));

			 /*  변경후 정보 */
			 sArg += " FV_INOUT_DT="          + Quote(ds_ProdHstOut.GetColumn(0,"inout_dt"));
			 sArg += " FV_INOUT_CL="          + Quote(ds_ProdHstOut.GetColumn(0,"inout_cl"));
			 sArg += " FV_INOUT_DTL_CL="      + Quote(ds_ProdHstOut.GetColumn(0,"inout_dtl_cl"));
			 sArg += " FV_NEW_POS_AGENCY="    + Quote(ds_ProdHstOut.GetColumn(0,"in_pos_agency"));
			 sArg += " FV_NEW_POS_AGENCY_NM=" + Quote(ds_ProdHstOut.GetColumn(0,"in_pos_agency_nm"));
			 sArg += " FV_NEW_HLD_PLC_ID="    + Quote(hldPlcId);
			 sArg += " FV_NEW_HLD_PLC_NM="    + Quote(hldPlcNm);

		Dialog("DIS::DISDSM03810.xml", sArg, 830, 520);
}
