/*******************************************************************************
 * @description    : 상담내역 저장
*******************************************************************************/
function btn_save_OnClick(obj)
{

	if ( !lf_saveValidationCheck() ) {
        return;
    }
    ds_consult_m.SetColumn(0, "COLOR_CD", 		div_conInfo2.cmb_color.value);
	ds_consult_m.SetColumn(0, "EQP_ST", 		div_conInfo2.cmb_rate.text);
	FV_RATE_CD = ds_consult_m.GetColumn(0,"EQP_ST");

//    trace( " new EQP_ST  ======== " + ds_consult_m.GetColumn(0,"EQP_ST"));

 	var sSvcID = "saveConsultMgmt";
	var sInDs  = "ds_consult_m=ds_consult_m ds_consult_d=ds_consult_d";
	var sOutDs = "";
	var sArg   = "sktst.cst.ADV#saveConsultMgmt";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

	return;
}

/*******************************************************************************
 * @description    :  상담등록 저장시 validation check
*******************************************************************************/
function lf_saveValidationCheck()
{
    var bFlag = true;

    if ( uf_IsNull(div_conInfo1.edt_agencyNm.Text) || uf_IsNull(div_conInfo1.edt_agency.Text) ) {
		alert("대리점을 입력해 주십시요.");
		return false;
	}

    if ( uf_IsNull(div_conInfo1.edt_dealNm.Text) || uf_IsNull(div_conInfo1.edt_dealCd.Text) ) {
		alert("상담처를 입력해 주십시요.");
		return false;
	}

    return bFlag;
}

/*******************************************************************************
 * @description : FULLSET checkbox 클릭시
 ******************************************************************************/ 
function div_eqpSalePrc_cmb_fnshYn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strText == "Y"){
	    f_setSalePrc();
	} else {
	    f_unSetSalePrc();
	}
}

/*******************************************************************************
 * @description : 판매가 계산 처리
 ******************************************************************************/ 
function f_setSalePrc()
{
    var iEqpPrc  = div_eqpSalePrc.msk_eqpPrchsPrc.Value;
    var iCnptPrc = div_eqpSalePrc.msk_eqpPrchsPrc.Value;
    var iSalePrc = 0;

    /*    
    var sSalePol   = div_eqpSalePrc.rdo_salePol.value;
    //
    if (sSalePol == "") {

    } else {

    }
    */

    iSalePrc = iEqpPrc + iCnptPrc;

    div_eqpSalePrc.msk_salePrc.value    = iSalePrc;
    div_eqpSalePrc.msk_fixSalePrc.value = iSalePrc;
}

/*******************************************************************************
 * @description : 판매가 초기화
 ******************************************************************************/ 
function f_unSetSalePrc()
{
    div_eqpSalePrc.msk_salePrc.Value    = "";
    div_eqpSalePrc.msk_fixSalePrc.Value = "";
}

