/*******************************************************************************
* 함수명 : POLBOP00100_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function POLBOP00100_OnLoadCompleted(obj)
{
	cf_setGrdChecknSortEvent(grd_ipolGnrlMgmt);	// 그리드 정렬
	cf_setGrdChecknSortEvent(grd_aplyModel);	// 그리드 정렬
    http.Sync = true;
	// 권한 설정
	cf_setAuth(pFrmNum);
    // 세션정보 취득.
    FV_ORG_ID = gds_session.GetColumn(0,"orgId");
    FV_ORG_CL = gds_session.GetColumn(0,"orgCl");	

	if(ds_condition.GetRowCount() < 1)
	{
		ds_condition.AddRow();
	}

    ds_condition.setColumn(0, "ORG_ID", gds_session.GetColumn(0, "posAgencyId"));
	uf_enableAgencyByAuth("div_search", "edt_agencyCdNm,edt_agencyCd,btn_agency");

	cf_setCommonCodeDsEnv();
	//cf_setCommonCode("div_search.cmb_polOper", "ZPOL_C_00290", "전체");
	//cf_setCommonCode("div_search.cmb_polSubj", "ZPOL_C_00300", "전체");
	cf_setCommonCode("ds_polOper", "ZPOL_C_00290");   //정책운영
	cf_setCommonCode("ds_polSubj", "ZPOL_C_00300");   //정책주관
	cf_setCommonCode("ds_prMnyCl", "ZPOL_C_00520");   //일반장려금정책구분
	cf_setCommonCode("div_search.cmb_prMnyCl", "ZPOL_C_00520", "선택");   //일반장려금구분코드(정책구분)
	cf_checkCommonCode();

	//ds_condition.SetColumn(0,"POL_YM",POL_YM);  //적용년월

	df_search();

	f_modUserSet();
	div_search.cmb_modUser.index = 0;   //작성자

	http.Sync = false;
	//div_search.cmb_polOper.index = 0;  //정책운영
	//div_search.cmb_polSubj.index = 0;  //정책주관
	div_search.cmb_prMnyCl.Index = 0;    //정책구분
}


/*******************************************************************************
* 함수명 : df_reSet
* 기능명 : 화면 초기화
*******************************************************************************/
function df_reSet(sArg)
{
	// 권한 설정
	cf_setAuth(pFrmNum);
    // 세션정보 취득.
    FV_ORG_ID = gds_session.GetColumn(0,"orgId");
    FV_ORG_CL = gds_session.GetColumn(0,"orgCl");	

	//cf_setCommonCodeDsEnv();
	//cf_setCommonCode("div_search.cmb_polOper", "ZPOL_C_00290");
	//cf_setCommonCode("div_search.cmb_polSubj", "ZPOL_C_00300", "전체");
	//cf_setCommonCode("ds_polOper", "ZPOL_C_00290");   //정책운영
	//cf_setCommonCode("ds_polSubj", "ZPOL_C_00300");   //정책주관
	//cf_checkCommonCode();

    http.Sync = true;
	df_search(sArg);
	//ds_polOper.setColumn(0, "COMM_CD_VAL", "");
	ds_polOper.setColumn(0, "COMM_CD_VAL_NM", "전체");
	div_search.cmb_polOper.Index = 0;    //정책운영
	div_search.cmb_polSubj.Index = 0;    //정책주관
	div_search.cmb_prMnyCl.Index = 0;    //정책구분

	f_modUserSet();
	div_search.cmb_modUser.index = 0;   //작성자

	http.Sync = false;
}

/*******************************************************************************
* 함수명 : f_modUserSet
* 기능명 : 작성자 세팅
*******************************************************************************/
function f_modUserSet()
{
    ds_modUser.ClearData();
	ds_modUser.AddRow();
	ds_modUser.SetColumn(ds_modUser.lastrow,"MOD_USER_ID","");
	ds_modUser.SetColumn(ds_modUser.lastrow,"MOD_USER_NM","전체");
	var nExistRow = "";

	for( var idx =0 ; idx<=ds_ipolGnrlMgmt.RowCount()-1; idx++ ){

		nExistRow = ds_modUser.FindRow("MOD_USER_ID",ds_ipolGnrlMgmt.GetColumn(idx,"MOD_USER_ID"));
		if( nExistRow == -1 ){
		//alert("새로 작성");
			ds_modUser.AddRow();
		    ds_modUser.SetColumn(ds_modUser.lastrow,"MOD_USER_ID",ds_ipolGnrlMgmt.GetColumn(idx,"MOD_USER_ID"));
		    ds_modUser.SetColumn(ds_modUser.lastrow,"MOD_USER_NM",ds_ipolGnrlMgmt.GetColumn(idx,"MOD_USER_NM"));
		}

	}
	//ds_modUser.Sort("MOD_USER_NM");

}




/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcSearch")
	{
		ds_aplyModel.ClearData();
		ds_objtModel.ClearData();

	}
}


/*******************************************************************************
* 함수명 : f_lastYnSet
* 기능명 : 정책 리스트 중 최종 이력 유무를 세팅
           (상세 페이지에서 최종 이력만 갱신, 삭제 가능함. 그외는 수정만 가능함)
*******************************************************************************/
function f_lastYnSet()
{

	var	iCnt = 1;
	ds_org.clearData();
	ds_org.addRow();
	ds_org.setColumn(0, "COMM_CD_VAL", "");
	ds_org.setColumn(0, "COMM_CD_VAL_NM", "전체");
	for( var idx =0 ; idx<=ds_ipolGnrlMgmt.RowCount(); idx++ ){
		if(ds_ipolGnrlMgmt.GetColumn(idx,"IPOL_ID") != ds_ipolGnrlMgmt.GetColumn(idx+1,"IPOL_ID")){
		    ds_ipolGnrlMgmt.SetColumn(idx,"LAST_YN","Y");
		} else {
		    ds_ipolGnrlMgmt.SetColumn(idx,"LAST_YN","N");
		}
		if(idx == ds_ipolGnrlMgmt.RowCount()) {
		    ds_ipolGnrlMgmt.SetColumn(idx,"LAST_YN","Y");
		}
		if(idx < ds_ipolGnrlMgmt.RowCount()){
			if(ds_ipolGnrlMgmt.GetColumn(idx,"MOD_DVS") != ds_ipolGnrlMgmt.GetColumn(idx-1,"MOD_DVS")){
				ds_org.addRow();
				ds_org.setColumn(icnt, "COMM_CD_VAL", ds_ipolGnrlMgmt.GetColumn(idx,"MOD_DVS"));
				ds_org.setColumn(icnt, "COMM_CD_VAL_NM", ds_ipolGnrlMgmt.GetColumn(idx,"MOD_DVS_NM"));
				icnt++;
			}
		}

	}


}




