var FV_STR_NAME; // 사용자 이름
var FV_ORG_ID;		// 소속
var FV_ORG_CL;		// 소속구분

/*******************************************************************************
* 함수명 : POLMSP00210_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function POLMSP00210_OnLoadCompleted(obj)
{
	cf_setGrdChecknSortEvent(grd_polSplst);	// 그리드 정렬
	// 권한 설정
	cf_setAuth(pFrmNum);

	FV_ORG_ID     = gds_session.GetColumn(0, "orgid");
	FV_ORG_CL     = gds_session.GetColumn(0, "orgcl");

	http.Sync = true;
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_search.cmb_slNetCd", "ZPOL_C_00105", "전체");   		//영업망
	cf_setCommonCode("div_search.cmb_dsNetCd", "ZPOL_C_00110", "전체"); 		//유통망
	cf_setCommonCode("div_search.cmb_salePtn", "ZPOL_C_00020", "전체"); 		//판매유형
	cf_setCommonCode("div_process.cmb_prodChrtic1", "ZBAS_C_00020", "전체"); 	//상품특성1
	cf_setCommonCode("div_process.cmb_prodChrtic2", "ZBAS_C_00030", "전체"); 	//상품특성2
	cf_setCommonCode("grd_polSplst", "ZPOL_C_00340", null, 9); 					//승인상태
	cf_checkCommonCode();

	ds_adptProdMgmt.Copy(parent.ds_CopyAdptProdMgmt);

	f_setTotalCount(ds_prodMgmt.RowCount(), ds_adptProdMgmt.RowCount());

	div_search.cal_polYm.Value = substr(Today(),0,6);

	cf_copyDataset(ds_zPolC, parent.ds_zPolC, null, true);

	if(uf_isNull(OPLST_ID) == true){ //신규버튼 클릭시
		uf_ObjComponentEnable(div_search,true);
	}else{
		uf_ObjComponentEnable(div_search,false);
	}

	uf_enableAgencyByAuth("div_search", "edt_agencyCdNm,edt_agencyCd,btn_agency","edt_agencyUkeyCd");

	http.Sync = false;	
}

/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	if(sSvcID == "svcSearch"){

		return;

	}else if(sSvcID == "svcSearchProdMdlList"){

		ds_prodMgmt.AddColumn("chk");
		ds_adptProdMgmt.AddColumn("chk");

		f_setTotalCount(ds_prodMgmt.RowCount(), ds_adptProdMgmt.RowCount());

		if(ds_adptProdMgmt.rowcount > 0){
			ds_adptProdMgmt.row = 0;
			ds_adptProdMgmt.SelectRow(0,true);
			ds_adptProdMgmt.SelectRow(ds_adptProdMgmt.lastrow,false);
		}	

		grd_adptProdMgmt.Redraw = true;

		return;

	}else if(sSvcID == "svcGetCopyProdMgmtList"){

		f_setTotalCount(ds_prodMgmt.RowCount(), ds_adptProdMgmt.RowCount());

		df_mdl_search();

		grd_prodMgmt.Redraw = true;
		grd_adptProdMgmt.Redraw = true;		

		return;

	}else if(sSvcID == "svcInsertPolSplstTmp"){

		if(parent.ds_copyPolSplstTmp.rowCount == 0 && NEW_FLAG == "N"){ //(최초 변경시 &&  기존가격표 일때) 에만 부모데이터 만 카피하기위해. 
			parent.ds_copyPolSplstTmp.Copy(parent.ds_polSplstTmp); //부모 데이터셋 Copy
			parent.ds_polSplstTmp.clearData();
		}

		parent.ds_condition.SetColumn(0, "TPLST_ID", ds_tempKey.GetColumn(0, "TPLST_ID"));

		this.close("1");
		return;

	}else if(sSvcID == "svcSearchSaleTypCdMin"){

		return;

	}

}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search(sButtonID)
{

	if(uf_isNull(ds_condition.GetColumn(0,"ORG_ID")) == true){
		alert(cf_getMessage(MSG_00135, "대리점"));
		div_search_btn_agency_OnClick();
		return false; 
	}

	var sSvcID = "svcSearch";
	var sInDs = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_polSplst=ds_polSplst";
	var sArg = "sktst.pol.MSP#getPolSplst";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

}

/*******************************************************************************
* 함수명 : df_mdl_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_mdl_search(sButtonID)
{
	grd_adptProdMgmt.Redraw = false;

	if(sButtonID == "btn_getMdl"){ //적용모델 선택 조회버튼 클릭시
		f_getMdlParentDataSet();
	}

	ds_condition.SetColumn(0,"POL_YM",APLY_YM);
	ds_condition.SetColumn(0,"APLY_STA_DTM",APLY_STA_DTM);
	ds_condition.SetColumn(0,"HOUR",HOUR);
	ds_condition.SetColumn(0,"MIN",MIN);

	var sSvcID = "svcSearchProdMdlList";
	var sInDs = "nc_input_fieldDs=ds_condition ds_adptProdMgmt=ds_adptProdMgmt";
	var sOutDs = "ds_prodMgmt=ds_prodMgmt";
	var sArg = "sktst.pol.MSP#getProdMgmtList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
* 함수명 : btn_confirm_OnClick
* 기능명 : 확인버튼
*******************************************************************************/
function btn_confirm_OnClick(obj)
{
	if(ds_adptProdMgmt.rowcount == 0){
		alert(cf_getMessage(MSG_00134, "적용모델"));
		return;
	}

	if(confirm(cf_getMessage(MSG_00136, "적용된 모델을 M채널가격표에 등록")) == 1){

		f_setPolSplstTmp();

	}
}

/*******************************************************************************
* 함수명 : df_close
* 기능명 : 화면 종료
*******************************************************************************/
function btn_cancel_OnClick(sButtonID)
{
	this.close("0");
}

/*******************************************************************************
* 함수명 : f_setPolSplstTmp
* 기능명 : Temp 테이블에 단말기 등록
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function f_setPolSplstTmp()
{
	ds_condition.SetColumn(0,"POL_YM",APLY_YM);
	ds_condition.SetColumn(0,"APLY_YM",APLY_YM);
	ds_condition.SetColumn(0,"SALE_TYP_CD_MAX",SALE_TYP_CD_MAX);
	ds_condition.SetColumn(0,"ORG_ID",ORG_ID);
	ds_condition.SetColumn(0,"SL_NET_CD",SL_NET_CD);
	ds_condition.SetColumn(0,"DS_NET_CD",DS_NET_CD);
	ds_condition.SetColumn(0,"AGRMT_PRD_CD",AGRMT_PRD_CD);

	var sSvcID = "svcInsertPolSplstTmp";
	var sInDs = "ds_condition=ds_condition ds_adptProdMgmt=ds_adptProdMgmt ds_zPolC=ds_zPolC";
	var sOutDs = "ds_tempKey=ds_tempKey";
	var sArg = "sktst.pol.MSP#addPolSplstTmp";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}

/*******************************************************************************
* 함수명 : grd_prodMgmt_OnCellDblClick
* 기능명 :  대상모델 OnCellDblClick 이벤트 발생
*******************************************************************************/
function grd_prodMgmt_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var idx=0; idx<ds_prodMgmt.rowcount; idx++ ){		
		if(ds_prodMgmt.GetColumn(idx, "PROD_CD") == ds_adptProdMgmt.GetColumn(nRow, "PROD_CD") ){
			return;
		}
    }

    uf_MoveShuttle(ds_prodMgmt, ds_adptProdMgmt);    	
    ds_adptProdMgmt.Sort("PROD_CD"); 
}

/*******************************************************************************
* 함수명 : grd_adptProdMgmt_OnCellDblClick
* 기능명 :  적용모델 OnCellDblClick 이벤트 발생
*******************************************************************************/
function grd_adptProdMgmt_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var idx=0; idx<ds_adptProdMgmt.rowcount; idx++ ){		
		if(ds_adptProdMgmt.GetColumn(idx, "PROD_CD") == ds_prodMgmt.GetColumn(nRow, "PROD_CD") ){
			return;
		}
    }

    uf_MoveShuttle(ds_adptProdMgmt, ds_prodMgmt);    	
    ds_prodMgmt.Sort("PROD_CD"); 
}

/*******************************************************************************
* 함수명 : btn_suttleRight_OnClick
* 기능명 :  좌측버튼이벤트 발생
*******************************************************************************/
function btn_suttleRight_OnClick(obj)
{
	if(ds_prodMgmt.currow == -1 ){
		alert(cf_getMessage(MSG_00134, "대상모델"));
		return;
	}

	grd_prodMgmt.Redraw = false;
	grd_adptProdMgmt.Redraw  = false;

	var sHead = grd_prodMgmt.GetCellProp("head",0,"text");
	var nTotCnt = ds_prodMgmt.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_prodMgmt.GetColumn(nRow,"CHK");
	   }else{
			bCheck = ds_prodMgmt.GetColumn(nRow,"CHK");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_prodMgmt.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_adptProdMgmt.AddRow();
			ds_adptProdMgmt.CopyRow(ds_adptProdMgmt.lastrow,ds_prodMgmt,nRow);
			ds_adptProdMgmt.SetColumn(ds_adptProdMgmt.lastrow, "CHK", "0");
			ds_prodMgmt.DeleteRow(nRow);
	   }
	}

	ds_adptProdMgmt.Sort("PROD_CD");
	grd_prodMgmt.SetCellProp("head",0,"text","0");

	grd_prodMgmt.Redraw = true;
	grd_adptProdMgmt.Redraw  = true;
}

