/*******************************************************************************
* 함수명 : grd_polSplst_OnCellDblClick
* 기능명 :  참조가격표 리스트 OnCellDblClick 발생
*******************************************************************************/
function grd_polSplst_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var oGrdDs = object(obj.BindDataset);

	if(confirm(cf_getMessage(MSG_00136, "등록된 M채널가격표를 이관")) == 1){

		var copySucc = "N";

		http.Sync = true;

		f_getSaleTypCdMin(oGrdDs,nRow);

		http.Sync = false;

		if(oGrdDs.GetColumn(nRow, "SL_NET_CD") != SL_NET_CD){
			cf_copyDataset(ds_zPolC, parent.ds_zPolC, null, true);
			alert(cf_getMessage(MSG_00138,"영업망이 일치하지 않은 참조가격표 "));
			return false;
		}

		if(oGrdDs.GetColumn(nRow, "DS_NET_CD") != DS_NET_CD){
			cf_copyDataset(ds_zPolC, parent.ds_zPolC, null, true);
			alert(cf_getMessage(MSG_00138,"유통망이 일치하지 않은 참조가격표 "));
			return false;
		}		

		if(oGrdDs.GetColumn(nRow, "SALE_TYP_CD_MAX") != SALE_TYP_CD_MAX){
			cf_copyDataset(ds_zPolC, parent.ds_zPolC, null, true); 
			alert(cf_getMessage(MSG_00138,"판매유형이 일치하지 않은 참조가격표 "));
			return false;
		}

		if(ds_zPolC.rowcount == parent.ds_zPolC.rowcount){ //판매유형 row 개수 일치

			for( var nRow=0; nRow < parent.ds_zPolC.rowcount; nRow++ ){

				var findRow = ds_zPolC.FindRow("COMM_CD_VAL",parent.ds_zPolC.GetColumn(nRow,"COMM_CD_VAL"));

				if(findRow >= 0){
					copySucc = "Y";	
				}else{
					copySucc = "N";	
					break;
				}
			}

		}

		if(copySucc == "N"){
			cf_copyDataset(ds_zPolC, parent.ds_zPolC, null, true); //판매유형 원상복귀
			alert("이관할 수 없는 참조가격표 입니다.");
			return false;
		}else{

			var sArg = "";
				sArg  = " COPY_FLAG=" + quote("Y");
				sArg += " NEW_FLAG=" + quote("Y");
				sArg += " EDIT_FLAG=" + quote("N");
				sArg += " ALLOT_SALE_PRC_ADJ=" + quote(oGrdDs.getColumn(nRow,"ALLOT_SALE_PRC_ADJ"));

				parent.ds_copyCondition.clearData();
				parent.ds_copyCondition.AddRow();

				parent.ds_copyCondition.SetColumn(0, "OPLST_ID",   		oGrdDs.GetColumn(nRow, "OPLST_ID"));
				parent.ds_copyCondition.SetColumn(0, "APLY_YM",   		oGrdDs.GetColumn(nRow, "APLY_YM"));
				parent.ds_copyCondition.SetColumn(0, "ORG_ID",   		oGrdDs.GetColumn(nRow, "ORG_ID"));
				parent.ds_copyCondition.SetColumn(0, "SL_NET_CD",   	oGrdDs.GetColumn(nRow, "SL_NET_CD"));
				parent.ds_copyCondition.SetColumn(0, "DS_NET_CD",   	oGrdDs.GetColumn(nRow, "DS_NET_CD"));
				parent.ds_copyCondition.SetColumn(0, "AGRMT_PRD_CD",   	oGrdDs.GetColumn(nRow, "AGRMT_PRD_CD"));
				parent.ds_copyCondition.SetColumn(0, "SALE_TYP_CD_MAX", oGrdDs.GetColumn(nRow, "SALE_TYP_CD_MAX"));
				parent.ds_copyCondition.SetColumn(0, "APLY_STA_DTM",   	oGrdDs.GetColumn(nRow, "APLY_STA_DTM"));
				parent.ds_copyCondition.SetColumn(0, "APLY_END_DTM",   	oGrdDs.GetColumn(nRow, "APLY_END_DTM"));

				gf_openWindow("100000617", sArg);

				this.close();

		}

	}else{

		grd_prodMgmt.Redraw = false;
		grd_adptProdMgmt.Redraw = false;

		ds_copyCondition.ClearData();
		ds_adptProdMgmt.ClearData();
		ds_copyCondition.AddRow();

		ds_copyCondition.SetColumn(0, "OPLST_ID",   		oGrdDs.GetColumn(nRow, "OPLST_ID"));
		ds_copyCondition.SetColumn(0, "APLY_YM",   			oGrdDs.GetColumn(nRow, "APLY_YM"));
		ds_copyCondition.SetColumn(0, "ORG_ID",   			oGrdDs.GetColumn(nRow, "ORG_ID"));
		ds_copyCondition.SetColumn(0, "SL_NET_CD",   		oGrdDs.GetColumn(nRow, "SL_NET_CD"));
		ds_copyCondition.SetColumn(0, "DS_NET_CD",   		oGrdDs.GetColumn(nRow, "DS_NET_CD"));
		ds_copyCondition.SetColumn(0, "AGRMT_PRD_CD",   	oGrdDs.GetColumn(nRow, "AGRMT_PRD_CD"));
		ds_copyCondition.SetColumn(0, "SALE_TYP_CD_MAX", 	oGrdDs.GetColumn(nRow, "SALE_TYP_CD_MAX"));

		ds_copyCondition.SetColumn(0, "POL_YM", 			APLY_YM);
		ds_copyCondition.SetColumn(0, "APLY_STA_DTM", 		APLY_STA_DTM);
		ds_copyCondition.SetColumn(0, "HOUR", 				HOUR);
		ds_copyCondition.SetColumn(0, "MIN", 				MIN);

		var sSvcID = "svcGetCopyProdMgmtList";
		var sInDs = "nc_input_fieldDs=ds_copyCondition";
		var sOutDs = "ds_adptProdMgmt=ds_adptProdMgmt";
		var sArg = "sktst.pol.MSP#getCopyProdMgmtList";

		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

	}
}

/*******************************************************************************
* 함수명 : f_getMdlParentDataSet
* 기능명 :  M채널가격표의 모델 DataSet Copy 
*******************************************************************************/
function f_getMdlParentDataSet()
{
	ds_adptProdMgmt.ClearData();

	if(parent.ds_polSplstTmp.GetRowCount() > 0){

		for( var nRow=0; nRow < parent.ds_polSplstTmp.rowcount; nRow++ ){

			ds_adptProdMgmt.AddRow();
			ds_adptProdMgmt.SetColumn(ds_adptProdMgmt.lastrow,"PROD_CD",  	   	parent.ds_polSplstTmp.GetColumn(nRow,"PROD_CD"));
			ds_adptProdMgmt.SetColumn(ds_adptProdMgmt.lastrow,"DEAL_CO_NM", 	parent.ds_polSplstTmp.GetColumn(nRow,"DEAL_CO_NM"));
			ds_adptProdMgmt.SetColumn(ds_adptProdMgmt.lastrow,"PROD_NM",  	   	parent.ds_polSplstTmp.GetColumn(nRow,"PROD_NM"));
			ds_adptProdMgmt.SetColumn(ds_adptProdMgmt.lastrow,"PROD_CHRTIC_1",  parent.ds_polSplstTmp.GetColumn(nRow,"PROD_CHRTIC_1"));
			ds_adptProdMgmt.SetColumn(ds_adptProdMgmt.lastrow,"PROD_CHRTIC_2",  parent.ds_polSplstTmp.GetColumn(nRow,"PROD_CHRTIC_2"));

		}
	}

}

/*******************************************************************************
* 함수명 : f_setTotalCount
* 기능명 :  데이터 건수 Display  
*******************************************************************************/
function f_setTotalCount(stLeftCount, stRightCount) {
	st_leftTotCount.Text = stLeftCount + " 건";
	st_rightTotCount.Text  = stRightCount + " 건";
}

