var FV_STR_NAME; // 사용자 이름
var PRE_FIX_STRD_SALE_PRC = 0;
var PRE_APLY_STA_DTM = ""; 
var PRE_APLY_END_DTM = "";
var	MOD_YMD = "";
var	MOD_YMD_END = "";
var	CHK_ORG = "";  //조직정보 체크유무

/*******************************************************************************
* 함수명 : POLOAP00600_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function POLOAP00600_OnLoadCompleted(obj)
{	

	// 권한 설정
	cf_setAuth(pFrmNum);

    //cf_setGrdChecknSortEvent(tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc);	// 대상판매처 그리드 정렬
    //cf_setGrdChecknSortEvent(tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc);	// 적용판매처 그리드 정렬

    http.Sync = true;
	f_setCmCode();

	df_search();
    CHK_ORG = "";
	http.Sync = false;

}

/*******************************************************************************
* 함수명 : df_reSet
* 기능명 : 화면 초기화
*******************************************************************************/
function df_reSet(sArg)
{
	// 권한 설정
	cf_setAuth(pFrmNum);

    http.Sync = true;
	df_search(sArg);
    CHK_ORG = "";
	http.Sync = false;
}


function f_setCmCode()
{
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();
	//cf_setCommonCode("div_search.cmb_polOper", "ZPOL_C_00290");   //정책운영
	//cf_setCommonCode("ds_polSubj", "ZPOL_C_00300");   //정책주관
	//ds_polSubj.SetColumn(0,"COMM_CD_VAL","선택");  //정책주관
	//ds_polSubj.SetColumn(0,"COMM_CD_VAL_NM","");
	cf_setCommonCode("ds_prodChrtic1", "ZBAS_C_00020");   //상품특성1
	cf_setCommonCode("ds_prodChrtic2", "ZBAS_C_00030");   //상품특성2

	cf_setCommonCode("tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1", "ZBAS_C_00020", "전체");  //상품특성1
	cf_setCommonCode("tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic2", "ZBAS_C_00030", "전체");  //상품특성2
	cf_setCommonCode("ds_saleTypCdMax", "ZPOL_C_00020");  //판매유형코드 대
	cf_setCommonCode("ds_saleTypCdMid", "ZPOL_C_00030");  //판매유형코드 중
	cf_setCommonCode("ds_saleTypCdMin", "ZPOL_C_00040");  //판매유형코드 소
	//cf_setCommonCode("ds_agrmtPrd", "ZPOL_C_00230");  //약정기간코드	
	cf_setCommonCode("ds_qtyEstMthd", "ZPOL_C_00440");  //수량산정방식코드	
	cf_setCommonCode("ds_itvlAplyMthd", "ZPOL_C_00450");  //구간적용방식코드	
	cf_setCommonCode("ds_strdClCd", "ZPOL_C_00180");  //구간구분코드	
	cf_setCommonCode("ds_payInfoTypCd", "ZPOL_C_00140");  //지급방식코드	



	// 설정된 코드를 기반으로 공통 코드를 추가 및 데이타 바인딩
	cf_checkCommonCode();

}


function f_setCode()
{
	//ds_mfact.SetColumn(0,"DEAL_CO_NM","전체");  //제조사
	//ds_mfact.SetColumn(0,"DEAL_CO_CD","");
	tab_addSaleCmms.tab_prodInfo.div_process.cmb_mfact.index = 0;   //제조사
	tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1.index = 0;  //상품특성1
	tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic2.index = 0;  //상품특성2
	tab_addSaleCmms.tab_prodInfo.div_process.cmb_sktOperYn.index = 0;   //SK운영
	//tab_addSaleCmms.tab_prodInfo.div_process.rdo_mdlAdpt.index = 0;     //전모델적용




	var chkYn = true;

	if(uf_isNull(ds_addSaleCmms.GetColumn(0,"APOL_ID"))== false) {   //리스트 클릭시 수정모드

		ds_condition.SetColumn(0,"POL_YM",ds_addSaleCmms.GetColumn(0,"POL_YM"));  //적용년월
		//div_search.cmb_year.Value = substr(ds_addSaleCmms.GetColumn(0,"POL_YM"),0,4);  //적용년
		//div_search.cmb_month.Value = substr(ds_addSaleCmms.GetColumn(0,"POL_YM"),4,2);  //적용월

		if(uf_isNull(PRE_APLY_STA_DTM)== true && uf_isNull(MOD_YMD)== true) {   //적용시작일시를 안바꿨을때..
			ds_condition.SetColumn(0,"APLY_STA_DTM",substr(ds_addSaleCmms.GetColumn(0,"APLY_STA_DTM"),0,8));  //적용시작년월일
			ds_condition.SetColumn(0,"HOUR",substr(ds_addSaleCmms.GetColumn(0,"APLY_STA_DTM"),8,2));  //적용시작시
			ds_condition.SetColumn(0,"MIN",substr(ds_addSaleCmms.GetColumn(0,"APLY_STA_DTM"),10,2));  //적용시작분

			ds_condition.SetColumn(0,"APLY_END_DTM",substr(ds_addSaleCmms.GetColumn(0,"APLY_END_DTM"),0,8));  //적용종료년월일
			ds_condition.SetColumn(0,"END_HOUR",substr(ds_addSaleCmms.GetColumn(0,"APLY_END_DTM"),8,2));  //적용종료시
			ds_condition.SetColumn(0,"END_MIN",substr(ds_addSaleCmms.GetColumn(0,"APLY_END_DTM"),10,2));  //적용종료분

			//초기 적용시작일시
			PRE_APLY_STA_DTM = div_search.cal_aplyStaDtm.Value+Right("0"+div_search.edt_hour.Text,2)+Right("0"+div_search.edt_min.Text,2);
			//초기 적용종료일시
			PRE_APLY_END_DTM = div_search.cal_aplyEndDtm.Value+Right("0"+div_search.edt_Endhour.Text,2)+Right("0"+div_search.edt_EndMin.Text,2);
		} else {
			ds_condition.SetColumn(0,"APLY_STA_DTM",substr(MOD_YMD,0,8));  //적용시작년월일
			ds_condition.SetColumn(0,"HOUR",substr(MOD_YMD,8,2));  //적용시작시
			ds_condition.SetColumn(0,"MIN",substr(MOD_YMD,10,2));  //적용시작분

			ds_condition.SetColumn(0,"APLY_END_DTM",substr(MOD_YMD_END,0,8));  //적용종료년월일
			ds_condition.SetColumn(0,"END_HOUR",substr(MOD_YMD_END,8,2));  //적용종료시
			ds_condition.SetColumn(0,"END_MIN",substr(MOD_YMD_END,10,2));  //적용종료분

			//alert(ds_gnrlPrMny.GetColumn(0,"APLY_STA_DTM"));
			PRE_APLY_STA_DTM = ds_addSaleCmms.GetColumn(0,"APLY_STA_DTM");
			PRE_APLY_END_DTM = ds_addSaleCmms.GetColumn(0,"APLY_END_DTM");
			MOD_YMD = "";
			MOD_YMD_END = "";
		}

		//var chkYn = true;
		if ( ds_condition.getColumn(0,"LAST_YN") == "N" ) {  //해당 정책건에 대한 최종이력이 아니면...
			 chkYn = false;
		} else {
			 chkYn = true;
		}

		div_search.cal_polYm.Enable = false;  //적용년월 수정불가 여부
		//div_search.cmb_month.Enable = chkYn;  //적용월 수정불가 여부

		div_search.cal_aplyStaDtm.Enable = chkYn;  //적용시작년월 수정불가 여부
		div_search.edt_hour.Enable = chkYn;  //적용시작시 수정불가 여부
		div_search.edt_min.Enable = chkYn;  //적용시작분 수정불가 여부

		div_search.cal_aplyEndDtm.Enable = chkYn;  //적용종료년월 수정불가 여부
		div_search.edt_endHour.Enable = chkYn;  //적용종료시 수정불가 여부
		div_search.edt_endMin.Enable = chkYn;  //적용종료분 수정불가 여부

		div_search.edt_polNm.Enable = true;  //정책명칭 수정불가 여부


        if( ds_addSaleCmms.getColumn(0,"ACNT") == 1 ) { 
			tab_addSaleCmms.tab_orgInfo.div_process.Enable = true; //판매처 적용구분 수정불가        

			tab_addSaleCmms.tab_payInfo.div_process.Enable = true;  //지급조건 수정불가
			tab_addSaleCmms.tab_payInfo.btn_add.Enable = true;       //지급조건 등급 추가버튼 수정불가
			tab_addSaleCmms.tab_payInfo.btn_subtract.Enable = true;  //지급조건 등급 삭제버튼 수정불가
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",0,"edit","checkbox");   //checkbox
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",1,"edit","integer");   //integer
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",2,"edit","combo");   //combo
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",3,"edit","combo");   //combo
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",4,"edit",decode(ds_addSaleCmms.GetColumn(0,"ALL_MDL_APLY_YN"), 'Y','none','combo'));   //combo
        } else {
			tab_addSaleCmms.tab_orgInfo.div_process.Enable = false; //판매처 적용구분 수정불가        

			tab_addSaleCmms.tab_payInfo.div_process.Enable = false;  //지급조건 수정불가
			tab_addSaleCmms.tab_payInfo.btn_add.Enable = false;       //지급조건 등급 추가버튼 수정불가
			tab_addSaleCmms.tab_payInfo.btn_subtract.Enable = false;  //지급조건 등급 삭제버튼 수정불가
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",0,"edit","none");   //checkbox
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",1,"edit","none");   //integer
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",2,"edit","none");   //combo
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",3,"edit","none");   //combo
			tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",4,"edit",decode(ds_addSaleCmms.GetColumn(0,"ALL_MDL_APLY_YN"), 'Y','none','none'));   //combo
        }


		//btn_delete.Enable = chkYn;  //삭제 불가 여부

	    //저장, 삭제 버튼을 로그인한 사용자와 작성한자의 조직이 틀리면... 
        if( ds_addSaleCmms.getColumn(0,"MOD_DVS") != gds_session.GetColumn(0, "orgid") ) { 
            btn_save.Visible   = false;
            btn_delete.Visible = false;
        } else {
            btn_save.Visible   = true;
        }

	} else {   //신규 등록 모드
		ds_addSaleCmms.AddRow(0);

		if(uf_isNull(PRE_APLY_STA_DTM)== true && uf_isNull(MOD_YMD)== true) {   //적용시작일시를 안바꿨을때..
			ds_condition.SetColumn(0,"APLY_STA_DTM",ds_condition.GetColumn(0,"POL_YM")+"01");  //적용시작년월일
			ds_condition.SetColumn(0,"HOUR","00");  //적용시작시
			ds_condition.SetColumn(0,"MIN","00");  //적용시작분

			var lastDay = uf_MonthLastDay(ds_condition.GetColumn(0,"POL_YM")+"01");
			ds_condition.SetColumn(0,"APLY_END_DTM",ds_condition.GetColumn(0,"POL_YM")+lastDay);  //적용종료년월일
			ds_condition.SetColumn(0,"END_HOUR","23");  //적용종료시
			ds_condition.SetColumn(0,"END_MIN","59");  //적용종료분
		} else {
			ds_condition.SetColumn(0,"APLY_STA_DTM",substr(MOD_YMD,0,8));  //적용시작년월일
			ds_condition.SetColumn(0,"HOUR",substr(MOD_YMD,8,2));  //적용시작시
			ds_condition.SetColumn(0,"MIN",substr(MOD_YMD,10,2));  //적용시작분

			ds_condition.SetColumn(0,"APLY_END_DTM",substr(MOD_YMD_END,0,8));  //적용종료년월일
			ds_condition.SetColumn(0,"END_HOUR",substr(MOD_YMD_END,8,2));  //적용종료시
			ds_condition.SetColumn(0,"END_MIN",substr(MOD_YMD_END,10,2));  //적용종료분

			//alert(ds_gnrlPrMny.GetColumn(0,"APLY_STA_DTM"));
			MOD_YMD = "";
			MOD_YMD_END = "";
		}


		//alert(ds_condition.GetColumn(0,"POL_YM"));
		var cal_accSchdMth = AddMonth(ds_condition.GetColumn(0,"POL_YM")+"01");
		//div_search.cmb_accSchdYear.Value = substr(cal_accSchdMth,0,4);  //정산예정년
		//div_search.cmb_accSchdMonth = substr(cal_accSchdMth,4,2);  //정산예정월

		//초기 적용시작일시
		PRE_APLY_STA_DTM = "";	
        //초기 적용종료일시
		PRE_APLY_END_DTM = "";


        //div_search.cmb_polOper.Index = 0;    //정책운영
		//div_search.rdo_vatAply.Value =  "Y";   //부가세포함여부
		//div_search.rdo_plstAply.Value =  "Y";   //가격표반영여부
		tab_addSaleCmms.tab_prodInfo.div_process.rdo_mdlAdpt.Value =  "N";  //전모델적용여부
		tab_addSaleCmms.tab_payInfo.div_process.rdo_qtyEstMthd.Value =  "01";  //수량산정방식
		tab_addSaleCmms.tab_payInfo.div_process.rdo_itvlAplyMthd.Value =  "01";  //구간적용방식


		//var chkYn = true;

		div_search.cal_polYm.Enable = false;  //적용년월 수정불가 여부
		//div_search.cmb_month.Enable = chkYn;  //적용월 수정불가 여부

		div_search.cal_aplyStaDtm.Enable = true;  //적용시작년월 수정불가 여부
		div_search.edt_hour.Enable = true;  //적용시작시 수정불가 여부
		div_search.edt_min.Enable = true;  //적용시작분 수정불가 여부

		div_search.cal_aplyEndDtm.Enable = true;  //적용종료년월 수정불가 여부
		div_search.edt_endHour.Enable = true;  //적용종료시 수정불가 여부
		div_search.edt_endMin.Enable = true;  //적용종료분 수정불가 여부

		div_search.edt_polNm.Enable = true;  //정책명칭 수정불가 여부

		tab_addSaleCmms.tab_orgInfo.div_process.Enable = true;; //판매처 적용구분 수정불가

		tab_addSaleCmms.tab_payInfo.div_process.Enable = true;  //지급조건 수정불가
		tab_addSaleCmms.tab_payInfo.btn_add.Enable = true;       //지급조건 등급 추가버튼 수정불가
		tab_addSaleCmms.tab_payInfo.btn_subtract.Enable = true;  //지급조건 등급 삭제버튼 수정불가
		tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",0,"edit","checkbox");   //checkbox
		tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",1,"edit","integer");   //integer
		tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",2,"edit","combo");   //combo
		tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",3,"edit","combo");   //combo
		tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("Body",4,"edit",decode(ds_addSaleCmms.GetColumn(0,"ALL_MDL_APLY_YN"), 'Y','none','combo'));   //combo

		//btn_delete.Enable = false;  //삭제 불가 여부


	}

	//trace(ds_saleTyp.SaveXML());
	//trace(tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1.DataColumn);
	//trace(tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1.DataColumn);
	     div_search.btn_monthCalendar.Enable = false;  //적용년월 달력 팜업

		//div_search.btn_search.Enable = chkYn;  //정책기간적용 수정불가 여부

		//조직정보 수정불가 여부
		tab_addSaleCmms.tab_orgInfo.grd_orgInfo.Editable = true;
		//조직정보 대상판매처 수정불가 여부
		tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.Editable = true;
		//조직정보 적용판매처 수정불가 여부
		tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.Editable = true;

		//상품정보 전모델적용 수정불가
		tab_addSaleCmms.tab_prodInfo.div_process.rdo_mdlAdpt.Enable = false;
		//상품정보 제조사 선택불가
		tab_addSaleCmms.tab_prodInfo.div_process.cmb_mfact.Enable = true;
		//상품정보 상품특성1 선택불가
		tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1.Enable = true;
		//상품정보 상품특성2 선택불가
		tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic2.Enable = true;
		//상품정보 SKT운영 선택불가
		tab_addSaleCmms.tab_prodInfo.div_process.cmb_sktOperYn.Enable = true;

		//상품정보 대상모델 수정불가
		tab_addSaleCmms.tab_prodInfo.grd_objtModel.Editable = true;
		//상품정보 적용모델 수정불가
		tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Editable = true;
		//상품정보 적용모델로 이동버튼 클릭불가
		tab_addSaleCmms.tab_prodInfo.btn_toAplyModel.Enable = true;
		//상품정보 대상모델로 이동버튼 클릭불가
		tab_addSaleCmms.tab_prodInfo.btn_toObjtModel.Enable = true;



		//영업정보 영업망 수정불가 여부
		tab_addSaleCmms.tab_saleInfo.grd_slNet.Editable = true;
		//영업정보 유통망 수정불가 여부
		tab_addSaleCmms.tab_saleInfo.grd_dsNet.Editable = true;
		//영업정보 판매유형 수정불가 여부
		tab_addSaleCmms.tab_saleInfo.grd_saleTyp.Editable = true;

		//지급정보 수정불가 여부
		tab_addSaleCmms.tab_payInfo.grd_payInfo.Editable = true;


		//btn_save.Enable = chkYn;  //저장 불가 여부
		btn_delete.Enable = chkYn;  //삭제 불가 여부

	    if (cf_idAuthSet(gds_session.GetColumn(0, "loginId")) == "OK") {
            btn_save.Visible   = true;
            btn_delete.Visible   = true;
	    }


		if (ds_condition.getColumn(0,"POP_YN") == "Y") {
            btn_save.Visible   = false;
            btn_delete.Visible = false;
		} else {
		}

}




/*******************************************************************************
* 함수명 : ds_orgInfo_OnColumnChanged
* 기능명 : 조직정보
*******************************************************************************/

function f_orgInfo()
{

	tab_addSaleCmms.tab_orgInfo.grd_orgInfo.Redraw = false;
	ds_orgInfo.FireEvent = false;

	//대중소 체크박스 초기화
	f_oLmsInit(ds_orgInfo,"ORG_ID_1","ORG_ID_2","ORG_ID_3","ORG_ID_4");


	tab_addSaleCmms.tab_orgInfo.grd_orgInfo.Redraw = true;
	ds_orgInfo.FireEvent = true;	


}


function ds_orgInfo_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
   //-----------------------------------------------------
   FV_ORG_ID = gds_session.GetColumn(0,"orgId");
   TM_ORG_ID = ds_orgInfo.GetColumn(nRow,"ORG_ID_3");

   if ( CHK_ORG != "ORG|"+nRow+"|"+strColumnID ) {
     	//대리점매핑
		FV_ORG_ID = cf_orgMapping(FV_ORG_ID,TM_ORG_ID);

		if ( FV_ORG_ID == "P10001" || FV_ORG_ID == "P00000" ) {
		} else {
			if ( FV_ORG_ID != TM_ORG_ID ) {
				alert("소속된 조직이 아닙니다.");
				CHK_ORG = "ORG|"+nRow+"|"+strColumnID;
				ds_orgInfo.SetColumn(nRow,strColumnID,varOldValue);
				return false;
			}
			if ( strColumnID == "SELECT1" ) {
				alert("소속된 조직이 아닙니다.");
				CHK_ORG = "ORG|"+nRow+"|"+strColumnID;
				ds_orgInfo.SetColumn(nRow,strColumnID,varOldValue);
				return false;
			}
		}
   } else {
		CHK_ORG = "";
   }
   //-----------------------------------------------------


	tab_addSaleCmms.tab_orgInfo.grd_orgInfo.Redraw = false;
	ds_orgInfo.FireEvent = false;

	//대중소 체크박스 클릭시 엑션
    f_oLmsOnClick(ds_orgInfo,"ORG_ID_1","ORG_ID_2","ORG_ID_3","ORG_ID_4",nRow,strColumnID,varOldValue,varNewValue);	

	ds_orgInfo.FireEvent = true;
	tab_addSaleCmms.tab_orgInfo.grd_orgInfo.Redraw = true;
}


/*-----------------------------------------------------------------------------------
 * Method : dvGrRate_grRate_OnHeadClick
 * Desc   : grid헤더 클릭시 모두체크
 *----------------------------------------------------------------------------------*/
function tab_addSaleCmms_tab_orgInfo_grd_orgInfo_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if( nCell==0 || nCell==2 || nCell==4 || nCell==6)
	{

		FV_ORG_ID = gds_session.GetColumn(0,"orgId");
		if ( FV_ORG_ID == "P10001" ) {
		} else {
		   alert("소속된 조직이 아닙니다.");
		   return false;
		}

		f_gridcheckAll(obj,ds_orgInfo,nCell);  //전부 체크
	}
}



