/*-----------------------------------------------------------------------------------
* 기능명 : 그리드에서 셀을 더블클릭했을때
function tab_addSaleCmms_tab_prodInfo_grd_objtModel_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var i=0; i<ds_objtModel.rowcount; i++ ){		
		if(ds_objtModel.GetColumn(i, "PROD_CD") == ds_aplyModel.GetColumn(nRow, "MDL_ID") ){
			return;
		}
    }

    f_moveShuttle(ds_objtModel, ds_aplyModel);    	
    ds_aplyModel.Sort("PROD_NM");  	
}

function tab_addSaleCmms_tab_prodInfo_grd_aplyModel_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var i=0; i<ds_aplyModel.rowcount; i++ ){		
		if(ds_aplyModel.GetColumn(i, "MDL_ID") == ds_objtModel.GetColumn(nRow, "PROD_CD") ){
			return;
		}
    }

    f_moveShuttle(ds_aplyModel, ds_objtModel);    	
    ds_objtModel.Sort("PROD_NM"); 
}
*----------------------------------------------------------------------------------*/





/*-----------------------------------------------------------------------------------
* 함수명 : f_moveShuttle
* 기능명 : ource Dataset에 있는 row를 Target Dataset으로 이동함
*----------------------------------------------------------------------------------*/

function f_moveShuttle(ds, dsTarget)
{
	dsTarget.addRow();
	ds.copyToRow(ds.row, dsTarget.id, dsTarget.row );	
	ds.DeleteRow(ds.row);
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_addSaleCmms_tab_prodInfo_btn_toAplyModel_OnClick
* 기능명 : 대상모델에서 적용모델로 이동함
*----------------------------------------------------------------------------------*/
function tab_addSaleCmms_tab_prodInfo_btn_toAplyModel_OnClick(obj)
{
	if(ds_objtModel.currow == -1 ){
	    //선택된 대상모델 없습니다.
		alert(cf_getMessage(MSG_00134, "대상모델"));
		return;
	}

	tab_addSaleCmms.tab_prodInfo.grd_objtModel.Redraw = false;
	tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Redraw  = false;

	var sHead = tab_addSaleCmms.tab_prodInfo.grd_objtModel.GetCellProp("head",0,"text");
	var nTotCnt = ds_objtModel.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_objtModel.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_objtModel.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_objtModel.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_aplyModel.AddRow();
			ds_aplyModel.CopyRow(ds_aplyModel.lastrow,ds_objtModel,nRow);
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "SELECTED", "0");
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "QTY_INCLD_YN", "1");
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "PAY_INCLD_YN", "1");
			ds_objtModel.DeleteRow(nRow);
	   }
	}

	//ds_aplyModel.Sort("PROD_NM");
	ds_addSaleCmms.SetColumn(0,"MOD_YN_SALE","Y");  //모델 변경됨으로 표시
	for( var nRow=0; nRow<ds_payInfo.rowcount; nRow++ ){
	   ds_payInfo.SetColumn(nRow,"OPEN_YN","N");
	}
	tab_addSaleCmms.tab_prodInfo.grd_objtModel.SetCellProp("head",0,"text","0");

	tab_addSaleCmms.tab_prodInfo.grd_objtModel.Redraw = true;
	tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Redraw  = true;
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_addSaleCmms_tab_prodInfo_btn_toObjtModel_OnClick
* 기능명 : 적용모델에서 대상모델로 이동함
*----------------------------------------------------------------------------------*/
function tab_addSaleCmms_tab_prodInfo_btn_toObjtModel_OnClick(obj)
{
	if(ds_aplyModel.currow == -1 ){
	    //선택된 적용모델 없습니다.
		alert(cf_getMessage(MSG_00134, "적용모델"));
		return;
	}

	tab_addSaleCmms.tab_prodInfo.grd_objtModel.Redraw = false;
	tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Redraw  = false;

	var sHead = tab_addSaleCmms.tab_prodInfo.grd_aplyModel.GetCellProp("head",0,"text");
	var nTotCnt = ds_aplyModel.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_aplyModel.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_aplyModel.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_aplyModel.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_objtModel.AddRow();
			ds_objtModel.CopyRow(ds_objtModel.lastrow,ds_aplyModel,nRow);
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "SELECTED", "0");
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "QTY_INCLD_YN", "0");
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "PAY_INCLD_YN", "0");
			ds_aplyModel.DeleteRow(nRow);
	   }
	}

	//ds_objtModel.Sort("PROD_NM");
	ds_addSaleCmms.SetColumn(0,"MOD_YN_SALE","Y");  //모델 변경됨으로 표시
	for( var nRow=0; nRow<ds_payInfo.rowcount; nRow++ ){
	   ds_payInfo.SetColumn(nRow,"OPEN_YN","N");
	}
	tab_addSaleCmms.tab_prodInfo.grd_aplyModel.SetCellProp("head",0,"text","0");

	tab_addSaleCmms.tab_prodInfo.grd_objtModel.Redraw = true;
	tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Redraw  = true;
}



/*-----------------------------------------------------------------------------------
 * Method : tab_addSaleCmms_tab_prodInfo_div_process_rdo_mdlAdpt_OnClick
 * Desc   :  전모델 적용
 *----------------------------------------------------------------------------------*/

function tab_addSaleCmms_tab_prodInfo_div_process_rdo_mdlAdpt_OnClick(obj)
{
	//tab_addSaleCmms.tab_prodInfo.div_process.rdo_mdlAdpt.Value
	//alert(obj.Value);
    if ( obj.Value == "Y") {
        tab_addSaleCmms.tab_prodInfo.div_process.cmb_mfact.index = 0;  //제조사 전체로 세팅
        tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1.index = 0;  //상품특성1 전체로 세팅
        tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic2.index = 0;  //상품특성2 전체로 세팅
        tab_addSaleCmms.tab_prodInfo.div_process.cmb_sktOperYn.index = 0;  //SK운영여부 전체로 세팅

        var strMfact       = tab_addSaleCmms.tab_prodInfo.div_process.cmb_mfact.value;
        var strProdChrtic1 = tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic1.value;
        var strProdChrtic2 = tab_addSaleCmms.tab_prodInfo.div_process.cmb_prodChrtic2.value;
        var strSktOperYn   = tab_addSaleCmms.tab_prodInfo.div_process.cmb_sktOperYn.value;
		var sFilterDs      = "";

		//ds_objtModel.Unfilter();
		if(uf_isNull(strMfact)== false) {
			sFilterDs += " DEAL_CO_CD='" + strMfact + "' &&";
		}
		if (strProdChrtic1 != "") {
			sFilterDs += " PROD_CHRTIC_1_CD='" + strProdChrtic1 + "' &&";
		}
		if (strProdChrtic2 != "") {
			sFilterDs += " PROD_CHRTIC_2_CD='" + strProdChrtic2 + "' &&";
		}
		if (strSktOperYn != "") {
			sFilterDs += " SKT_OPER_YN='" + strSktOperYn + "' &&";
		}
			//sFilterDs += " 1=1";
			//alert(SubStr(sFilterDs,0,Length(sFilterDs)-2));
		ds_objtModel.Filter(SubStr(sFilterDs,0,Length(sFilterDs)-2));  //대상모델 전체 조회

		//f_gridcheckOneAll(obj,ds_objtModel);  //전부 체크

		//tab_addSaleCmms_tab_prodInfo_btn_toAplyModel_OnClick();   //적용모델로 이동


		tab_addSaleCmms.tab_prodInfo.grd_objtModel.Redraw = false;
		tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Redraw  = false;

		for(var iCnt=0; iCnt<=ds_aplyModel.GetCount(); iCnt++){
				ds_aplyModel.SetColumn(iCnt, "QTY_INCLD_YN", "1");
				ds_aplyModel.SetColumn(iCnt, "PAY_INCLD_YN", "1");
		}


		//var sHead = tab_addSaleCmms.tab_prodInfo.grd_objtModel.GetCellProp("head",0,"text");
		var nTotCnt = ds_objtModel.GetCount();

        if( nTotCnt > 0 ) {
			if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

			for(var nRow=nTotCnt; nRow>=0; nRow--){
					ds_aplyModel.AddRow();
					ds_aplyModel.CopyRow(ds_aplyModel.lastrow,ds_objtModel,nRow);
					ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "SELECTED", "0");
					ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "QTY_INCLD_YN", "1");
					ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "PAY_INCLD_YN", "1");
					ds_objtModel.DeleteRow(nRow);
			}

			ds_aplyModel.Sort("PROD_NM");
			//tab_addSaleCmms.tab_prodInfo.grd_objtModel.SetCellProp("head",0,"text","0");

        }

		tab_addSaleCmms.tab_prodInfo.grd_objtModel.Redraw = true;
		tab_addSaleCmms.tab_prodInfo.grd_aplyModel.Redraw  = true;



    }

}




/*******************************************************************************
* 함수명 : f_payInfo
* 기능명 : 지급정보
*******************************************************************************/

//지급정보 플러스(추가) 버튼 클릭시
function tab_addSaleCmms_tab_payInfo_btn_add_OnClick(obj)
{
	ds_payInfo.AddRow();
	ds_payInfo.SetColumn(ds_payInfo.lastrow, "SEQ", ds_payInfo.lastrow+1);
	ds_payInfo.SetColumn(ds_payInfo.lastrow, "SELECTED", "0");
	ds_payInfo.SetColumn(ds_payInfo.lastrow, "STRD_CL_CD", "01");
	ds_payInfo.SetColumn(ds_payInfo.lastrow, "PAY_INFO_TYP_CD", "01");
	ds_payInfo.SetColumn(ds_payInfo.lastrow, "ITVL_TRGT", "0");
	ds_payInfo.SetColumn(ds_payInfo.lastrow, "GRD_INS_YN", "N");

}

//지급정보 마이너스 버튼 클릭시
function tab_addSaleCmms_tab_payInfo_btn_subtract_OnClick(obj)
{
	if(ds_payInfo.currow == -1 ){
	    //선택된 지급정보가 없습니다.
		alert(cf_getMessage(MSG_00134, "지급정보"));
		return;
	}

	tab_addSaleCmms.tab_payInfo.grd_payInfo.Redraw = false;

	var sHead = tab_addSaleCmms.tab_payInfo.grd_payInfo.GetCellProp("head",0,"text");
	var nTotCnt = ds_payInfo.GetCount();
	var sSeq = "";
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_payInfo.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_payInfo.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_payInfo.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_payInfo.DeleteRow(nRow);
            //지급정보에 해당하는 모델별장려금 삭제            
			sSeq = ds_payInfo.GetColumn(nRow,"SEQ");
			for( var idx=0; idx<=ds_mdlPrCmms.rowcount; idx++ ){
				if(ds_mdlPrCmms.GetColumn(idx,"SEQ") == sSeq){
					ds_mdlPrCmms.DeleteRow(idx);
				}
			}
	   }
	}

	tab_addSaleCmms.tab_payInfo.grd_payInfo.SetCellProp("head",0,"text","0");
	tab_addSaleCmms.tab_payInfo.grd_payInfo.Redraw = true;


}

//지급정보 해드 체크박스 클릭
function tab_addSaleCmms_tab_payInfo_grd_payInfo_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if( nCell==0)
	{
		if ( ds_condition.getColumn(0,"LAST_YN") == "Y" ) {  //해당 정책건에 대한 최종이력이면...
			f_gridcheckOneAll(obj,ds_payInfo);  //전부 체크
		}
	}

}


//일괄 차등 모델별 장려금  팜업창 띄위기
function tab_addSaleCmms_tab_payInfo_grd_payInfo_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 5){

		var sArg = "SEQ=" +quote(ds_payInfo.GetColumn(nRow,"SEQ"));
		sArg += " GRD_INS_YN=" + quote(ds_payInfo.getColumn(nRow,"GRD_INS_YN")); 


		var iCnt = 0;
		for( iRow = 0; iRow < ds_saleTyp.GetRowCount(); iRow++ )
		{
		    if (ds_saleTyp.GetColumn(iRow,"SELECT3") == 1) {
		        iCnt ++;
		    }
		}

		var sHeight = 0;
		var sWidth  = 0;
        if( ds_payInfo.getColumn(nRow,"GRD_INS_YN") == "Y" ) {  //차등입력이면....
			sWidth  = 1;
			sHeight = 1;
			dialog("pol::POLOAP00700_01.xml", sArg, sWidth, sHeight,,200 ,100 );
        } else {    //일괄입력이면...
			sWidth  = 1;
			sHeight = 1;
			dialog("pol::POLOAP00700_02.xml", sArg, sWidth, sHeight,,205 ,nY+300 );
        }
		ds_payInfo.SetColumn(nRow,"OPEN_YN","Y");  //모델별장려금 입력창 띄움(확인함으로 간주)


	}

}





/*******************************************************************************
* 함수명 : f_oLmsInit
* 기능명 : 조직정보, 영업정보 판매유형의  대중소 체크박스 만들기
*******************************************************************************/
function f_oLmsInit(OBJ,TARGET_1,TARGET_2,TARGET_3,TARGET_4)
{

    //체크박스 만들기
	for( var idx=0; idx<=OBJ.rowcount; idx++ ){

		if(idx == 0){
			OBJ.SetColumn(0,"LRGFLAG","Y");
			OBJ.SetColumn(0,"MDLFLAG","Y");
			OBJ.SetColumn(0,"SMLFLAG","Y");
			OBJ.SetColumn(0,"EMLFLAG","Y");
		}else{
			if(OBJ.GetColumn(idx,TARGET_1) == OBJ.GetColumn(idx-1,TARGET_1)){
				OBJ.SetColumn(idx,"LRGFLAG","N");
			}else{ 
				OBJ.SetColumn(idx,"LRGFLAG","Y");
			}

			if(OBJ.GetColumn(idx,TARGET_2) == OBJ.GetColumn(idx-1,TARGET_2)){
				OBJ.SetColumn(idx,"MDLFLAG","N");
			}else{
				OBJ.SetColumn(idx,"MDLFLAG","Y");
			}	

			if(OBJ.GetColumn(idx,TARGET_3) == OBJ.GetColumn(idx-1,TARGET_3)){
				OBJ.SetColumn(idx,"SMLFLAG","N");
			}else{
				OBJ.SetColumn(idx,"SMLFLAG","Y");
			}					
			if (OBJ==ds_orgInfo) {
				if(OBJ.GetColumn(idx,TARGET_4) == OBJ.GetColumn(idx-1,TARGET_4)){
					OBJ.SetColumn(idx,"EMLFLAG","N");
				}else{
					OBJ.SetColumn(idx,"EMLFLAG","Y");
				}					
			}
		}
	}

    //대,중 분류 체크박스에 체크하기
    f_oLmsInitChecked(OBJ,"SELECT1",TARGET_1);
    f_oLmsInitChecked(OBJ,"SELECT2",TARGET_2);
	if (OBJ==ds_orgInfo) {
		f_oLmsInitChecked(OBJ,"SELECT3",TARGET_3);
	}


}

/*******************************************************************************
* 함수명 : f_oLmsInitChecked
* 기능명 : 조직정보, 영업정보 판매유형의  대중소 체크박스에 DB에서 가져온 정보대로 체크하기
*******************************************************************************/
function f_oLmsInitChecked(OBJ,SELECT,TARGET)
{
	for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
		if(OBJ.GetColumn(idx,TARGET) != OBJ.GetColumn(idx+1,TARGET)){
			var iCnt = 0;
			var oCnt = idx;
			for( var id=idx ; id>=0; id-- ){
				if(OBJ.GetColumn(id,SELECT) == "1"){
					iCnt++;
				}
				if(OBJ.GetColumn(id,TARGET) != OBJ.GetColumn(id-1,TARGET)){
					oCnt = (oCnt - id)+1;
					break;
				}					
			}
			var selected = 0;
			if( iCnt == oCnt ) { selected = 1; }
			for( var id=idx ; id>=0; id-- ){
				if (OBJ==ds_saleTyp) {
                    if(selected == 1) {
						    //alert(OBJ.GetColumn(id,"CHECKED_ST"));
						if(TARGET = "SALE_TYP_CD_1") {
							OBJ.SetColumn(id,SELECT,substr(OBJ.GetColumn(id,"CHECKED_ST"),0,1));
						} else if (TARGET = "SALE_TYP_CD_2") {
						    //alert(ds_gnrlPrMny.GetColumn(0,"IPOL_ID") +"==="+substr(OBJ.GetColumn(id,"CHECKED_ST"),1,1));
							OBJ.SetColumn(id,SELECT,substr(OBJ.GetColumn(id,"CHECKED_ST"),1,1));
						}
                    } else {
						OBJ.SetColumn(id,SELECT,selected);
                    }
				} else {
					OBJ.SetColumn(id,SELECT,selected);
				}
				if(OBJ.GetColumn(id,TARGET) != OBJ.GetColumn(id-1,TARGET)){
					break;
				}
			}
		}					
	}
}

/*******************************************************************************
* 함수명 : f_oLmsOnClick
* 기능명 : 조직정보, 영업정보 판매유형의  대중소 체크박스 클릭시 엑션
*******************************************************************************/
function f_oLmsOnClick(OBJ,TARGET_1,TARGET_2,TARGET_3,TARGET_4,nRow,strColumnID,varOldValue,varNewValue)
{

	if(strColumnID == "SELECT1"){  //------------------------------------------------
		for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
			if(OBJ.GetColumn(idx,TARGET_1) == OBJ.GetColumn(nRow,TARGET_1)){
				if (OBJ==ds_saleTyp) {
					f_setAC(idx,varOldValue,varNewValue);
					ds_addSaleCmms.SetColumn(0,"MOD_YN_SALE","Y");  //판매유형 변경됨으로 표시
				}
				OBJ.SetColumn(idx,strColumnID,varNewValue);
				OBJ.SetColumn(idx,"SELECT2",varNewValue);
				OBJ.SetColumn(idx,"SELECT3",varNewValue);
				OBJ.SetColumn(idx,"SELECT4",varNewValue);
		    }
		}
	}else if(strColumnID == "SELECT2"){  //-----------------------------------------
		for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
			if(OBJ.GetColumn(idx,TARGET_2) == OBJ.GetColumn(nRow,TARGET_2)){
				if(OBJ.GetColumn(idx,"SELECT1") == 1){
					OBJ.SetColumn(idx,strColumnID,varOldValue);
					OBJ.SetColumn(idx,"SELECT3",varOldValue);
					OBJ.SetColumn(idx,"SELECT4",varOldValue);
				}else{
					if (OBJ==ds_saleTyp) {
						f_setAC(idx,varOldValue,varNewValue);
						ds_addSaleCmms.SetColumn(0,"MOD_YN_SALE","Y");  //판매유형 변경됨으로 표시
					}
					OBJ.SetColumn(idx,strColumnID,varNewValue);
					OBJ.SetColumn(idx,"SELECT3",varNewValue);				
					OBJ.SetColumn(idx,"SELECT4",varNewValue);				
				}
			}

			if (OBJ != ds_saleTyp) {  //'''''''''''''''''''''''''''
				if(idx =OBJ.lastrow){
					var inx = OBJ.SearchRow(TARGET_1 + "==" + quote(OBJ.GetColumn(nRow,TARGET_1)) +
										   " && SELECT2 !=" + quote("1"));

					if(inx == -1){
						for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
							if(OBJ.GetColumn(idx,TARGET_1) == OBJ.GetColumn(nRow,TARGET_1)){
								OBJ.SetColumn(idx,"SELECT1","1");
							}
						}
					}

				}	
			}	//'''''''''''''''''''''''''''''''''''''''''''''''''

		}
	}else if(strColumnID == "SELECT3"){  //----------------------------------------
		for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
			if(OBJ.GetColumn(idx,TARGET_3) == OBJ.GetColumn(nRow,TARGET_3)){
				if(OBJ.GetColumn(idx,"SELECT2") == 1){
					OBJ.SetColumn(idx,strColumnID,varOldValue);
					OBJ.SetColumn(idx,"SELECT4",varOldValue);
				}else{
					if (OBJ==ds_saleTyp) {
						f_setAC(idx,varOldValue,varNewValue);
						ds_addSaleCmms.SetColumn(0,"MOD_YN_SALE","Y");  //판매유형 변경됨으로 표시
					}
					OBJ.SetColumn(idx,strColumnID,varNewValue);
					OBJ.SetColumn(idx,"SELECT4",varNewValue);				
				}
			}

			if (OBJ != ds_saleTyp) {  //'''''''''''''''''''''''''''
				if(idx =OBJ.lastrow){
					var idx = OBJ.SearchRow(TARGET_1 + "==" + quote(OBJ.GetColumn(nRow,TARGET_1)) +
								   " && " + TARGET_2 + "==" + quote(OBJ.GetColumn(nRow,TARGET_2)) +
										   " && SELECT3 !=" + quote("1"));
					if(idx == -1){
						for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
							if(OBJ.GetColumn(idx,TARGET_2) == OBJ.GetColumn(nRow,TARGET_2)){
								OBJ.SetColumn(idx,"SELECT2","1");  //체크
							}
						}
					}

					var inx = OBJ.SearchRow(TARGET_1 + "==" + quote(OBJ.GetColumn(nRow,TARGET_1)) +
										   " && SELECT2 !=" + quote("1"));

					if(inx == -1){
						for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
							if(OBJ.GetColumn(idx,TARGET_1) == OBJ.GetColumn(nRow,TARGET_1)){
								OBJ.SetColumn(idx,"SELECT1","1");   //체크
							}
						}
					}								  
				}	
			}	//'''''''''''''''''''''''''''''''''''''''''''''''''

		}
	}else if(strColumnID == "SELECT4"){  //---------------------------------------
		for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){

			if(OBJ.GetColumn(idx,TARGET_4) == OBJ.GetColumn(nRow,TARGET_4)){
				if(OBJ.GetColumn(idx,"SELECT3") == 1){
					OBJ.SetColumn(idx,strColumnID,varOldValue);
				}else{
					if (OBJ==ds_saleTyp) {
						f_setAC(idx,varOldValue,varNewValue);
						ds_addSaleCmms.SetColumn(0,"MOD_YN_SALE","Y");  //판매유형 변경됨으로 표시
					}
					OBJ.SetColumn(idx,strColumnID,varNewValue);
				}
			}
			//alert(idx +"--"+OBJ.lastrow);
			if (OBJ != ds_saleTyp) {  //'''''''''''''''''''''''''''
				if(idx =OBJ.lastrow){
					var idx = OBJ.SearchRow(TARGET_1 + "==" + quote(OBJ.GetColumn(nRow,TARGET_1)) +
								   " && " + TARGET_2 + "==" + quote(OBJ.GetColumn(nRow,TARGET_2)) +
								   " && " + TARGET_3 + "==" + quote(OBJ.GetColumn(nRow,TARGET_3)) +
										   " && SELECT4 !=" + quote("1"));
					if(idx == -1){
						for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
							if(OBJ.GetColumn(idx,TARGET_3) == OBJ.GetColumn(nRow,TARGET_3)){
								OBJ.SetColumn(idx,"SELECT3","1");  //체크
							}
						}
					}


					var idx = OBJ.SearchRow(TARGET_1 + "==" + quote(OBJ.GetColumn(nRow,TARGET_1)) +
								   " && " + TARGET_2 + "==" + quote(OBJ.GetColumn(nRow,TARGET_2)) +
										   " && SELECT3 !=" + quote("1"));
					if(idx == -1){
						for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
							if(OBJ.GetColumn(idx,TARGET_2) == OBJ.GetColumn(nRow,TARGET_2)){
								OBJ.SetColumn(idx,"SELECT2","1");  //체크
							}
						}
					}

					var inx = OBJ.SearchRow(TARGET_1 + "==" + quote(OBJ.GetColumn(nRow,TARGET_1)) +
										   " && SELECT2 !=" + quote("1"));

					if(inx == -1){
						for( var idx =0 ; idx<=OBJ.RowCount(); idx++ ){
							if(OBJ.GetColumn(idx,TARGET_1) == OBJ.GetColumn(nRow,TARGET_1)){
								OBJ.SetColumn(idx,"SELECT1","1");   //체크
							}
						}
					}								  
				}	
			}	//'''''''''''''''''''''''''''''''''''''''''''''''''''

		}

	}else{  //----------------------------------------------------------------

        //할부 현금 체크박스 클릭시 엑션
		if(OBJ.GetColumn(nRow,"SELECT3") == 0){
			//if(OBJ.GetColumn(nRow,"ALLOT_QTY_INCLD_YN") == 1){
				//f_oLmsOnClick(ds_saleTyp,"SALE_TYP_CD_1","SALE_TYP_CD_2","SALE_TYP_CD_3","",nRow,"SELECT3","0","1");	
			//}
		} else {
			if(    OBJ.GetColumn(nRow,"ALLOT_QTY_INCLD_YN") == 0
                && OBJ.GetColumn(nRow,"ALLOT_PAY_INCLD_YN") == 0
                && OBJ.GetColumn(nRow,"CASH_QTY_INCLD_YN")  == 0
                && OBJ.GetColumn(nRow,"CASH_PAY_INCLD_YN")  == 0
			  ) 
			{
               //alert("dddd");

			}
		}

	}  //----------------------------------------------------------------------


	if (OBJ==ds_orgInfo) {
		f_moveAplyToObjtSalePlc();  //조직정보 체크해제시 적용판매처에서 대상판매처로 이동
		f_setSalePlcFilter();  //대상판매처 필터(필더가 마지막 순서 중요)
	}

}

/*******************************************************************************
* 함수명 : f_oLmsOnClick
* 기능명 : 조직정보, 영업정보 판매유형의  대중소 체크박스 클릭시 엑션
*******************************************************************************/
function f_setAC(nRow,varOldValue,varNewValue)
{
     if (varOldValue == 1 && varNewValue == 0) {
		 ds_saleTyp.SetColumn(nRow,"ALLOT_QTY_INCLD_YN","0");
		 ds_saleTyp.SetColumn(nRow,"ALLOT_PAY_INCLD_YN","0");
		 ds_saleTyp.SetColumn(nRow,"CASH_QTY_INCLD_YN","0");
		 ds_saleTyp.SetColumn(nRow,"CASH_PAY_INCLD_YN","0");
     } else if (varOldValue == 0 && varNewValue == 1) {
		 ds_saleTyp.SetColumn(nRow,"ALLOT_QTY_INCLD_YN","1");
		 ds_saleTyp.SetColumn(nRow,"ALLOT_PAY_INCLD_YN","1");
		 ds_saleTyp.SetColumn(nRow,"CASH_QTY_INCLD_YN","1");
		 ds_saleTyp.SetColumn(nRow,"CASH_PAY_INCLD_YN","1");
     }
}

/*******************************************************************************
* 함수명 : f_GridCheckAll 
* 기능명 : Grid의 checkbox를 한꺼번에 check/uncheck하는 기능
* 파라미터
 * @param  : grid - Grid
 * @param  : ds - Dataset
 * @return : None
*******************************************************************************/
function f_gridCheckAll(grid,ds,nCell)
{

	// Check Box 전체 선택
	var nCount = ds.rowcount;
	if(nCell == 0 || nCell == 2 || nCell == 4 || nCell==6){
		if( grid.GetCellProp("head", nCell, "Text") == "1" ) {
			for ( var irow=0; irow < nCount; irow++ ) {
				if ( ds.ID == "ds_saleTyp" ) {
			         ds.SetColumn(irow,"ALLOT_QTY_INCLD_YN","0");
                     ds.SetColumn(irow,"ALLOT_PAY_INCLD_YN","0");
                     ds.SetColumn(irow,"CASH_QTY_INCLD_YN","0");
                     ds.SetColumn(irow,"CASH_PAY_INCLD_YN","0");
			    }
				ds.SetColumn(irow,"SELECT1","0");
				ds.SetColumn(irow,"SELECT2","0");
				ds.SetColumn(irow,"SELECT3","0");
				if(ds.ID == "ds_orgInfo") {
				ds.SetColumn(irow,"SELECT4","0");
				}
				grid.SetCellProp("head",0,"text","0");	
				grid.SetCellProp("head",2,"text","0");
				grid.SetCellProp("head",4,"text","0");
				if(ds.ID == "ds_orgInfo") {
				grid.SetCellProp("head",6,"text","0");
				}
			}
		} else {
			for ( var irow=0; irow < nCount; irow++ ){
				if ( ds.ID == "ds_saleTyp" ) {
				    if(nCell == 0) {
						ds.SetColumn(irow,"SELECT1","1");
						ds.SetColumn(irow,"SELECT2","1");
						ds.SetColumn(irow,"SELECT3","1");

						grid.SetCellProp("head",0,"text","1");	
						grid.SetCellProp("head",2,"text","1");
						grid.SetCellProp("head",4,"text","1");							
				    } else if (nCell == 2) {
						ds.SetColumn(irow,"SELECT2","1");
						ds.SetColumn(irow,"SELECT3","1");

						grid.SetCellProp("head",2,"text","1");
						grid.SetCellProp("head",4,"text","1");							
				    } else if (nCell == 4) {
						ds.SetColumn(irow,"SELECT3","1");

						grid.SetCellProp("head",4,"text","1");							
				    } 
				} else {
					ds.SetColumn(irow,"SELECT1","1");
					ds.SetColumn(irow,"SELECT2","1");
					ds.SetColumn(irow,"SELECT3","1");
					if(ds.ID == "ds_orgInfo") {
					ds.SetColumn(irow,"SELECT4","1");
					}
					grid.SetCellProp("head",0,"text","1");	
					grid.SetCellProp("head",2,"text","1");
					grid.SetCellProp("head",4,"text","1");							
					if(ds.ID == "ds_orgInfo") {
					grid.SetCellProp("head",6,"text","1");							
					}
				}
			}

		}
	}


}


function f_gridcheckOneAll(grid,ds)
{
	// Check Box 전체 선택
	var nCount = ds.rowcount;
	if( grid.GetCellProp("head", 0, "Text") == "1" ) {
		for ( var irow=0; irow < nCount; irow++ ) {
			if ( ds.ID == "ds_slNet" || ds.ID == "ds_dsNet" ) {
				ds.SetColumn(irow,"QTY_INCLD_YN","0");
				ds.SetColumn(irow,"PAY_INCLD_YN","0");
			}
			ds.SetColumn(irow,"SELECTED","0");
			grid.SetCellProp("head",0,"text","0");				
		}
	} else {
		for ( var irow=0; irow < nCount; irow++ ){
			if ( ds.ID == "ds_slNet" || ds.ID == "ds_dsNet" ) {
				ds.SetColumn(irow,"QTY_INCLD_YN","1");
				ds.SetColumn(irow,"PAY_INCLD_YN","1");
			}
			ds.SetColumn(irow,"SELECTED","1");
			grid.SetCellProp("head",0,"text","1");				
		}
	}
}




/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcSearch")
	{
    http.Sync = true;
		tab_addSaleCmms.TabIndex = 0;
		f_setCode();        //적용날짜, 예정월, 코드 등등  초기 세팅	
		f_orgInfo();
		f_saleTyp();
		f_setSalePlcFilter();		
		f_setObjtSalePlcFilter();
		f_setAplySalePlcFilter();
		//f_payInfo_Init();
		//trace(ds_addSaleAmtCd.SaveXML());
    http.Sync = false;


	}
	if(sSvcID == "svcSave")
	{
		var sArg  = "POL_YM=" + quote(ds_addSaleCmms.getColumn(0,"POL_YM")); 
		gf_OpenWindow( "100000248", sArg); 
		this.close();
	}
	if(sSvcID == "svcAdd")
	{
		var sArg  = "POL_YM=" + quote(ds_addSaleCmms.getColumn(0,"POL_YM")); 
		gf_OpenWindow( "100000248", sArg); 
		this.close();
	}	
	if(sSvcID == "svcDelete")
	{
		var sArg  = "POL_YM=" + quote(ds_addSaleCmms.getColumn(0,"POL_YM")); 
		gf_OpenWindow( "100000248", sArg); 
		this.close();
	}	

}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search(sArg)
{
	//alert(sArg);
	if (uf_isNull(sArg)== true) {
		ds_condition.SetColumn(0,"APOL_ID",APOL_ID);
		ds_condition.SetColumn(0,"POL_YM",POL_YM);
		ds_condition.SetColumn(0,"POL_TS",POL_TS);
		ds_condition.SetColumn(0,"APLY_STA_DTM",APLY_STA_DTM);
		ds_condition.SetColumn(0,"LAST_YN",LAST_YN);
	} else {
		// 리셋시.. 넘어온 파라메터를 데이타셋으로 세팅
		PRE_APLY_STA_DTM = "";
		PRE_APLY_END_DTM = "";
		ds_condition.clearData();
		ds_condition.addRow();
		var arrParams = sArg.split(" ", "webstyle");
		for(var i=0, n=arrParams.length; i<n; i++)
		{
			var arrParamInfo= arrParams[i].split("=","webstyle");
			var sParamName = arrParamInfo[0];
			var sparamValue = arrParamInfo[1];
			ds_condition.setColumn(0, sParamName, unQuote(sparamValue));
		}
	}

	ds_aplySalePlc.ClearData();  //적용판매처 초기화

	if(uf_isNull(ds_condition.GetColumn(0,"APOL_ID"))== true) {   //신규등록시

		if(uf_isNull(PRE_APLY_STA_DTM)== true && uf_isNull(MOD_YMD)== true) {   //적용시작일시를 안바꿨을때..
			//ds_condition.SetColumn(0,"APLY_STA_DTM",ds_condition.GetColumn(0,"POL_YM")+"010000");
			ds_condition.SetColumn(0,"APLY_STA_DTM",APLY_STA_DTM);
			if (uf_isNull(ds_condition.GetColumn(0,"APLY_STA_DTM"))== true) {
				ds_condition.SetColumn(0,"APLY_STA_DTM",ds_condition.GetColumn(0,"POL_YM")+"010000");
			}
		} else {
			ds_condition.SetColumn(0,"APLY_STA_DTM",MOD_YMD);
		}

	}


	//trace("============="+ds_condition.SaveXML());
	var sSvcID = "svcSearch";
	var sInDs = "nc_input_fieldDs=ds_condition";
	var sOutDs  = " ds_addSaleCmms=ds_addSaleCmms ";   //추가수수료 기본정보
	    sOutDs += " ds_orgInfo=ds_orgInfo ";             //조직정보
	    sOutDs += " ds_objtSalePlc=ds_objtSalePlc ";      //대상 판매처정보
	    sOutDs += " ds_aplySalePlc=ds_aplySalePlc ";      //적용 판매처정보
	    sOutDs += " ds_slNet=ds_slNet ";                 //영업망
	    sOutDs += " ds_dsNet=ds_dsNet ";                 //유통망
	    sOutDs += " ds_saleTyp=ds_saleTyp ";             //판매유형
	    sOutDs += " ds_mfact=ds_mfact ";                 //제조사
	    sOutDs += " ds_objtModel=ds_objtModel ";         //대상모델
	    sOutDs += " ds_aplyModel=ds_aplyModel ";		 //적용모델
	    sOutDs += " ds_payInfo=ds_payInfo ";		     //지급정보
	    sOutDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
	var sArg = "sktst.pol.OAP#getAddSaleCmmsDetail";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

}




