/*******************************************************************************
 * @description    : 공통 저장 함수
*******************************************************************************/
function df_save()
{
	// 마감여부 체크 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.
	cf_CheckCloseMnth('04',ds_condition.getColumn(0,"POL_YM"),'적용기간'); 
	//마감이 됬다면... 저장못함.
	if(FV_BOOLEAN_CLOSE) return;


	//trace(ds_payInfo.SaveXML());
	//판매유형 체크 상태 셋업
	for( var idx =0 ; idx<=ds_saleTyp.RowCount(); idx++ ){
		if(uf_isNull(ds_saleTyp.GetColumn(idx,"SELECT1")) == true) {
			ds_saleTyp.SetColumn(idx,"SELECT1","0");		
        }
		if(uf_isNull(ds_saleTyp.GetColumn(idx,"SELECT2")) == true) {
			ds_saleTyp.SetColumn(idx,"SELECT2","0");		
        }
		if(uf_isNull(ds_saleTyp.GetColumn(idx,"SELECT3")) == true) {
			ds_saleTyp.SetColumn(idx,"SELECT2","0");		
        }

	    ds_saleTyp.SetColumn(idx,"CHECKED_ST",
		    ToString(ds_saleTyp.GetColumn(idx,"SELECT1"))
		  + ToString(ds_saleTyp.GetColumn(idx,"SELECT2"))
		  + ToString(ds_saleTyp.GetColumn(idx,"SELECT3"))
	    );

	}


	if (!(f_pre_save())) return;  //유효성 체크

	var sSvcID = "";
	var sInDs = "";
	var sOutDs = "";
	var sArg = "";
	var APLY_STA_DTM = "";
	var APLY_END_DTM = "";

	APLY_STA_DTM = div_search.cal_aplyStaDtm.Value+Right("0"+div_search.edt_hour.Text,2)+Right("0"+div_search.edt_min.Text,2);
	APLY_END_DTM = div_search.cal_aplyEndDtm.Value+Right("0"+div_search.edt_endHour.Text,2)+Right("0"+div_search.edt_endMin.Text,2);
	ds_addSaleCmms.SetColumn(0,"APLY_STA_DTM",APLY_STA_DTM);
	ds_addSaleCmms.SetColumn(0,"APLY_END_DTM",APLY_END_DTM);
	ds_addSaleCmms.SetColumn(0,"POL_YM",div_search.cal_polYm.Value);
	//ds_addSaleCmms.SetColumn(0,"ACC_SCHD_MTH",div_search.cmb_accSchdYear.Value+div_search.cmb_accSchdMonth.Value);
	//trace(ds_aplyModel.SaveXML());
    //alert(PRE_APLY_STA_DTM+"___"+APLY_STA_DTM);
	if(PRE_APLY_STA_DTM == APLY_STA_DTM){
	    //저장하시겠습니까?
		if(confirm(cf_getMessage(MSG_00063, "")) == 1){
			sSvcID = "svcSave";

			sInDs  = " ds_addSaleCmms=ds_addSaleCmms ";    //추가수수료 기본정보
			sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
			sInDs += " ds_slNet=ds_slNet ";                  //영업망
			sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
			sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
			sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
			sInDs += " ds_payInfo=ds_payInfo ";		         //지급정보
			sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
			sInDs += " ds_aplySalePlc=ds_aplySalePlc ";		 //적용판매처

			sOutDs = "";

			sArg = "sktst.pol.OAP#saveAddSaleCmms";
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
		}

	}else{
		if(uf_isNull(ds_addSaleCmms.GetColumn(0,"APOL_ID"))== true) {   //리스트 클릭시 수정모드
						//새로운 추가수수료 정책을 반영하시겠습니까?
						if(confirm(cf_getMessage(MSG_00136, "새로운 추가수수료 정책을 반영")) == 1){

							sSvcID = "svcAdd";

							sInDs  = " ds_addSaleCmms=ds_addSaleCmms ";    //추가수수료 기본정보
							sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
							sInDs += " ds_slNet=ds_slNet ";                  //영업망
							sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
							sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
							sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
							sInDs += " ds_payInfo=ds_payInfo ";		         //지급정보
							sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
							sInDs += " ds_aplySalePlc=ds_aplySalePlc ";		 //적용판매처

							sOutDs = "";

							 sArg = "sktst.pol.OAP#addAddSaleCmms";
							 cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
						}
		} else {
				//저장하시겠습니까?
				if(confirm(cf_getMessage(MSG_00136, "소급 변경 ")) == 1){
					sSvcID = "svcSave";

					sInDs  = " ds_addSaleCmms=ds_addSaleCmms ";    //추가수수료 기본정보
					sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
					sInDs += " ds_slNet=ds_slNet ";                  //영업망
					sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
					sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
					sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
					sInDs += " ds_payInfo=ds_payInfo ";		         //지급정보
					sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
					sInDs += " ds_aplySalePlc=ds_aplySalePlc ";		 //적용판매처

					sOutDs = "";

					sArg = "sktst.pol.OAP#saveAddSaleCmms";
					cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
				} else {
						//새로운 추가수수료 정책을 반영하시겠습니까?
						if(confirm(cf_getMessage(MSG_00136, "새로운 추가수수료 정책을 반영")) == 1){

							sSvcID = "svcAdd";

							sInDs  = " ds_addSaleCmms=ds_addSaleCmms ";    //추가수수료 기본정보
							sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
							sInDs += " ds_slNet=ds_slNet ";                  //영업망
							sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
							sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
							sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
							sInDs += " ds_payInfo=ds_payInfo ";		         //지급정보
							sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
							sInDs += " ds_aplySalePlc=ds_aplySalePlc ";		 //적용판매처

							sOutDs = "";

							 sArg = "sktst.pol.OAP#addAddSaleCmms";
							 cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
						}
				}
		}
	}

	//trace(ds_aplySalePlc.SaveXML());


}

/*******************************************************************************
* 함수명 : f_pre_save
* 기능명 : 저장버튼 전 체크함수 
*******************************************************************************/
function f_pre_save()
{	
	//if(SAVE_FLAG = "N"){
	//	alert("변경사항이 없습니다.");
	//	return;
	//}

	//if(ds_pol_uplst_aply_mdl.rowcount < 1){
	//	alert("적용할 단말기가 없습니다.");
	//	return;
	//}

	if(length(div_search.edt_hour.Text) < 1 || div_search.edt_hour.Text > 24){
	    //적용기간 시작시간을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용기간 시작시간"));
		div_search.edt_hour.SetFocus();
		return;
	}

	if(length(div_search.edt_min.Text) < 1 || div_search.edt_min.Text > 59){
	    //적용기간 시작분을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용기간 시작분"));
		div_search.edt_min.SetFocus();
		return;
	}

    if(uf_isNull(div_search.edt_polNm.Text) == true) {
	    //정책명칭을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "정책명칭"));
		div_search.edt_polNm.SetFocus();
		return;
    }


    if (ds_aplySalePlc.rowcount == 0) {
	    //적용판매처를 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용판매처"));
		return false;
    }


    if (ds_aplyModel.rowcount == 0) {
	    //적용모델을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용모델을"));
		return false;
    }

    var iCnt=0;
	for( var nRow=0; nRow<ds_slNet.rowcount; nRow++ ){
		if(ds_slNet.GetColumn(nRow,"SELECTED") == 1){
		    iCnt++;
		}
	}
    if (iCnt == 0) {
	    //영업망을 선택하여주십시요.
		alert(cf_getMessage(MSG_00047, "영업망을"));
		tab_addSaleCmms.TabIndex = 2;
		tab_addSaleCmms.tab_saleInfo.grd_slNet.SetFocus();
		return;
    }

    iCnt=0;
	for( var nRow=0; nRow<ds_dsNet.rowcount; nRow++ ){
		if(ds_dsNet.GetColumn(nRow,"SELECTED") == 1){
		    iCnt++;
		}
	}
    if (iCnt == 0) {
	    //유통망을 선택하여주십시요.
		alert(cf_getMessage(MSG_00047, "유통망을"));
		tab_addSaleCmms.TabIndex = 2;
		tab_addSaleCmms.tab_saleInfo.grd_dsNet.SetFocus();
		return;
    }

    iCnt=0;
	for( var nRow=0; nRow<ds_saleTyp.rowcount; nRow++ ){
		if(ds_saleTyp.GetColumn(nRow,"SELECT3") == "1"){
		    iCnt++;
		}
	}
    if (iCnt == 0) {
	    //판매유형을 선택하여주십시요.
		alert(cf_getMessage(MSG_00047, "판매유형을"));
		tab_addSaleCmms.TabIndex = 2;
		tab_addSaleCmms.tab_saleInfo.grd_saleTyp.SetFocus();
		return;
    }

	for( var nRow=0; nRow<ds_payInfo.rowcount; nRow++ ){
		if(uf_isNull(ds_payInfo.GetColumn(nRow,"BAS_QTY")) == true){
			//기준수량은 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "기준수량"));
			tab_addSaleCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfo, nRow, 1);
			return false;
		}
		if(ds_addSaleCmms.GetColumn(0,"MOD_YN_SALE") == "Y"){
			if(ds_payInfo.GetColumn(nRow,"OPEN_YN") != "Y"){
				//n번째 구간의 모델별 추가수수료를 입력하십시요.
				alert((nRow+1)+cf_getMessage(MSG_00030, "번째 구간의 모델별 추가수수료"));
				tab_addSaleCmms.TabIndex = 3;
				f_set_gridFocus(grd_payInfo, nRow, 7);
				return false;
			}
		}
	}




    if (ds_payInfo.rowcount == 0) {
	    //지급정보를 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "지급정보"));
		return false;
    }

    //지급정보의 구간 체크
    var sTrdClCd = "";     //STRD_CL_CD: 01(이상), 02(미만), 03(초과), 04(한정)
    var sMinQty = "";
    var sMaxQty = "";

	if( ds_payInfo.GetColumn((ds_payInfo.rowcount-1),"STRD_CL_CD") == "02" ) { 
	    //마지막 구간은 미만이 될 수 없습니다.
		alert(cf_getMessage(MSG_00083, "마지막 구간은 미만이 될 수"));
		return false;
	}

	for( var nRow=0; nRow<ds_payInfo.rowcount; nRow++ ){
		if(ds_payInfo.GetColumn(nRow,"STRD_CL_CD")=="01"){ //이상이면...
			//alert("마지막 구간은 이상으로 끝나야 합니다.");
				ds_payInfo.SetColumn(nRow,"MIN_QTY",ds_payInfo.GetColumn(nRow,"BAS_QTY"));
				ds_payInfo.SetColumn(nRow-1,"MAX_QTY",ds_payInfo.GetColumn(nRow,"BAS_QTY")-1);
			if (nRow == (ds_payInfo.rowcount-1)) {
				ds_payInfo.SetColumn(nRow,"MAX_QTY","999999999999");
			}

		} else if (ds_payInfo.GetColumn(nRow,"STRD_CL_CD")=="03"){ //초과면...
				ds_payInfo.SetColumn(nRow,"MIN_QTY",ds_payInfo.GetColumn(nRow,"BAS_QTY")+1);
				ds_payInfo.SetColumn(nRow,"MAX_QTY","999999999999");
			if (ds_payInfo.rowcount > 1) {
				//초과 조건에 다른 구간이 올 수 없습니다.
				alert(cf_getMessage(MSG_00083, "초과 조건에 다른 구간이 올 수"));
				return false;
			}

		} else if (ds_payInfo.GetColumn(nRow,"STRD_CL_CD")=="04"){ //한정이면...
				ds_payInfo.SetColumn(nRow,"MIN_QTY","1");
				ds_payInfo.SetColumn(nRow,"MAX_QTY",ds_payInfo.GetColumn(nRow,"BAS_QTY"));
			if (ds_payInfo.rowcount > 1) {
				//한정 조건에 다른 구간이 올 수 없습니다.
				alert(cf_getMessage(MSG_00083, "한정 조건에 다른 구간이 올 수"));
				return false;
			}

		}

	}

	for( var nRow=0; nRow<ds_payInfo.rowcount; nRow++ ){
		if (ds_payInfo.GetColumn(nRow,"STRD_CL_CD")=="02"){ //미만이면...
				ds_payInfo.SetColumn(nRow,"MIN_QTY",ds_payInfo.GetColumn(nRow-1,"BAS_QTY"));
				ds_payInfo.SetColumn(nRow,"MAX_QTY",ds_payInfo.GetColumn(nRow,"BAS_QTY")-1);
			if (nRow == 0) {
				ds_payInfo.SetColumn(nRow,"MIN_QTY","1");
			}
			if (nRow == (ds_payInfo.rowcount-1)) {
				ds_payInfo.SetColumn(nRow,"MAX_QTY","999999999999");
			}
		}

	}



	for( var nRow=1; nRow<ds_payInfo.rowcount; nRow++ ){
		if ( ds_payInfo.GetColumn(nRow,"MIN_QTY") != (ds_payInfo.GetColumn(nRow-1,"MAX_QTY")+1) ) {
				//n번째 구간을 다시 입력하십시요.
				alert((nRow+1)+cf_getMessage(MSG_00140, "번째 구간"));
				return false;
		}
	}


	for( var nRow=0; nRow<ds_payInfo.rowcount-1; nRow++ ){
		if ( ds_payInfo.GetColumn(nRow,"MIN_QTY") >= ds_payInfo.GetColumn(nRow,"MAX_QTY") ) {
			if(nRow == 0) {
			    if (    ds_payInfo.GetColumn(nRow,"MIN_QTY") == "1" 
			         && ds_payInfo.GetColumn(nRow,"MAX_QTY") == "1" ) {
				} else {
						//n번째 구간을 다시 입력하십시요.
						alert((nRow+1)+cf_getMessage(MSG_00140, "번째 구간"));
						return false;
				}
			} else {
				//n번째 구간을 다시 입력하십시요.
				alert((nRow+2)+cf_getMessage(MSG_00140, "번째 구간"));
				return false;
			}
		}
	}


    //차감정책일 경우 수량을 0대 부터
	ds_mdlPrCmms.Filter(" SEQ='1' ");
    //alert(ds_mdlPrCmms.GetColumn(0,"PR_CMMS"));
    if (    ds_payInfo.GetColumn(0,"GRD_INS_YN") == "N" 
         && ds_mdlPrCmms.GetColumn(0,"PR_CMMS") < 0
         && ds_payInfo.GetColumn(0,"MIN_QTY") == "1" ) {
				ds_payInfo.SetColumn(0,"MIN_QTY","0");
				//alert("==="+ds_payInfo.GetColumn(0,"MIN_QTY"));
    }
	ds_mdlPrCmms.UnFilter();
    //alert(ds_mdlPrCmms.GetColumn(0,"PR_CMMS"));
	//return false;        


    return true;


}


/*******************************************************************************
* 함수명 : f_set_gridFocus
* 기능명 : 그리드 포커스 이동
*******************************************************************************/
function f_set_gridFocus(gridObj, nRow, nCellIdx) 
{	
	var dataSetObj = object(gridObj.bindDataset);
	dataSetObj.row = nRow;
	gridObj.SetCellPos(nCellIdx);
	gridObj.SetFocus();
}


/*******************************************************************************
* 함수명 : df_delete
* 기능명 : 삭제
*******************************************************************************/
function df_delete(sButtonID)
{
	// 마감여부 체크 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.
	cf_CheckCloseMnth('04',ds_condition.getColumn(0,"POL_YM"),'적용기간'); 
	//마감이 됬다면... 저장못함.
	if(FV_BOOLEAN_CLOSE) return;

	if(confirm(cf_getMessage(MSG_00136, "추가판매수수료를 삭제")) == 1){ //추가판매수수료를 삭제 하시겠습니까?	
		var sSvcID = "svcDelete";
		var sInDs = "nc_input_fieldDs=ds_addSaleCmms";
		var sOutDs = "";
		var sArg = "sktst.pol.OAP#deleteAddSaleCmms";

		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
	}
}


/*-----------------------------------------------------------------------------------
* 함수명 : f_setSalePlcFilter
* 기능명 : 대상판매처 조회
*----------------------------------------------------------------------------------*/
function f_setSalePlcFilter()
{
        //var sOrgId       = "";
		var sFilterDs      = "";
		//ds_objtSalePlc.Unfilter();


		iCnt=0;
		for( var nRow=0; nRow<ds_orgInfo.rowcount; nRow++ ){
			if(ds_orgInfo.GetColumn(nRow,"SELECT4") == "1"){

				sFilterDs += " ORG_ID='" + ds_orgInfo.GetColumn(nRow,"ORG_ID_4") + "' ||";			
				iCnt++;

			}
		}
		if (iCnt == 0) {
				sFilterDs += " ORG_ID='000000' ||";			
		}


			//sFilterDs += " 1=1";
			//alert(SubStr(sFilterDs,0,Length(sFilterDs)-2));
		ds_objtSalePlc.Filter(SubStr(sFilterDs,0,Length(sFilterDs)-2));
}



/*******************************************************************************
* 함수명 : tab_addSaleCmms_tab_orgInfo_grd_objtSalePlc_OnHeadClick
* 기능명 : 추가판매수수료 대상 판매처 전체선택
*******************************************************************************/
function tab_addSaleCmms_tab_orgInfo_grd_objtSalePlc_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(object(obj.bindDataset).getRowCount() > 0)
	{
		var sType = obj.GetCellProp("head", nCell, "display");
		if(sType == "checkbox")	
		{
			if( nCell==0 )
			{
				f_gridcheckOneAll(obj,ds_objtSalePlc);  //전부 체크
			}
		}
		else
		{
			cf_markGridSort(obj, nCell, nX, nY, nPivotIndex);
		}
	}

}

/*******************************************************************************
* 함수명 : tab_addSaleCmms_tab_orgInfo_grd_aplySalePlc_OnHeadClick
* 기능명 : 추가판매수수료 적용 판매처 전체선택
*******************************************************************************/
function tab_addSaleCmms_tab_orgInfo_grd_aplySalePlc_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(object(obj.bindDataset).getRowCount() > 0)
	{
		var sType = obj.GetCellProp("head", nCell, "display");
		if(sType == "checkbox")	
		{
			if( nCell==0 )
			{
				f_gridcheckOneAll(obj,ds_aplySalePlc);  //전부 체크
			}
		}
		else
		{
			cf_markGridSort(obj, nCell, nX, nY, nPivotIndex);
		}
	}

}




/*-----------------------------------------------------------------------------------
* 함수명 : tab_addSaleCmms_tab_orgInfo_btn_toAplySalePlc_OnClick
* 기능명 : 대상판매처에서 적용판매처로 이동함
*----------------------------------------------------------------------------------*/
function tab_addSaleCmms_tab_orgInfo_btn_toAplySalePlc_OnClick(obj)
{
	if(ds_objtSalePlc.currow == -1 ){
	    //선택된 대상모델 없습니다.
		alert(cf_getMessage(MSG_00134, "대상판매처"));
		return;
	}

	tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.Redraw = false;
	tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.Redraw  = false;

	var sHead = tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.GetCellProp("head",0,"text");
	var nTotCnt = ds_objtSalePlc.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_objtSalePlc.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_objtSalePlc.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_objtSalePlc.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_aplySalePlc.AddRow();
			ds_aplySalePlc.CopyRow(ds_aplySalePlc.lastrow,ds_objtSalePlc,nRow);
			ds_aplySalePlc.SetColumn(ds_aplySalePlc.lastrow, "SELECTED", "0");
			ds_objtSalePlc.DeleteRow(nRow);
	   }
	}

	//ds_aplySalePlc.Sort("PROD_NM");
	tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.SetCellProp("head",0,"text","0");

	tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.Redraw = true;
	tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.Redraw  = true;

}






/*-----------------------------------------------------------------------------------
* 함수명 : tab_addSaleCmms_tab_orgInfo_btn_toObjtSalePlc_OnClick
* 기능명 : 적용판매처에서 대상판매처로 이동함
*----------------------------------------------------------------------------------*/
function tab_addSaleCmms_tab_orgInfo_btn_toObjtSalePlc_OnClick(obj)
{
	if(ds_aplySalePlc.currow == -1 ){
	    //선택된 적용모델 없습니다.
		alert(cf_getMessage(MSG_00134, "적용판매처"));
		return;
	}

	tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.Redraw = false;
	tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.Redraw  = false;

	var sHead = tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.GetCellProp("head",0,"text");
	var nTotCnt = ds_aplySalePlc.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_aplySalePlc.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_aplySalePlc.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_aplySalePlc.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_objtSalePlc.AddRow();
			ds_objtSalePlc.CopyRow(ds_objtSalePlc.lastrow,ds_aplySalePlc,nRow);
			ds_objtSalePlc.SetColumn(ds_objtSalePlc.lastrow, "SELECTED", "0");
			ds_aplySalePlc.DeleteRow(nRow);
	   }
	}

	//ds_objtModel.Sort("PROD_NM");
	tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.SetCellProp("head",0,"text",0);

	tab_addSaleCmms.tab_orgInfo.grd_objtSalePlc.Redraw = true;
	tab_addSaleCmms.tab_orgInfo.grd_aplySalePlc.Redraw  = true;

}

/*-----------------------------------------------------------------------------------
* 함수명 : f_moveAplyToObjtSalePlc
* 기능명 : 조직정보 체크해제시 적용판매처에서 대상판매처로 이동
*----------------------------------------------------------------------------------*/
function f_moveAplyToObjtSalePlc()
{
    var sORG_ID_4 = "";
	for( var idx =0 ; idx<=ds_orgInfo.RowCount(); idx++ ){
		if(ds_orgInfo.GetColumn(idx,"SELECT4") == "0"){
			sORG_ID_4 = sORG_ID_4 + ds_orgInfo.GetColumn(idx,"ORG_ID_4") + ","; 
		}
	}

	sORG_ID_4 = SubStr(sORG_ID_4,0,Length(sORG_ID_4)-1);

	var arrOrgId = sORG_ID_4.split(",", "webstyle");

	for( var idx =0 ; idx<=ds_aplySalePlc.RowCount(); idx++ ){
		for(var i=0; i<=arrOrgId.length; i++)
		{
			if(ds_aplySalePlc.GetColumn(idx,"ORG_ID") == arrOrgId[i]){
				ds_aplySalePlc.SetColumn(idx,"SELECTED","1"); 
			}
		}
	}

	if(ds_aplySalePlc.currow == -1 ){
		return;
	}

	tab_addSaleCmms_tab_orgInfo_btn_toObjtSalePlc_OnClick();
}





//############################################################################################
//적용기간 날짜 입력 오류 체크
//############################################################################################
/*******************************************************************************
* 함수명 : div_search_cal_aply_sta_dtm_OnChanged
* 기능명 :  적용기간 시작일 OnChanged 발생시
*******************************************************************************/
function div_search_cal_aplyStaDtm_OnChanged(obj,strOldText,strNewText)
{
	if(uf_isNull(div_search.cal_aplyStaDtm.Value) == false){ 

	    if(substr(ds_condition.GetColumn(0,"POL_YM"),0,6) > substr(div_search.cal_aplyStaDtm.Value,0,6)) {
					ds_condition.SetColumn(0,"APLY_STA_DTM",substr(strOldText,0,8));
					obj.SetFocus();

					MOD_YMD     = "";
					MOD_YMD_END = "";

					//적용기간은 적용시작일자와 종료일자 기간 사이에 입력하십시오.
					alert(cf_getMessage(MSG_00131, "적용기간;적용시작일자;종료일자"));
					return false;
	    }

		MOD_YMD     = div_search.cal_aplyStaDtm.Value+div_search.edt_hour.Text+div_search.edt_min.Text;
		MOD_YMD_END = div_search.cal_aplyEndDtm.Value+div_search.edt_endHour.Text+div_search.edt_endMin.Text;

		if(uf_isNull(PRE_APLY_STA_DTM) == false){ 
			if(MOD_YMD <= PRE_APLY_STA_DTM || MOD_YMD >= PRE_APLY_END_DTM){
				ds_condition.SetColumn(0,"APLY_STA_DTM",substr(strOldText,0,8));
				obj.SetFocus();
				//적용기간은 적용시작일자와 종료일자 기간 사이에 입력하십시오.
				alert(cf_getMessage(MSG_00131, "적용기간;적용시작일자;종료일자"));
				return false;				
			}


		}
			var sArg  = " APOL_ID="+ quote(ds_condition.getColumn(0,"APOL_ID"));
				sArg += " POL_YM=" + quote(ds_condition.getColumn(0,"POL_YM"));
				sArg += " POL_TS=" + quote(ds_condition.getColumn(0,"POL_TS"));
				sArg += " APLY_STA_DTM=" + quote(MOD_YMD);
				sArg += " APLY_END_DTM=" + quote(MOD_YMD_END);
				sArg += " LAST_YN=" + quote("Y");
			gf_OpenWindow( "100000249", sArg); 

	}

}

/*******************************************************************************
* 함수명 : div_search_edt_hour_OnChanged
* 기능명 :  적용기간 시간 변경시 
*******************************************************************************/
function div_search_edt_hour_OnChanged(obj,strText)
{
	if(uf_isNull(strText) == false){

		if(length(strText) !=2) {

			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"HOUR",strText);
		}

		if(strText > 23){
			ds_condition.SetColumn(0,"HOUR","00");
			obj.SetFocus();
			//23시 초과할수 없습니다.
			alert(cf_getMessage(MSG_00034, "23시를"));
			return false;
		}

		var MOD_YMD = div_search.cal_aplyStaDtm.Value+div_search.edt_hour.Text+div_search.edt_min.Text;

		if(uf_isNull(PRE_APLY_STA_DTM) == false){ 
			if(MOD_YMD <= PRE_APLY_STA_DTM || MOD_YMD >= PRE_APLY_END_DTM){
				ds_condition.SetColumn(0,"HOUR","00");
				obj.SetFocus();
				//적용기간은 적용시작일자와 종료일자 기간 사이에 입력하십시오.
				alert(cf_getMessage(MSG_00131, "적용기간;적용시작일자;종료일자"));
				return false;

			}
		}
	}

}

/*******************************************************************************
* 함수명 : div_search_edt_min_OnChanged
* 기능명 :  적용기간 분 변경시 
*******************************************************************************/
function div_search_edt_min_OnChanged(obj,strText)
{
	if(uf_isNull(strText) == false){

		if(length(strText) !=2) {

			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"MIN",strText);
		}

		if(strText > 59){
			ds_condition.SetColumn(0,"MIN","00");
			obj.SetFocus();
			//59분을 초과할수 없습니다.
			alert(cf_getMessage(MSG_00034, "59분을"));
			return false;
		}

		var MOD_YMD = div_search.cal_aplyStaDtm.Value+div_search.edt_hour.Text+div_search.edt_min.Text;

		if(uf_isNull(PRE_APLY_STA_DTM) == false){ 
			if(MOD_YMD <= PRE_APLY_STA_DTM || MOD_YMD >= PRE_APLY_END_DTM){
				ds_condition.SetColumn(0,"MIN","00");
				obj.SetFocus();
				//적용기간은 적용시작일자와 종료일자 기간 사이에 입력하십시오.
				alert(cf_getMessage(MSG_00131, "적용기간;적용시작일자;종료일자"));
				return false;

			}
		}
	}

}



/*******************************************************************************
* 함수명 : div_search_edt_endHour_OnChanged
* 기능명 :  적용종료기간 시간 변경시 
*******************************************************************************/
function div_search_edt_endHour_OnChanged(obj,strText)
{
	if(uf_isNull(strText) == false){

		if(length(strText) !=2) {

			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"END_HOUR",strText);
		}

		if(strText > 23){
			ds_condition.SetColumn(0,"END_HOUR","00");
			obj.SetFocus();
			//23시 초과할수 없습니다.
			alert(cf_getMessage(MSG_00034, "23시를"));
			return false;
		}

	}

}

/*******************************************************************************
* 함수명 : div_search_edt_endMin_OnChanged
* 기능명 :  적용기간 분 변경시 
*******************************************************************************/
function div_search_edt_endMin_OnChanged(obj,strText)
{
	if(uf_isNull(strText) == false){

		if(length(strText) !=2) {
			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"END_MIN",strText);
		}

		if(strText > 59){
			ds_condition.SetColumn(0,"END_MIN","00");
			obj.SetFocus();
			//59분을 초과할수 없습니다.
			alert(cf_getMessage(MSG_00034, "59분을"));
			return false;
		}

	}

}

/*******************************************************************************
* 함수명 : div_search_cal_aplyEndDtm_OnChanged
* 기능명 :  적용기간 시작일 OnChanged 발생시
*******************************************************************************/
function div_search_cal_aplyEndDtm_OnChanged(obj,strOldText,strNewText)
{
	if(uf_isNull(strOldText) == false){ 

	    if(substr(ds_condition.GetColumn(0,"POL_YM"),0,6) > substr(div_search.cal_aplyEndDtm.Value,0,6)) {
					ds_condition.SetColumn(0,"APLY_END_DTM",substr(strOldText,0,8));
					obj.SetFocus();

					MOD_YMD     = "";
					MOD_YMD_END = "";

					alert("적용종료기간은 해당월을 넘어 갈 수 없습니다.");
					return false;
	    }

		var MOD_YMD = div_search.cal_aplyEndDtm.Value+div_search.edt_endHour.Text+div_search.edt_endMin.Text;
		var STA_YMD = div_search.cal_aplyStaDtm.Value+div_search.edt_Hour.Text+div_search.edt_Min.Text;

		//if(uf_isNull(PRE_APLY_STA_DTM) == false){ 
			if(MOD_YMD <= STA_YMD){
				ds_condition.SetColumn(0,"APLY_END_DTM",substr(strOldText,0,8));
				obj.SetFocus();
				//적용종료기간은 적용시작일자 이상으로 입력하십시오.
				alert(cf_getMessage(MSG_00132, "적용종료기간;적용시작일자"));
				return false;

			}
		//}
	}

}


/*******************************************************************************
 * @description    :  월달력 사용 - 이벤트
*******************************************************************************/
function btn_monthCalendar_OnClick(obj)
{
	cf_callMonthCalendar(div_search.cal_polYm);
}

/*******************************************************************************
 * @description    :  기존모델 팜업 호출
*******************************************************************************/
function div_search_btn_search_OnClick(obj)
{

	var sArg  = " POL_YM="       + quote(ds_condition.getColumn(0,"POL_YM"));
	    sArg += " SET_APLY_STA_DTM=" + quote(div_search.cal_aplyStaDtm.Value+div_search.edt_Hour.Text+div_search.edt_Min.Text);

	var sHeight = 0;
	var sWidth  = 0;
	sWidth  = 1;
	sHeight = 1;
	dialog("pol::POLOAP00601.xml", sArg, sWidth, sHeight,,200 ,100 );

}


/*******************************************************************************
 * @description    :  상품정보 탭 클릭시
*******************************************************************************/
function tab_addSaleCmms_OnChanged(obj,nOldIndex,nNewindex)
{
	if(nNewindex == 1) {
		if (ds_objtModel.rowcount == 0 && ds_aplyModel.rowcount == 0) {
			//운영모델단가표가 등록되어 있지 않습니다.
			alert(cf_getMessage(MSG_00013, substr(ds_condition.GetColumn(0,"POL_YM"),0,4)+"년" +substr(ds_condition.GetColumn(0,"POL_YM"),4,6) +"월 운영모델단가표가"));
			return false;
		}
	}

}

/*******************************************************************************
 * @description    :  지급정보 구간적용방식 중 달성구간 개별적용을 선택시...
*******************************************************************************/
function tab_addSaleCmms_tab_payInfo_div_process_rdo_itvlAplyMthd_OnClick(obj)
{
	//alert(tab_addSaleCmms.tab_payInfo.div_process.rdo_itvlAplyMthd.Value);
	if(tab_addSaleCmms.tab_payInfo.div_process.rdo_itvlAplyMthd.Value == "02") {

		for( var nRow=0; nRow<ds_payInfo.rowcount; nRow++ ){
			if ( ds_payInfo.GetColumn(nRow,"PAY_INFO_TYP_CD") == "02" ) {
					alert("지급방식이 정액일 경우\n\n구간적용방식을 개별적용으로 할 수 없습니다.");
					tab_addSaleCmms.tab_payInfo.div_process.rdo_itvlAplyMthd.Value = "01";
					return false;
			}
		}

	}

}

/*******************************************************************************
 * @description    :  지급방식을 정액으로 선택시...
*******************************************************************************/
function ds_payInfo_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID="PAY_INFO_TYP_CD") {
	    //alert(ds_payInfo.GetColumn(nRow,"PAY_INFO_TYP_CD"));
		if( ds_payInfo.GetColumn(nRow,"PAY_INFO_TYP_CD") == "02" ) {
			if(tab_addSaleCmms.tab_payInfo.div_process.rdo_itvlAplyMthd.Value == "02") {
					alert("구간적용방식이 개별적용일 경우\n\n지급방식을 정액으로 할 수 없습니다.");
					ds_payInfo.SetColumn(nRow,"PAY_INFO_TYP_CD","01");
					return false;
			}		
		}
	}

}



function df_searchSet(obj)
{
		MOD_YMD     = div_search.cal_aplyStaDtm.Value+div_search.edt_hour.Text+div_search.edt_min.Text;
		MOD_YMD_END = div_search.cal_aplyEndDtm.Value+div_search.edt_endHour.Text+div_search.edt_endMin.Text;

		var sArg  = " APOL_ID="+ quote(ds_condition.getColumn(0,"APOL_ID"));
			sArg += " POL_YM=" + quote(ds_condition.getColumn(0,"POL_YM"));
			sArg += " POL_TS=" + quote(ds_condition.getColumn(0,"POL_TS"));
			sArg += " APLY_STA_DTM=" + quote(MOD_YMD);
			sArg += " APLY_END_DTM=" + quote(MOD_YMD_END);
			sArg += " LAST_YN=" + quote(ds_condition.getColumn(0,"LAST_YN"));
		gf_OpenWindow( "100000249", sArg); 

}

