				 }
			}
			for( var iRow = 0; iRow < ds_polCostDtl.rowcount; iRow++ ) {

				//alert(sRow1+"==="+sRow2+"==="+sColumn+"==="+ds_polCostDtl.GetColumn(sRow2,"PR_MNY"));
				sRow = object(aDs).SearchRow(" PROD_CD ='" + ds_polCostDtl.GetColumn(iRow,"MDL_ID") + "' ");
				sColumn = ds_polCostDtl.GetColumn(iRow,"SALE_TYP_CD_MIN") +"_"+ ds_polCostDtl.GetColumn(iRow,"POL_OPER") +"_"+ ds_polCostDtl.GetColumn(iRow,"POL_SUBJ") +"_"+ ds_polCostDtl.GetColumn(iRow,"PR_MNY_CL_CD");				 				
				gColumn = ds_polCostDtl.GetColumn(iRow,"SALE_TYP_CD_MIN") +"_"+ ds_polCostDtl.GetColumn(iRow,"POL_OPER") +"_"+ ds_polCostDtl.GetColumn(iRow,"POL_SUBJ");
				//object(aDs).SetColumn(sRow, sColumn,          ds_polCostDtl.GetColumn(iRow,"PR_MNY"));	
                //장려금별 컬럼
				object(aDs).SetColumn(sRow, sColumn,          Floor(ToNumber(ds_polCostDtl.GetColumn(iRow,"PR_MNY"))/ToNumber(nAUnit)));

                //운영사별 컬럼
                if ( uf_isNull(object(aDs).GetColumn(sRow, gColumn)) == true ) {
					object(aDs).SetColumn(sRow, gColumn,          Floor(ToNumber(ds_polCostDtl.GetColumn(iRow,"PR_MNY"))/ToNumber(nAUnit)));
                } else {
					object(aDs).SetColumn(sRow, gColumn,          ToNumber(object(aDs).GetColumn(sRow, gColumn)) + Floor(ToNumber(ds_polCostDtl.GetColumn(iRow,"PR_MNY"))/ToNumber(nAUnit)));
                }

			}


			for( var iRow = 0; iRow < ds_polMdlLst.rowcount; iRow++ ) {
			     object(aDs).SetColumn(iRow, "S10101_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS10101_ssum));
			     object(aDs).SetColumn(iRow, "S10201_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS10201_ssum));
			     object(aDs).SetColumn(iRow, "S10202_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS10202_ssum));
			     object(aDs).SetColumn(iRow, "S20101_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20101_ssum));
			     object(aDs).SetColumn(iRow, "S20102_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20102_ssum));
			     object(aDs).SetColumn(iRow, "S20103_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20103_ssum));
			     object(aDs).SetColumn(iRow, "S20201_ssum", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20201_ssum));

			     object(aDs).SetColumn(iRow, "S10101_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS10101_ssum+1));
			     object(aDs).SetColumn(iRow, "S10201_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS10201_ssum+1));
			     object(aDs).SetColumn(iRow, "S10202_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS10202_ssum+1));
			     object(aDs).SetColumn(iRow, "S20101_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20101_ssum+1));
			     object(aDs).SetColumn(iRow, "S20102_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20102_ssum+1));
			     object(aDs).SetColumn(iRow, "S20103_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20103_ssum+1));
			     object(aDs).SetColumn(iRow, "S20201_snet", tab_polCost.tab_prMnyInfo.grd_polSplst.GetCellText("Body",iRow,oS20201_ssum+1));
			}



		//trace(object(aDs).SaveXML());
        //object(aDs).Unfilter();
	    tab_polCost.tab_prMnyInfo.grd_polSplst.AutoFit = false;
		tab_polCost.tab_prMnyInfo.grd_polSplst.Redraw = true;

	    tab_polCost.tab_operInfo.grd_polSplst.AutoFit = false;
		tab_polCost.tab_operInfo.grd_polSplst.Redraw = true;

	//Grid0.GetCellProp("body",Grid0.GetCellPos(),"colid") 


	}
}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search(sButtonID)
{
	if(uf_IsNull(div_search.edt_posAgencyId.value)){

		alert(cf_getMessage(MSG_00121, "대리점;조회"));
		div_search.edt_posAgencyNm.SetFocus();
		return;
	}		

	// 조회값 선택 여부 확인 
	//cf_checkObjValues(div_search);

    //alert(ds_condition.GetColumn(0,"ORG_ID"));

	var sSvcID  = "svcSearch";
	var sInDs   = " ds_condition=ds_condition ";
	var sOutDs  = " ds_polMdlLst=ds_polMdlLst ";
	    sOutDs += " ds_polCostLst=ds_polCostLst ";   
	    sOutDs += " ds_polCostDtl=ds_polCostDtl ";   
	var sArg    = "sktst.pol.PSM#getPolCostLst";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}




