		colsNum = 0;
		if ( ds_polCostLst.GetColumn(nRow,"SALE_TYP_CD_MIN") <> ds_polCostLst.GetColumn(nRow+1,"SALE_TYP_CD_MIN") ) {
		colNum++;
		sColid = ds_polCostLst.GetColumn(nRow,"SALE_TYP_CD_MIN") +"_ssum";

		sExpr = "Floor(("+sExprDtl+")/ToNumber("+nAUnit+"))";
		sContBodys += '<cell align="right" col="' + colNum + '" colid="' + sColid + '" expr="' + sExpr + '" display="number" bkcolor="user13" font="돋움,8"/>\n';
		if      ( sColid == "S10101_ssum" ) { oS10101_ssum = colNum; }
		else if ( sColid == "S10201_ssum" ) { oS10201_ssum = colNum; }
		else if ( sColid == "S10202_ssum" ) { oS10202_ssum = colNum; }
		else if ( sColid == "S20101_ssum" ) { oS20101_ssum = colNum; }
		else if ( sColid == "S20102_ssum" ) { oS20102_ssum = colNum; }
		else if ( sColid == "S20103_ssum" ) { oS20103_ssum = colNum; }
		else if ( sColid == "S20201_ssum" ) { oS20201_ssum = colNum; }

		colNum++;
		sColid = ds_polCostLst.GetColumn(nRow,"SALE_TYP_CD_MIN") +"_snet";
		sExprDtl = "ToNumber(FIX_CRDT_PRCHS_PRC)" + "-(" + sExprDtl+ ")";
		sExpr = "Floor(("+sExprDtl+")/ToNumber("+nAUnit+"))";
		//sContBodys += '<cell align="right" col="' + colNum + '" colid="' + sColid + '"  display="number" bkcolor="user8" font="돋움,8"/>\n';
		sContBodys += '<cell align="right" col="' + colNum + '" colid="' + sColid + '" expr="' + sExpr + '" display="number" bkcolor="user8" font="돋움,8"/>\n';		
		}
		colNum++;
	}






	sContBodys += '</body>\n';
	sContents = sContents + sContBodys;

	sContents	+= '</format>\n';
	sContents	+= '</contents>\n';

	var objGrid = object(grdName);

	objGrid.Contents = sContents;


}



/*******************************************************************************
 * @description    : excelDown 클릭- 이벤트
*******************************************************************************/
function Div2_btn_excelDown_OnClick(obj)
{
    if ( tab_polCost.TabIndex == 0 ) {
		cf_exportExcel(tab_polCost.tab_operInfo.grd_polSplst);	
    } else {
		cf_exportExcel(tab_polCost.tab_prMnyInfo.grd_polSplst);	
    }
}






/*******************************************************************************
 * @description    : 대리점 OnChar Changed 이벤트
*******************************************************************************/
function div_search_edt_posAgencyNm_OnCharChanged(obj,strPreText,strPostText)
{
	if(strPreText != strPostText){
		div_search.edt_posAgencyId.value = "";
	}	
}

/*******************************************************************************
 * @description    : 대리점 OnKeyDown 이벤트
*******************************************************************************/
function div_search_edt_posAgencyNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13"){
		// org_id 와 org_cl 의 파라메터는 필수 입니다.
		var sArg  = "org_id="+FV_ORG_ID; // 필수

		cf_callAgencyPop(sArg, div_search, "edt_posAgencyNm,edt_posAgencyId");	
	} 		
}

/*******************************************************************************
 * @description    : 대리점 버튼 클릭 이벤트
*******************************************************************************/
function div_search_btn_agency_OnClick(obj)
{
	// org_id 와 org_cl 의 파라메터는 필수 입니다.
	var sArg  = "org_id="+FV_ORG_ID; // 필수
	cf_callAgencyPop(sArg, div_search, "edt_posAgencyNm,edt_posAgencyId");				
}

/*******************************************************************************
* 함수명 : div_search_edt_hour_OnChanged
* 기능명 :  조회기준일시 시간 변경시 
*******************************************************************************/
function div_search_edt_hour_OnChanged(obj,strText)
{
	if(uf_isNull(strText) == false){

		if(length(strText) !=2) {

			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"SEARCH_HOUR",strText);
		}

		if(strText > 23){
			ds_condition.SetColumn(0,"SEARCH_HOUR","00");
			obj.SetFocus();
			alert("23시를 초과할수 없습니다.");
			return false;
		}
	}
}

/*******************************************************************************
* 함수명 : div_search_edt_min_OnChanged
* 기능명 :  조회기준일시 분 변경시 
*******************************************************************************/
function div_search_edt_min_OnChanged(obj,strText)
{
		if(uf_isNull(strText) == false){

		if(length(strText) !=2) {
			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"SEARCH_MINUTE",strText);
		}

		if(strText > 59){
			ds_condition.SetColumn(0,"SEARCH_MINUTE","00");
			obj.SetFocus();
			alert("59분을 초과할수 없습니다.");
			return false;
		}
	}
}

/*******************************************************************************
* 함수명 : div_search_cmb_slNetCd_OnChanged
* 기능명 :  영업망 변경시 조회조건에 영업구분 세팅
*******************************************************************************/
function div_search_cmb_slNetCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    ds_condition.SetColumn(0,"COMM_CD_ID",ds_slNetCd.GetColumn(nNewIndex,"COMM_CD_ID"));
	//alert(ds_slNetCd.GetColumn(nNewIndex,"COMM_CD_ID"));
}



/*******************************************************************************
 * @description    :  거래처구분코드를 영업구분코드로 변환
*******************************************************************************/
function sf_convertDealCoClToSlCl(sDealCoCl)
{
	var sRetValue;
	switch(sDealCoCl)
	{
	case "A2":
		sRetValue = "01";
		break;

	case "A3":
	case "A4":	
		sRetValue = "02";
		break;

	case "B1":	
		sRetValue = "03";
		break;

	case "B2":	
		sRetValue = "04";
		break;

	default:
		break;
	}
	return sRetValue;
}

function div_search_cmb_dsNetCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(strCode == "0") {
		div_search.cmb_agrmtPrd.Index = 0;    //약정기간
    }

}
