function f_getSaleChgrgId()
{
    sf_getChgrgIdList("ds_input", "ds_cmbSaleChgrgId","", div_dealCoInfo.edt_salePlc.Text, div_dealCoinfo.cal_svcDtm.Value );  //대리점
	//판매변경담당자 -- 일반판매 도매,도매 적용해야함.
}

