/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function form_OnLoadCompleted(obj)
{	
	cf_setAuth(pFrmNum);				// 권한 공통
	cf_setGrdChecknSortEvent(grd_list);	// 그리드 정렬	
	f_init();
}

